INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4692', '国立东方艺术博物馆（都灵）', '小三脚架罐子', '', '', '压扁的球形花瓶，放置在三只脚上，形状像集市的爪子。短领和下摆向外折叠。刻有薄片的鱼片（在橱窗下）标记了肩膀和腹部之间的通道。蓝色、琥珀色和奶油色的釉料，沿着墙壁在花瓶底部形成大的复合斑块，并部分覆盖了脚。蓝色，由钴制成，在这个时代的中国生产中相当罕见，其中它通常被绿色代替。', '', '', '', 'https://www.maotorino.it/media/opere/Cv07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4693', '国立东方艺术博物馆（都灵）', '釉面“胡”花瓶', '', '', '球形壶足平直，高曲颈，宽口形如低矮的圆锥杯。两个应用环的装饰图案位于腹部的中央部分，截然相反。唯一的其他装饰由一系列不同分组的电缆螺纹组成。花瓶完全覆盖，包括底座，由深绿色明亮的玻璃覆盖。', '', '', '', 'https://www.maotorino.it/media/opere/Cv69.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4694', '国立东方艺术博物馆（都灵）', '带图案盖的“云酒尊”花瓶', '', '', '这个花瓶的独到之处在于盖子的锥形形状，模拟三圈山峰，并在道教传统的“神仙”的全方位雕像中达到高潮，具有人鸟杂交的特性。花瓶的主体被一根肋骨分成两个大带，并在顶部，截然相反，两个带厚活动环的普寿面膜。两条带子上装饰着曲线图案的雕刻（拱门或程式化的花朵）在三排上重复，并由三组水平菱形界定，在下端，由一系列顶点朝上的尖三角形组成。花瓶放在三个比喻的脚上。', '', '', '', 'https://www.maotorino.it/media/opere/Cb18.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4695', '国立东方艺术博物馆（都灵）', '菱形画马', '', '', '这种类型的马部分由易腐烂的元素组成，尤其是插入零件底部孔中的木腿。头上有两个孔，臀部底部有一个孔，分别容纳了动物的耳朵和尾巴；背面有一个较大的孔，用于用榫头固定骑士的身材。马的脖子大，枪口细长，是典型的汉马。表面覆盖着棕色油漆，上面冷铺着其他颜料。这个标本的标志是椭圆形的鞍座，上面悬挂着菱形的放射状装饰品。', '', '', '', 'https://www.maotorino.it/media/opere/Cc54.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4696', '国立东方艺术博物馆（都灵）', '谷物酒的“剑”罐', '', '', '半球形的吻，有两个对称排列的活动环。在脚和腹部的中央有一个突出的辫子图案。Agemina的装饰有三个寄存器。上带将几何形状与程式化的曲龙交替出现。中央带显示四对龙相互面对：在两个中央面上，它们被一个带有三个内部“逗号”的圆圈隔开。和漩涡。底面呈现两排交替出现的小龙。', '', '', '', 'https://www.maotorino.it/media/opere/Cb07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4697', '国立东方艺术博物馆（都灵）', '戴着高头饰的军官', '', '', '令人印象深刻的人物站立，向左转，右手折叠在胸前，左臂沿着侧面伸展，他必须持有武器或等级属性。圆圆的眼睛和半张着的嘴。他头上戴着一种涂成黑色的高头饰。她穿着一件长到脚的白色束腰外衣和一件V领和巨大喇叭袖的外套；边缘用珍贵的颜料（天青石和孔雀石）完成。。胸围由盔甲保护，盔甲向下覆盖腹部。盔甲的下缘最初被金箔覆盖，大部分剥落。', '', '', '', 'https://www.maotorino.it/media/opere/Cc04.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4698', '国立东方艺术博物馆（都灵）', '拉贾里拉萨纳的观音', '', '', '观音菩萨，中国版印度观世音菩萨，坐在皇家放松的位置（rajalilasana）在这个基础上，它似乎唤起了马罗人挖出的一块岩石，因此让人想起了布达拉卡山上菩萨的存在，在海洋中间。光滑的脸呈现出从眼睑向下的眼睛，表示眼睛向下看着被轮回所淹没的生物，玫瑰花蕾形状的小嘴，眉弓中央有规则的鼻子，标记不明显。整齐的刘海从高高的头饰中露出，中间是阿弥陀佛的形象，观世音菩萨所属的宇宙佛。菩萨戴着项圈、耳环和手镯，他穿着一条长披肩，遮住了两个肩膀，并用柔软的褶皱落在前臂上。', '', '', '', 'https://www.maotorino.it/media/opere/Cs10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4699', '国立东方艺术博物馆（都灵）', '谷物酒的“胡”罐', '', '', '容器有高的喇叭形颈部，球状的腹部，平底和环形的脚，有七个主要的装饰带，分为正方形，其中设计重复相同的四到八次。在上面的带子中，面对着长腿和伸出的喙的鸟类，而第二个乐队则有一系列双重的方形螺旋图案。第三个带是最高的，花瓶的手柄固定在第三个带上，展示了三个层次的狩猎场景。第四个乐队重复了第二个乐队的抽象主题，将其与下一个乐队分开，第五个乐队仍然致力于狩猎场景。在第六个乐队中，一个人鸟（萨满？）在与蛇搏斗的涉水者之间跳舞。花瓶底部的最后一条带子上装饰着菱形图案。', '', '', '', 'https://www.maotorino.it/media/opere/Cb08.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4700', '国立东方艺术博物馆（都灵）', '女性性格', '', '', '贵妇的葬礼雕像处于站立位置，但中轴相对于间隔的脚略有移动。肉嘟嘟的小手的优美姿态。圆脸，五官精致；红润的脸颊，鲜红的嘴巴，黑色的眼睛和眉毛。精心设计的发型，从一只耳朵到另一只耳朵都有一个大蝴蝶结。宽大的连衣裙，宽大的袖子和V领，右侧保留了绿色颜料的痕迹。这位女士穿着红色的鞋子，鞋尖抬高，并饰有浅瓣。', '', '', '', 'https://www.maotorino.it/media/opere/Cc14.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4701', '国立东方艺术博物馆（都灵）', '短鬃毛和打结尾巴的马', '', '', '大型雕像，四肢僵硬，关节突出。头部雕刻有力，嘴巴张开，嘴唇抬起突出牙齿，舌头向左垂下，膨胀的青蛙和圆圆的眼睛。鬃毛从尖尖的耳朵中间的一簇开始，其余的都剪短了。唯一的饰面是用螺柱焊接的带子固定的床头板。玻璃覆盖了雕像，直到结的上方，也许是为了标记斗篷的斑驳。背面玻璃上的有限间隙似乎表明骑士的原始存在。', '', '', '', 'https://www.maotorino.it/media/opere/Cc56.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4702', '国立东方艺术博物馆（都灵）', '彩绘新石器时代罐子', '', '', '球状的奥拉在顶部非常膨胀，向基部逐渐变细，颈部呈喇叭形。两个孔眼对称地应用于容器最大直径以下。肩部的装饰主要由黑色的水平图案组成，并由经典的拱门或花彩图案封闭。主装饰带呈现出六个红色螺旋的连续理论，周围是大的黑色条纹。下摆的内部装饰是重复设计的同心圆拱门。', '', '', '', 'https://www.maotorino.it/media/opere/Cn05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4703', '国立东方艺术博物馆（都灵）', '天王夫妇', '', '', '气势磅礴的天王雕像，在岩石般的基座上，以制服恶魔的行为。两尊雕像的头部，没有上釉，是用冷漆画的。两位国王都有一种威胁的表情，大眼睛突出，眉毛皱起，鼻孔扩张，长胡子突出，红红的嘴巴张着，做着鬼脸。在盔甲中，领口、胸板、腹部和鳞片臀部是有区别的。肩带呈双角龙的形状，下巴张开，鼻子朝上（可能是印度马卡拉的结果）；腿由护膝和护膝保护。', '', '', '', 'https://www.maotorino.it/media/opere/Cc05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4704', '国立东方艺术博物馆（都灵）', '带面纱的男性角色', '', '', '这个人物不寻常的可塑性使人认为它是徒手塑造的，而不是用模具印刷的。人被描绘成坐在凳子上或动物的背上。双臂向胸部折叠，双手握拳，正面朝向胸部，他们可以让人以为他们抓住了坐骑的缰绳。从这个人的脸上，肯定是一个外国人，只能看到浓密的眉毛，西部剪裁的凸出的眼睛和大的鹰钩鼻，部分被三角形的面纱覆盖，该面纱与特定的尖端相连或头巾。颈背被另一块覆盖耳朵的三角形布覆盖，并落在左肩。', '', '', '', 'https://www.maotorino.it/media/opere/Cc28.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4705', '国立东方艺术博物馆（都灵）', '凤形盖壶', '', '', '壶的梨形腹部向颈部延伸，边缘扩大，底部逐渐变细，脚高呈喇叭形，带有凸起的底座和扁平的支撑。可拆卸的鸟冠形状的盖子和弯曲的龙形手柄直立在后腿上。四条装饰带完全覆盖了壶身，由双浮雕螺纹隔开。四个乐队中的第一个展示了向下的大花瓣图案，第二个展示了老鼠的叶子。中间的带子，在罐子的肚子上，包括六个用珠子框起来的圆形纪念章，每个人都有一个裸体的男性形象，背景是风格化的岩石。下带装饰有六个花卉图案，每个图案都与上带的纪念章相对应。', '', '', '', 'https://www.maotorino.it/media/opere/Cv01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4706', '国立东方艺术博物馆（都灵）', '护墓怪（真木寿）', '', '', '长着怪诞的胡子的人脸，牛耳朵和头顶上的高角的怪物，正面蹲在带有圆形边缘的四边形底座上。强大的胸部和前腿以牛蹄结束，翅膀几乎是从肩膀后面出现的前板。胸部和翅膀上装饰着已经褪色的红色蜗壳。在这个时代的许多坟墓中都发现了类似的小雕像，同样类型的保护怪物也出现在更大的尺寸和玻璃上。在一些更重要的墓葬中使用了三才。', '', '', '', 'https://www.maotorino.it/media/opere/Cc08.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4707', '国立东方艺术博物馆（都灵）', '阅兵马', '', '', '优雅的阅兵马雕像，从胸部下部到尾巴下方都是中空的，站在四边形的底座上。坚固的颈部，以强烈凸出的轮廓焊接到头部。扁平的尾巴，末端略有凸起。枪口和饰边都做了详细的造型。床头板的带子在额头上饰有圆形饰钉，胸带上饰有八瓣花的吊坠。巨大的鞍座在马肩隆上向前移动，并覆盖着优雅的窗帘。', '', '', '', 'https://www.maotorino.it/media/opere/Cc30.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4708', '国立东方艺术博物馆（都灵）', '奇妙的动物形状的花瓶', '', '', '梨形的身体逐渐变细到肩膀的无足罐。低杯喇叭形的大嘴，带有厚实的直边，垂直突出。在车床上加工的主体上应用了模具模型元素，使花瓶看起来像一只坐在上面的大动物。后腿。高浮雕的大枪口有圆圆的眼睛，宽而扁平的鼻子，张开的下巴，舌头和牙齿清晰可见，位于上颚和外突舌头之间的小球体。头部下方有一根浅浮雕的长羽毛胡须。在花瓶的另一侧描绘了一个背脊。', '', '', '', 'https://www.maotorino.it/media/opere/Cv72.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4709', '国立东方艺术博物馆（都灵）', '“bo”钟', '', '', '这可能是音乐盒中最大的一块。在钟罩的每个表面上设置有九个由水平和垂直检测的肋限定的寄存器，每个包含三个盘绕的蛇形螺柱。插在六个螺柱之间的四个水平柱上有一个浮雕装饰，上面刻有精美的龙纹。你。钟的宽底带上也有类似的装饰。用于悬挂铃铛的方形环是由两只面对的猫科动物制成的，它们与环一起由单个厚的层压元件制成。', '', '', '', 'https://www.maotorino.it/media/opere/Cb21.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4710', '国立东方艺术博物馆（都灵）', '双柄釉面双耳瓶', '', '', '在基部渐细的球状身体的双耳瓶。脖子又高又细，末端呈杯状，嘴唇很厚。两个对称的龙形手柄从嘴到肩分开。颈部和肩部之间的连接处有明显的高度差，而两个雕刻的鱼片则标志着从肩膀到腹部的过渡。陈列柜在花瓶的下半部分有大的铸件。这种类型的花瓶在罗马双耳瓶中有自己的原型，其形状是由波斯和亚洲中心的银匠和玻璃制造商引入中国的。Rale。', '', '', '', 'https://www.maotorino.it/media/opere/Cv02.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4711', '国立东方艺术博物馆（都灵）', '“云酒尊”五谷酒', '', '', '这种容器的典型汉族形状的花瓶：圆柱形的主体搁在三只脚上，形状像蹲着的熊和圆锥形的盖子。具有强烈的象征意义。中间部分饰有以两个普寿面具为界的宽幅浮雕带。花瓶两侧再现的场景，以山景为背景，描绘了熊、虎、猴、龙、一只鹿，一只凤凰，一只野猪，一个带有鸟类特征的“神仙”。盖子再现了四座被海浪包围的山脉或岩石，中间是第五座山峰。在覆盖整个花瓶的绿色玻璃上，在盖子的边缘和花瓶的底部添加了一个琥珀色的展示柜。', '', '', '', 'https://www.maotorino.it/media/opere/Cv67.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4712', '国立东方艺术博物馆（都灵）', '面具（门框装饰）', '', '', '外形面具，枪口特征突出，头部上方有小而直的耳朵。它有一双方形的大眼睛，非常浓密的眉毛，两端卷到鼻根，鼻根又宽又扁，巨大的畸形嘴，露出整个上牙弓和下犬齿，并在内部带有红色色素残留物。圆形的枪口由胡须的程式化卷发构成。这件作品与北魏瓦顶上的灰土面具和Q的某些狮子头有一些相似之处。北方人。', '', '', '', 'https://www.maotorino.it/media/opere/Cc38.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4713', '国立东方艺术博物馆（都灵）', '长毛双峰驼', '', '', '又高又瘦的骆驼，脖子向右微微拱起。头部向上伸展，嘴巴张开，内部涂成红色。上唇由平行切口突出，使其看起来起皱。被毛的颜色为浅赭色，毛皮区呈红褐色。头发的粗糙可能是通过在模具获得的基础上手工涂抹半液体粘土获得的。驼峰从带有橙色和黑色双重边框的白色四边形瓜尔德拉帕中伸出。', '', '', '', 'https://www.maotorino.it/media/opere/cc33.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4714', '国立东方艺术博物馆（都灵）', '女性角色（舞者？', '', '', '女性角色，可能是舞者，双手被宽大的长袖遮住，抬起盖住脸的下部。紧身长裙紧贴臀部，脚上有喇叭形，腰部用腰带固定，V领。脸被精致地塑造着，头发有中央分叉，向后梳成一条长长的尾巴，一直垂到背部。这些雕像的风格与公元前2世纪出现在首都长安墓葬中的宫女形象相对应。。C。', '', '', '', 'https://www.maotorino.it/media/opere/Cc01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4715', '国立东方艺术博物馆（都灵）', '带有钝尖的“ GE ”斧头-匕首刀片', '', '', '带有弯曲的三角形刀片的斧头-匕首，连接到比脚跟窄得多的刀柄上。刀片具有双钝线和圆形尖端；浮雕装饰在最靠近脚跟的部分占据了一个三角形区域。设计，两面都一样，它可以分为两个寄存器：较低的寄存器有一个轻微的中央肋骨，该肋骨在顶部以一个小的闭塞圆圈结束；两侧有方形螺旋几何图案。相反，上面的寄存器显示了两个杏仁眼，上面有两个相对放置的示意性字符，在中央长方形图案的两侧，看起来像中央肋骨的延续。在刀柄的每一面上，都画着一个面具，上面有一双大大的杏仁眼。末端被挖空，形成两个半圆，突出眼睛的上轮廓。', '', '', '', 'https://www.maotorino.it/media/opere/Cb67.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4716', '国立东方艺术博物馆（都灵）', '三角“GE”斧头-匕首刀片', '', '', '这把匕首（ge）的形状完全对称，两面完全相同。它由一个大的三角形刀片组成，连接到一个比鞋跟窄得多的矩形刀柄上。刀刃两边都很锋利；一条突出的肋骨从尖端开始，一直延伸到一个圆形通孔，位于底部。在这里，浮雕在整个刀片上延伸-不包括鞋跟-逐渐向外变平。在鞋跟的尖端钻了两个长方形的小孔，并且在其前部的茎的中心处有一个三角形的孔。沿着叶片的中间肋之一垂直刻有四个字：黄X庄吉，“强大而吉祥的奥古斯都十世”。这句话的晦涩含义不值得深入研究：铭文是在作品融合后制作的，实际上，它是由表面氧化而产生的，因此必须被认为是现代的。', '', '', '', 'https://www.maotorino.it/media/2022/09/mao_placeholder.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4717', '国立东方艺术博物馆（都灵）', '鹿形装饰板', '', '', '该板略有浮雕，内部中空，两个半环固定在动物的颈部和侧面。这是一只年轻的鹿，可以通过耳朵上方仍然短到半圆形的角来识别，侧面描绘，腿聚集在身体下方。因为左侧是可见的，所以只有两条腿与蹄子重叠。动物的嘴是用切口做的，眼睛是用轻微凸起的圆圈做的，而尾巴是身体末端的一个小突起。', '', '', '', 'https://www.maotorino.it/media/opere/Cb26.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4718', '国立东方艺术博物馆（都灵）', '小花瓶，两个环和弯曲的脖子', '', '', '该容器的做工不精细，是一种非常常见的类型。它是一个大肚子的罐子，形状朝向底部，底部非常狭窄，并配有一个宽阔的弯曲的颈部和一个张开的嘴。下摆通过两个截然相反的拱形环与肩部相连，每个环上都装饰有水平的黑色条纹。其余的装饰，在倾向于棕色的红色背景上涂上厚厚的黑色笔触，它集中在颈部的内部和花瓶的外部，直到整流罩。在内部，在边缘下方，有三个同心的“花彩”拱门的重复图案。脖子上的装饰仅限于一排厚厚的人字纹。沿着容器的肩部，装饰依次由四个同心双拱门组成。', '', '', '', 'https://www.maotorino.it/media/opere/Cn31.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4719', '国立东方艺术博物馆（都灵）', '“鱼头”武器', '', '', '获得该物体的略微弯曲的石板厚度小于1厘米，它的形状让人想起张斧。刀刃的形状奇特，隐约让人联想到张着嘴的鱼头。实际上，尖端被深深地刻成三角形，刀片最弯曲的一面在边缘上是钝的。与鱼头的类比也是由一个简单地刻在物体一侧的图案所暗示的：它是四条线。弓形平行放置在刀片的底部，这在某种程度上让人联想到鳃裂。在刀片的底部，物体在两侧收缩了几毫米。这个凹痕继续呈锯齿状，每侧有五个规则的凹口，第二个是三角形的，其他的是拱形的。除了短暂的额外形状外，该物体还以矩形柄结尾，并在一个角度上强烈倒角。', '', '', '', 'https://www.maotorino.it/media/opere/cp2.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4720', '国立东方艺术博物馆（都灵）', '一个安萨的小花瓶', '', '', '这个制作粗糙的罐子，形状像一个带有球形主体的水罐，在肩膀上展开，向狭窄而平坦的底部逐渐变细。它有一个宽而略微弯曲的颈部，由一个带状手柄连接，两端在容器的肩部扩大。外表面覆盖着绳子的半凹印，它们会产生宽阔的水平和倾斜的凹槽，尤其是在颈部和腹部最大直径以下可见。在容器主体的上部，垂直和倾斜的线，分组为四条或更多条平行线，它们在烹饪前被生土刮伤了。类似的垂直条纹也会影响手柄，手柄的颜色比容器的其余部分更深。', '', '', '', 'https://www.maotorino.it/media/opere/Cn29.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4721', '国立东方艺术博物馆（都灵）', '乌龟', '', '', '这件作品是一件做工精良、不寻常的写实镂空雕塑。乌龟直立在四条粗壮的腿上，将脖子和头部向上推，嘴巴张开，下唇张开。鼻孔和眼睛是挖出来的，后者有一个特殊的破碎的椭圆形。一条水平直的小尾巴从甲壳后面伸出。胸甲的斑块一个接一个地突出，再加上腿上起皱的皮肤，它们突出了与有血有肉的动物的相似性。腿和头上有色素的痕迹：鼻孔、嘴巴和腿的一部分是红色的，与白色混合在一起。', '', '', '', 'https://www.maotorino.it/media/opere/Cc48.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4722', '国立东方艺术博物馆（都灵）', 'GIARA CARENATA “ HU ”', '', '', '这个罐子具有典型的马家窑类型的最古老的陶土的形状和装饰，其特点是面团质量高，从容器的角度轮廓和丰富的水平条纹绘画。它有一个短的喇叭形脖子，下摆向外，肩膀强烈扩张。达到最大直径后，在与腹部的交界处形成一个倒角，下面的花瓶逐渐缩小到平底。涂成黑色和白色的装饰仅限于手柄上方的容器顶部，它光滑而光滑，就像被包裹着一样。', '', '', '', 'https://www.maotorino.it/media/opere/Cn11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4723', '国立东方艺术博物馆（都灵）', '鹅形花瓶', '', '', '花瓶是由一只脖子直立的圆鹅组成的，蹲着藏着爪子。折叠的翅膀具有相对于身体其余部分的轮廓。扁平的三叶形尾巴构成了容器底部的延伸。头部在太阳穴上有两个羽毛卷，脸颊和眼睛凸起。枕骨上有一个涂成红色的短直颈孔口。花瓶的整个表面在黑色背景上涂有白色和红色的蜗壳，以抽象地唤起动物的羽毛和其他身体特征。', '', '', '', 'https://www.maotorino.it/media/opere/Cv64.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4724', '国立东方艺术博物馆（都灵）', '带锅的烤箱模型', '', '', '这个空心炉的形状几乎是平行六面体。前壁前面有一个平坦的门槛，相对于灶台有一个凸起的高边缘；底部有一个拱形入口，通向燃烧室。在另一侧，顶部是一个龙头形状的壁炉：长长的枪口，眼睛和鼻孔突出，它的嘴被刺穿以排出烟雾。窑壁用粗刻线条装饰，将表面划分为矩形框架和方形扇形；在架子上，与矩形相交的对角线给人留下了小菱形格子的印象。在灶台上，三个圆孔排成一排，以放置尽可能多的消声器，可拆卸。这种窑的结构和装饰是东汉时期广州地区的典型。', '', '', '', 'https://www.maotorino.it/media/opere/Cc74.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4725', '国立东方艺术博物馆（都灵）', '两环八把手罐', '', '', '做工精细，装饰精美的球形锅，后者分为水平带。它的形状略呈扁平，向上扩张，向狭窄的基部逐渐变细，颈部非常张开。两个孔眼对称地应用于容器最大直径以下。另外八个等距的鳍，在高度上有点不一致，围绕着它的肩膀。肩膀上的装饰是由菱形理论组成的，它们的顶点连接在一起，内部呈网状，位于两个黑色带之间。在穿孔鳍片的高度有一条红线强调了容器的肩部和腹部之间的通道。下面的装饰带，限制在锅的上半部分，直到两个环，占据了肩部的饰带。穿孔的鳍片是不寻常的，使这个花瓶具有原始的特征。', '', '', '', 'https://www.maotorino.it/media/opere/Cn06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4726', '国立东方艺术博物馆（都灵）', '带有熊形支架的硬币轴', '', '', '硬币树的树干在熊的头部上方插入一个垂直孔，坐着，前腿放在膝盖上，头部弯曲。它的特点是耳朵小，枪口细长，鼻子上有两个凹槽，嘴巴张开，但最重要的是，眼睛由两块镶嵌的绿松石组成。树干上装饰着雕刻的波浪，并以有规律的间隔呈现，四个环，每个环有四个外壳，用于插入树枝。现在的微型描绘类似于在树枝上看到的那些，在多个层面上以人类角色为主，从事不同的活动。树上有一只大鸟（朱雀）。，头部的装饰羽毛向前弯曲，扁平的尾巴像弯曲的三角形一样伸展。它还配有两个展开的、可拆卸的翅膀，上面挂着两个小铃铛形状的装饰品和一些鱼。', '', '', '', 'https://www.maotorino.it/media/opere/Cb32.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4727', '国立东方艺术博物馆（都灵）', '高颈双环整流罩', '', '', '这个罐子的形状是典型的新店陶瓷。它有一个又高又宽的弯曲的脖子，有一个扩大的下摆，嫁接在一个稍微凸起的肩膀上，达到了它的最大直径，它逐渐向容器的底部闭合。彩绘装饰由带有黑色条纹的棕色宽带组成。在脖子上的宽带中，四只涂成黑色的鸟的轮廓脱颖而出。罐子肩部的装饰非常明显，有四个几何图案，涂有棕色条纹，周围有黑色条纹。图案的两端是圆形的，两两对称。', '', '', '', 'https://www.maotorino.it/media/opere/Cn19.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4728', '国立东方艺术博物馆（都灵）', '带有圆形截面接头的斧头', '', '', '圆形截面的管状移植物在其内部形成锥形树干，并在靠近开口的一侧穿孔。在这个洞的旁边是一个熔合的缺口。手柄和刀片之间没有连续的解决方案：第一个变薄一点，然后横向加宽以形成一条线宽而直的斜面。每一个装饰都没有，表面上发现了一些融合的缺陷。这个斧头无疑来自滇国的一个遗址，虽然这种文化的刀片通常有一条圆形的线。', '', '', '', 'https://www.maotorino.it/media/opere/cb64.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4729', '国立东方艺术博物馆（都灵）', 'OBLUNGA储存罐', '', '', '这个标本的长圆形是由它的梨形身体逐渐向扁平的基部收缩而形成的。肩膀在顶部由一个几乎水平的平面突出，在该平面上嫁接了短而弯曲的脖子，带有外缘的外缘下摆。肩部和腹部上部的表面呈细斜方状，用三个对角线交替向右或向左装饰的小菱形。而腹部的其余部分则印有大菱形图案，每边3-4厘米，由水平和垂直交替的条纹组成。', '', '', '', 'https://www.maotorino.it/media/opere/cv55.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4730', '国立东方艺术博物馆（都灵）', '“YUE”型弯刀头', '', '', '没有装饰的扁平的岳斧头由刀刃和刀柄组成。刀片是方形的，在凸出的螺纹一侧更宽，带有圆角，一点也不锋利。在中心，向茎移动，有一个完美的圆形通孔。刀片的末端有一个垂直的凸起保护装置，该保护装置的末端有两个小突起。矩形柄在外部底角有一个小缺口，中间有一个四边形通孔。斧头的手柄没有保存下来，必须靠在刀柄和刀片之间的凸起部分上：一些鞋带穿过两个孔，终端部分的切口也必须用于完成固定。这种斧头的形状早在公元前13世纪就得到了证实，也可以在玉器中找到。', '', '', '', 'https://www.maotorino.it/media/opere/Cb33.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4731', '国立东方艺术博物馆（都灵）', '岩石基地上的守护者（DVARAPALA）', '', '', '放置在寺庙或佛教祭坛入口左侧的“门的守护者”（dvarapala）。剃光头的顶部有一簇被丝带包围的头发。愤怒的鬼脸和非凡的胸部肌肉与经典的阿波特罗描绘相对应。派切。左臂向下伸出，握拳以威胁的姿态。头后和肩膀周围飘动的围巾为身材增添了活力。此雕塑与Cs/12b形成一对。', '', '', '', 'https://www.maotorino.it/media/opere/Cs12a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4732', '国立东方艺术博物馆（都灵）', '女性裸体雕像', '', '', '这种类型的人体模型的可拆卸木臂固定在与肩膀高度相同的孔中。他们后来穿着布衣，可能还配有木制配件。小雕像描绘了一个裸体女人，姿势僵硬，解剖部位刚刚提到，头部特征很好。这张脸有成年女性的特征，头发向后梳，在脑后挽成一个发髻。身体很瘦，只有骨骼和肌肉：乳房像胸部下方的小凸起，臀部略显突出，脚趾有切口，生殖器有裂缝。没有留下任何污渍或其他色素的痕迹。', '', '', '', 'https://www.maotorino.it/media/opere/Cc58temp.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4733', '国立东方艺术博物馆（都灵）', '手鼓演奏者的形象', '', '', '这位年轻的女演员，是一个由另外四名女演员和一名舞者组成的团体的一员，它被描绘成盘腿坐在一个薄的方形底座上，胸前拿着一个垂直的双手鼓。女孩的脸是圆的，脸颊上涂着粉红色的美丽，嘴巴上涂着红色，睫毛和眼睛都涂成黑色。额头上用红色绘制了花卉镶嵌图案（花典）。头发是黑色的，中间有分叉，梳成双髻（双髻）。这位女演员穿着一件低胸紧身胸衣，长袖，红色背心和一条宽大的垂裙，涂成白色，上面有红色的条纹。一条棕色的长围巾，系在左胸的腰带上，穿过肩膀，向右走，然后落在小雕像的底座上。', '', '', '', 'https://www.maotorino.it/media/opere/Cc18c.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4734', '国立东方艺术博物馆（都灵）', '精加工零件', '', '', '两个饰面由矩形梯形金属板组成，然而，它的主要底部形成了一条宽阔的曲线，使物体向其斜侧显著扩展。沿着弯曲的部分，金属箔弯曲形成一个超过一厘米高的肩部。只装饰了板的外部及其边缘，内部光滑但未加工。主要装饰由三排厚“S”形的方形蜗壳组成，这些蜗壳沿着其波浪形边缘沿着饰面的轮廓。侧面：每个图案一半被金箔覆盖，另一半被银箔覆盖。镶嵌的金银细线在内缘和蜗壳之间穿过表面。肩部有其他雕刻图案，仅部分镀金或镀银。', '', '', '', 'https://www.maotorino.it/media/opere/Cb63a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4735', '国立东方艺术博物馆（都灵）', '竖琴演奏者的形象', '', '', '这位年轻的女演员，是一个由另外四名女演员和一名舞者组成的团体的一员，它被描绘成盘腿坐在一个薄的方形底座上，他打算用双手举到胸前弹奏竖琴。少女圆圆的脸，脸颊上画着粉红色的美人妆，嘴巴上涂着红色。额头上用红色绘制了一个三角形的花朵镶嵌图案（花点），眉间的顶点。头发是黑色的，中间有分叉，梳成双髻（双髻）。这位女演员穿着一件低胸紧身胸衣，袖子又长又窄，一件红色的banbi背心和一条宽大的垂坠裙围在胸前，完全覆盖了双腿并落在基座上。一条长长的红领巾，系在腰带上，与左胸齐平，披在肩上，垂在右侧，然后落在小雕像的底座上。', '', '', '', 'https://www.maotorino.it/media/opere/Cc18b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4736', '国立东方艺术博物馆（都灵）', '绑着头发的外国护发素', '', '', '所描绘的人物站立时，双腿略微分开，在一个带有斜角的薄矩形底座上。躯干稍微旋转，头部向右旋转，相应的手臂抬起并折叠，左侧远离身体，但保持低位。动态姿势和双手握拳（右侧与面部高度一致，左侧与臀部相对应）他们强调抓住动物（马或骆驼）的缰绳，这是马夫必须控制的。这个人有明显的土耳其-阿尔泰式特征：宽阔的脸，突出的颧骨，扁平的鼻子，多肉的嘴，细长的眼睛。他把长发用打结的发带绑在额头上。这件衣服是由一件长到膝盖以上，向右交叉的长袍组成的，领口宽而尖，袖子短而窄：它的腰部由刚才提到的腰带支撑，它的右侧还支撑着一个长方形的手提包。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_95.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4737', '国立东方艺术博物馆（都灵）', '带有边缘和青铜“Pushou”的漆花瓶', '', '', '梨形血管在低圆柱形的脚上，大的腹部逐渐缩小成一个长的喇叭形的颈部，下摆由光滑的青铜垫圈加固，凸盖插入互锁。铺上漆的木头很轻；花瓶的肩部有两个青铜动物形态面具（pushou）。花瓶的内部完全涂成黑色；相反，物体的整个可见表面都覆盖着一层厚厚的深棕色亮漆。装饰，既不影响盖子也不影响脚，位于两个水平带之间。', '', '', '', 'https://www.maotorino.it/media/opere/Cl11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4738', '国立东方艺术博物馆（都灵）', '带有流线型肩部和高领的花瓶', '', '', '这个花瓶具有最古老的马家窑类型陶土的典型形状和装饰。它有一个高圆柱形的脖子，有一个向外的下摆和一个强烈扩张的肩膀。达到最大直径后，在与腹部的交界处形成一个倒角，下面的花瓶逐渐缩小到平底。在这个又厚又深的肚子上，顶部有两个环形环。脖子上有五条黑色条纹与白色或未涂漆的条纹交替出现。脖子和肩膀之间的通道被一条黑色的粗带标记；再往下，五条水平线由三个等距的黑色圆圈对齐，每个圆圈的周长都用白色点缀。在两条黑色的厚带之间，主要的装饰带由线条和几何图形依次组成。', '', '', '', 'https://www.maotorino.it/media/opere/Cn12.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4739', '国立东方艺术博物馆（都灵）', '双金属剑的手柄', '', '', '这个全青铜的手柄相当平，在每一侧装饰有两条线，这两条线在花岗岩底部以“X”形相交。刀柄很长，有对称的侧刺和凸起的边缘：第一对突起，松果体形状，它位于手柄的正下方，然后护板继续略微收缩，以在底部的两个尖翼再次打开；增加的两对滚花金属尖端在刀柄中央横向突出，以提高其保护功能。在内部，它用与边缘相同的浮雕薄壁装饰：一条中心线将两排四个填充的矩形肺泡分开。它给你一个轻微的浮雕。刀柄的末端包围着铁刃的后跟，形成三个尖端。铁铜结合的兵器技术，肯定起源于中原的中国，自西周末期（公元前8至7世纪）以来，它就被证明了。C。', '', '', '', 'https://www.maotorino.it/media/opere/Cb070b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4740', '国立东方艺术博物馆（都灵）', '高柱上的房屋模型', '', '', '这座又宽又窄的房子的模型，竖立在四根圆柱柱子上，它由两个重叠的可移动部分组成：带柱子的支撑和实际的建筑物。无论是尺寸还是细节，都被不同人物的全方位小雕像所丰富。下部由一个低矮的长方形底座组成，四根柱子靠在底座上，成对靠近。在四根柱子之间的中心，因此在建筑物下面，一个比其他小雕像更大的沼泽人物脱颖而出：也许是主人，他戴着官员的头饰，长鞋和一件也能遮住他的手的束腰外衣。建筑物的上部是一栋带有两层屋顶的房屋，三个较小的侧面被墙壁封闭，而正面则在带阳台的阳台上打开。', '', '', '', 'https://www.maotorino.it/media/opere/Cc77.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4741', '国立东方艺术博物馆（都灵）', '马车的拨浪鼓', '', '', '拨浪鼓设置在一个高空心梯形支架上，截面为正方形。它被用作套筒，通过四个脚踝固定在杆上，这些脚踝插入在每一侧底部打开的圆孔中。在上侧的中央，有一根向上锥形的方形横杆，将套筒连接到拨浪鼓上。这是卵形的，前半部分穿孔成小三角形，向中央小孔汇聚；而后半部分则饱满光滑，只有中间有一个小孔。拨浪鼓囚禁了一个球，当它撞击内壁时会发出悦耳的声音。六个不同大小的元素可以放在一个梯子上，并根据相同的模型制作；最小的发出最尖锐的音调。拨浪鼓以两个或三个为一组固定在轭上。', '', '', '', 'https://www.maotorino.it/media/opere/Cb60d.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4742', '国立东方艺术博物馆（都灵）', '有贡品的人物', '', '', '这个角色，可能是一个穿着男装的年轻女子，站在那里，体重稍微向左移动。他双臂交叉在胸前，右手拿着一个无法辨认的小物件；左手，也是握紧的，它留在右边下面。这个角色的特点是一个大的“野蛮人头饰”（humao），尖端高而圆，边缘呈三叶形。人物的全脸具有精细的特征，嘴上有红色颜料的痕迹，其他身体特征为黑色。就像头饰一样，长到小腿的长袍也是外国风格的。这种类型的人物通常代表贡品或鹰猎者。', '', '', '', 'https://www.maotorino.it/media/opere/Cc27.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4743', '国立东方艺术博物馆（都灵）', '带菱形孔的套筒斧头', '', '', '斧头有一种罕见的形状：在几乎椭圆形的扁平刀片中，隐约呈菱形，对位的是一个中心孔，它绝对是菱形的，顶点是圆形的，向后移动到套筒上。这是管状的椭圆形截面和加厚的边缘，垂直连接到一个小的截锥柄。沿着后两个元素的轮廓清晰可见的肋骨证明了该物体在双壳类模具中的融合。在袖子的中间，在两边，具有技术原因的方形通孔：木柄被插入其中的横钉穿过它被脚踝固定住了。刀片的一侧留下了厚厚的织物的痕迹。这个斧头似乎是受草原艺术的某些模型的启发，其中一些模型上装饰着动物图案。', '', '', '', 'https://www.maotorino.it/media/opere/Cb37.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4744', '国立东方艺术博物馆（都灵）', '带高头罩的外国护林员', '', '', '描绘的人物站在一个细长的矩形底座上，底座呈斜角，身体的重量落在左腿上。将躯干和头部稍微向右旋转，相应的手臂抬起并折叠，左侧远离身体，但保持低位。动态姿势和双手握拳（右侧与面部高度一致，左侧与臀部相对应）他们强调抓住动物（马或骆驼）的缰绳，这是马夫必须控制的。这个人有明显的伊朗-白种人特征：浓密的胡须和胡须，大鹰鼻，浓密的眉毛，大眼睛。他戴着一个高的“野蛮人头饰”（humao），尖端是圆形的，翻领很宽。这件衣服由一件长到膝盖以上的长袍组成，向右交叉，领口宽而尖，袖子短而窄。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_86.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4745', '国立东方艺术博物馆（都灵）', '短剑和双鞘', '', '', '双鞘被设计成包含两种武器，一把短剑和一把匕首：只有第一把被保存了下来。两个不同尺寸的护套在顶部焊接在一起，长度超过一半，然后它们通过分离而变窄，并在较短的护套末端重新连接。拱形底部最初在两者上都被刺穿了。每一个在外表面上都装饰着一排八个不同大小的穿孔三角形：它们的尖端都朝下，除了最后一种，它们是对称地倒置的，外壳向底部延伸。在前面，在大的外部上部和小的外部下部有两个水平孔眼：它们用于固定在ci上。对角线而不是垂直方向的双鞘，可能是为了方便武器的出鞘和上鞘。这把剑有一个喇叭形的圆锥形手柄。', '', '', '', 'https://www.maotorino.it/media/opere/Cb47.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4746', '国立东方艺术博物馆（都灵）', '虎形装饰应用', '', '', '牌匾上画着一只浮雕的老虎，侧卧着，末端卷曲着长长的尾巴；仅表示身体右侧折叠的腿。头部呈圆形，向右抬起。比例是正确的，但是宽阔的腿，方头和笔直的耳朵表明与真正的动物有一定的距离。地幔是以传统的方式呈现的，通过一系列深刻的耦合拱门。板的内部是空心的。', '', '', '', 'https://www.maotorino.it/media/opere/Cb25.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4747', '国立东方艺术博物馆（都灵）', '带艾尔莎装饰的剑', '', '', '这把剑由一个带有两个环形凸起的圆柱形手柄和一个带有中央肋骨和切口的大双翼刀片组成。非常锋利。剑柄呈菱形截面，四边向剑刃略呈“V”形倾斜。在两面中的一面上描绘了一个由镶嵌的半宝石组成的动物面具，相对于中央肋骨对称：两个圆形的绿松石和其他长方形的切口分别画出眼睛和嘴巴，而对于面具顶部的蜗壳形状的抽象细节，则使用了一块介于棕色和黄色之间的石头。阿洛。在刀柄的另一面，同样的棕黄色石头构成了抽象的蜗壳装饰。这可能是一把阅兵剑。', '', '', '', 'https://www.maotorino.it/media/opere/Cb57.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4748', '国立东方艺术博物馆（都灵）', '平底小碗', '', '', '用刮刀把碗刮干净，然后小心地把它打磨光滑。它没有脚，位于扁平的底部，呈凹形。在外部，装饰仅限于沿边缘填充的六个拱门（或“花彩”）的图案。容器的内部装饰看到两个大的镜面图案并列在边缘下方。每个都由顶部的对角线带和底部的三个宽的水平弯曲带组成，它们在末端汇合，形成尖粒形状。杯子中央未装饰的空间，除了侧面的图案，是杏仁形的。总的来说，这种装饰在马家窑陶瓷中并不多见。', '', '', '', 'https://www.maotorino.it/media/opere/Cn36.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4749', '国立东方艺术博物馆（都灵）', '带小开口的球状罐子', '', '', '这个圆形容器的形状和装饰意在模仿柳条或竹子编织的篮子。开口的边缘是凹进的，边缘凸出且光滑；身体被挤压，短小的腹部向扁平的底部收缩，比罐子的开口宽一点。印有浮雕图案的装饰几乎覆盖了花瓶的整个外表面：它再现了蔬菜条纹的紧密交织。这样，它们内部就会出现圆形图案。一条波浪形的装饰带，用梳子制成，围绕边缘1厘米多一点。这块米色的面团上覆盖着半透明但不太亮的釉面，釉面一直延伸到腹部的一半左右。', '', '', '', 'https://www.maotorino.it/media/opere/Cv57.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4750', '国立东方艺术博物馆（都灵）', '康塔斯托里', '', '', '朔昌演员（说书人的表演类型，部分讲述，部分演唱）这里描绘的是用一个小鼓来讲述一个故事，小鼓支撑在左臂下，用一根棍子敲打。握在右手里。整个身体似乎都参与了这个故事，夸张的模仿使脸部扭曲成怪诞的特征。这个人身材矮小，肥胖且不成比例，四肢短于细长的躯干和大腹：他可能是个侏儒。他赤裸着上身，胸部明显松弛，巨大的屁股靠在凸起的圆形底座上。他只穿了一条露肚脐的大裤子。他头上戴着一顶笔直的帽子，中间是圆的，后面是凸起的。', '', '', '', 'https://www.maotorino.it/media/opere/Cc66.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4751', '国立东方艺术博物馆（都灵）', '罗汉·阿西索·因·达尼亚尼穆德拉', '', '', '罗汉坐在莲花的位置。莲花座），右腿在左腿上方，双手合十，以冥想的姿势（cin.dingyin）。覆盖座椅前部的方形面板突出了人物的尊严。剃光头是一个非常饱满的椭圆形，有大耳朵和明显的鼻子；眼睛半睁着，嘴巴紧绷，嘴唇突出，脖子上露出三道美丽的折痕。罗汉穿着一件厚重的僧袍，披着与雕像中轴对称的大褶皱。', '', '', '', 'https://www.maotorino.it/media/opere/cs11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4752', '国立东方艺术博物馆（都灵）', '袖口斧头', '', '', '斧头很重，很厚，而且刀刃很圆：我们怀疑它是否有锋利的刀刃，相反，我们认为它最初也被用作打击武器。刀片是矩形的，但在“螺纹”处变宽，并连接到一个椭圆形截面的套筒，该套筒在两端的一端稍宽。袖子上连接着一个长方形的扁柄，显然没有任何功能作用。在袖子上画了两个平行的浮雕部分，模仿了将斧头固定在手柄上的鞋带：它们围绕着东部。套筒的末端，并在两侧从刀片的侧面向刀柄的中心对角线会聚。', '', '', '', 'https://www.maotorino.it/media/opere/Cb36.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4753', '国立东方艺术博物馆（都灵）', '栖息的鸟形装饰品', '', '', '这个圆形雕塑描绘了一只张开翅膀和尾巴的勐禽，头部抬起，爪子紧贴水平排列的圆柱形支架。比例和形状有些逼真，尽管缺少一些细节，而且身体相当扁平。两部分的尾巴和翅膀的羽毛是用简单的平行雕刻线条绘制的。头部有一个厚实的钩喙，凸起的圆眼睛和一个小的枕骨突起。在支架下方，在中间，有一个圆孔，其边缘被检测到：它标记了原始支架的断裂点，可能是由于雕塑的巨大重量而发生的分离。这可能是青铜盆的装饰品。这只鸟可能在容器中央的某种栖息地上。', '', '', '', 'https://www.maotorino.it/media/opere/Cb22.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4754', '国立东方艺术博物馆（都灵）', '谷物酒的“酒尊”容器', '', '', '骨盆具有相当深的经典形状，腹部呈半球形，底部平坦，外部形状轻微。它有一个宽而平的外唇，略微向上弯曲。它靠在三只短而圆的脚上，这三只脚从戴着面具的嘴巴里伸出来：用于小耳朵和冠下的毛簇。它们可以被解释为虎头蛇尾。其他三种动物形态面具（普寿）带有凸起的环，它们贴在骨盆腹部的中央，每个环位于一只脚和另一只脚之间的空间的中间。其余的装饰是通过直接雕刻酒尊（用于与谷物酒一起喝的酒）的表面来获得的。', '', '', '', 'https://www.maotorino.it/media/opere/Cv73.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4755', '国立东方艺术博物馆（都灵）', '漆木马', '', '', '除了单独连接的尾巴外，这座雕塑是由一块独特的实木制成的。它以清晰而精确的切口雕刻而成，在几何线条中保留了对其做工的记忆，从而增强了其体积。这匹马最初完全被黑漆覆盖，其中留下的痕迹很少：目前，漆底的不透明棕色层占主导地位。身体粗壮，腿很短，胸部和颈部有力，头部不成比例地大，鬃毛被剪短。这只动物被描绘成僵硬而直立的姿势，嘴巴张开成亚硝酸盐，小耳朵笔直向前。模型的所有特征都表示对动物肌肉的仔细观察，但是，它被分解成不同的卷，因此被夸大了。', '', '', '', 'https://www.maotorino.it/media/opere/Cl12.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4756', '国立东方艺术博物馆（都灵）', '勇士', '', '', '小雕像描绘了一位站立的战士，双腿微微分开，手臂摆好姿势，好像在拿着武器。雕像的特点是头部较大，略微向右转，面部富有表现力，并以漫画的方式进行了详细描述。头部由最初涂成黑色的头盔保护，上面有一个灯泡形状的顶部；头盔有一个下摆，侧面边缘有凸起和折叠的翅膀。战士穿着一件带有彩色高领的盔甲，肩带露出手镯，其带有凸起尖端的立方体让人联想到头盔的形状。胸甲实际上是由皮革板制成的，在领口下方有一条打结的双垂直花边。腹股沟保护，浅裂，中间有一个凸起的圆盘，臀部穿在几件重叠的衣服上。小腿的棕色前部，在黑色的鞋子上方，暗示着小腿的存在。', '', '', '', 'https://www.maotorino.it/media/opere/Cc03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4757', '国立东方艺术博物馆（都灵）', '带脚的杯形支架', '', '', '这个高茎上的杯子形状的物体是用来提供食物的，由三个部分组成：托盘，茎和脚。整个物体的表面涂有一层明亮的黑漆，上面有不透明的红色和黄色装饰图案：颜料的粘合剂中可能含有少量的漆。托盘的上表面被均匀地涂成红色，但是使用中的擦伤和划痕让人瞥见了黑色的准备层。相反，外部有一个红色的主要图案，重复了四次，由对角线和蜗壳的组合组成，在黑色背景上与反场中的空白空间一起播放。在茎的中间部分，只有中间的环被涂成红色。下面开始了另一种装饰，让人联想到托盘的装饰，但更清晰，更弯曲，一直延伸到脚的底部。', '', '', '', 'https://www.maotorino.it/media/opere/cl06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4758', '国立东方艺术博物馆（都灵）', '带有拟人装饰的罐子', '', '', '这种球状的锅具有光滑的壁，并由精细且精制的面团制成。高弯曲的脖子，有薄壁，末端有一个非常喇叭形的下摆。在花瓶的两个侧面之一的中央，肩膀上贴着一个人物的半身像。右臂抬起，手掌向前张开，使这个奇特的人物变得更加特别。这个角色，也许是一个萨满，他的脸在额头，鼻中隔到颧骨和下巴上都画有黑色线条（纹身？）。手掌也是黑色的，容器的整个装饰系统也是黑色的。', '', '', '', 'https://www.maotorino.it/media/opere/Cn09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4759', '国立东方艺术博物馆（都灵）', '大椭圆形杯子', '', '', '形状罕见的杯子，恢复了“有翼杯子”（yushang）的形状，但手柄的形状有所不同，尺寸大于平均尺寸。此外，平底靠在四个两两面对的半圆形脚上。木芯相当厚，是横向切割的。像在豫商中一样，手柄从杯子边缘的高度开始，并以长边为中心；但是，它们没有典型的“机翼”形状。杯子完全覆盖着黑漆，里面覆盖着一层朱砂红漆。物体的几何装饰至少与其形状一样独特，并且是由红色和黄色颜料交替进行的。杯子，和Cl/4一起。D，它看起来相当古老，可以追溯到漆器的装饰曲目仍然严格依赖于b的时代。嗡嗡声。小单元并排成窄行和带状，让人想起六世纪的青铜器装饰。', '', '', '', 'https://www.maotorino.it/media/opere/Cl03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4760', '国立东方艺术博物馆（都灵）', '带猪的猪圈模型', '', '', '矩形围栏在长边的一侧由一个倾斜的平面并排，它可以进入一个带有半开的小门的高而窄的角落结构。这座建筑用作厕所，直接排放到猪圈的隔间中。屋顶略呈拱形；院子的对面开了一个小通风窗。厕所旁边的围栏的短边被向外倾斜的屋顶覆盖。里面是一只躺着的猪的雕像，前面是一个小的矩形凹槽。使这个模型恢复活力的薄而不稳定的陈列柜不覆盖庭院的内部，而只覆盖猪。', '', '', '', 'https://www.maotorino.it/media/opere/Cc73.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4761', '国立东方艺术博物馆（都灵）', '“京平”（“KUNDIKA”）水瓶', '', '', '这个烧瓶的主体几乎是球形的，放在一个高的、喇叭形的、凹形的脚上。在高颈上方，衣领变厚成一个形状的圆盘。圆锥形开口的边缘有两个凸起的环。肩部的浇口由一个几乎垂直连接的卵形底座和一个开放的壶形喷嘴组成。在肩部与花瓶腹部相接的地方有细的刻线。另外两个，近的和斜的，在衣领上方开口的中间：也许是转弯的线。带有细裂纹的毯子在最厚的地方呈现出玻璃状的外观。', '', '', '', 'https://www.maotorino.it/media/opere/Cv06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4762', '国立东方艺术博物馆（都灵）', '带珍珠和书的菩萨', '', '', '菩萨坐在带有平台的四边形座位上，我们不知道这是否是他最初的支撑。脚是粗壮的，手又长又细。右腿弯曲在身体旁边，脚掌放在座椅上，左腿垂直落下，脚放在平台上；右臂放松地放在相应腿的膝盖上，用手在拇指和中指之间优雅地握住一个小球；另一只手臂伸到身体旁边，手放在放在底座上的一本书上。头部饰有圆形头饰，中央部分饰有精致的花卉装饰。头发和胡须是黑色的，是雕像中唯一不是金色的部分。', '', '', '', 'https://www.maotorino.it/media/opere/Cs18.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4763', '国立东方艺术博物馆（都灵）', '谷物酒的“佳”花瓶', '', '', '青铜礼器（嘉）为了加热和饮用葡萄酒，根据一些用于纪念祖先和商神的洒在地上的仪式。该容器具有特征，可以将其归因于古代阶段之间的过渡时期（二里岗，公元前16至14世纪）。和安阳二期（公元前1300-1046年）商代，就像脚和身体的高度相等一样，带有旋钮的不太明显的柱子，简单的水平条纹装饰，平坦的表面。有两条装饰带，一条在中心，一条在花瓶主体的底部。上带呈现“闪电图案”（雷文），下部的中央区域由一个动物形态的面具（taotie）组成，眼睛间隔很大。', '', '', '', 'https://www.maotorino.it/media/opere/Cb01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4764', '国立东方艺术博物馆（都灵）', '“真木寿”，坟墓的保护生物，在高处', '', '', '令人印象深刻的奇妙生物雕像，具有怪诞的人脸，高角和翅膀，蹲在后腿上。它的头部是雕像唯一没有上釉的部分，朝上。那张有着隐隐约约的西方特征的脸是严厉的：浓密的皱眉，圆圆的凸出的眼睛，宽而扁平的鼻子，肉质紧实的嘴巴。脸的底部是紧凑的新月形胡须，上面刻有平行的轮廓，两侧有两个巨大的耳朵，形状像扇子或条纹贝壳。头上有一个很高的扭曲的圆锥形喇叭，在底座的两侧有两个缩小的喇叭。在脸上，原本覆盖着白色的燕窝，眉毛和固定的瞳孔仍然被涂成黑色。在生物的肩膀上，有一些不太厚的翅膀从正面升起。', '', '', '', 'https://www.maotorino.it/media/opere/Cc43b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4765', '国立东方艺术博物馆（都灵）', '负重的骆驼', '', '', '这只动物体型健壮，姿势静止，头部抬起，略微向右转：枪口的细节塑造得很好，涂成红色的半张开的嘴很好地体现了活动嘴唇的想法，赋予了动物自然的表情。相反，羊毛皮是用致密的粘土带制成的，涂在头部和肌肉发达的脖子上。驼峰和背部，被一块布覆盖，它们几乎消失在手杖的质量下：在平行夹板的侧面支撑上（土耳其和蒙古圆形帐篷的框架？），两个巨大的鞍座横向放置在驼峰之间。在袋子上绑着一些卷起来的织物和织物的绞线，而在承载货物的带子上还挂着其他几件物品，包括野味、容器和水壶。构成货物的物品将代表对死者的祭品，而不是大篷车真实货物的复制品。', '', '', '', 'https://www.maotorino.it/media/opere/Cc32.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4766', '国立东方艺术博物馆（都灵）', '岩石基地上的守护者（DVARAPALA）', '', '', '放置在寺庙或佛教祭坛入口右侧的“门的守护者”（dvarapala）。剃光头的顶部有一簇被丝带包围的头发。愤怒的鬼脸和非凡的胸部肌肉与经典的阿波特罗描绘相对应。派切。左臂以威胁的姿态抬起，头后和肩膀周围的飘动围巾为身材增添了活力。此雕塑与Cs/12a形成一对。', '', '', '', 'https://www.maotorino.it/media/opere/Cs12b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4767', '国立东方艺术博物馆（都灵）', '带侧固定环的斧头', '', '', '轴头长而细，呈扁平截锥形，截面呈椭圆形；开口沿主轴具有拱形的凹边。在一侧，大约在这个管状移植物的中间，在垂直位置有一个半环形孔眼，这是为了改善手柄的固定。在两个主面中的一个，在与钩子相同的高度上，开了一个又长又细的洞。手柄和刀片之间没有连续的解决方案：第一个变薄一点，然后横向加宽以形成一条线倒角成圆弧。在移植物的中央部分有一个排列在三个带上的装饰，其中只有最靠近刀片的那个是可识别的：它代表由多根线组成的辫子。', '', '', '', 'https://www.maotorino.it/media/opere/Cb65.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4768', '国立东方艺术博物馆（都灵）', '天王', '', '', '巍峨的天王像（守门；梵文lokapala）以霸气的姿势，描绘了制服躺在高基座上的洛查恶魔的行为。天王右手侧身，左手在胸前举起拳头。右腿伸直，压住恶魔的小腹，左腿弯曲，踩在他的额头上。头和手是雕像的非釉面部分，是冷漆的。国王有一种威胁性的表情，大眼睛突出，眉毛皱起，鼻孔扩张，长胡子突出，红红的嘴巴张着，做着鬼脸。下面的恶魔，仰卧着，头大而尖，头发蓬乱。他有一种威胁的表情，巨大而丰满的嘴唇，又小又圆的眼睛。它有一个幼稚的身体和四肢，用两只爪子武装起来，像踢一样向上推。', '', '', '', 'https://www.maotorino.it/media/opere/Cc06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4769', '国立东方艺术博物馆（都灵）', '平底杯', '', '', '杯子是马家窑生产的特色，用刮刀完成，并仔细打磨。它没有脚，位于扁平的底部，具有均匀的凹形。在容器的外部，大笔触的黑色装饰仅限于沿边缘的三个扁平花彩的图案。主要装饰覆盖了容器的整个内表面，并在空与满之间进行了精心的绘画流动性游戏。这类物品的装饰，其中，杯子外部的波纹也构成了中央螺旋的一种延伸，它需要很好地掌握该部分。', '', '', '', 'https://www.maotorino.it/media/opere/Cn34.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4770', '国立东方艺术博物馆（都灵）', '漆木马', '', '', '该雕塑没有原本要插入其中的尾巴，是由一块实木制成的。它以清晰而精确的切口雕刻而成，在几何线条中保留了对其做工的记忆，从而增强了其体积。身体粗壮，腿很短，后火车过度发达，胸部和颈部有力，头大得不成比例，鬃毛剪得很短。在枪口的强烈的蒙顿式轮廓上，突出了大而扩张的青蛙和非常突出的眼睛。模型的所有特征都表示对动物肌肉的仔细观察，但是，它被分解成不同的卷，因此被夸大了。', '', '', '', 'https://www.maotorino.it/media/opere/Cl13.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4771', '国立东方艺术博物馆（都灵）', '前面板', '', '', '正面由一块经过浮雕、雕刻和打孔加工的金属薄片组成，它描绘了一个具有怪诞人类特征的面具。叶片的轮廓在上部形成一个大的弧形，而侧面和底部则被挖空，以便在马头上精确定位：侧面雕刻，特别是，它们会绕过耳朵。两个巨大的拱形眉毛沿着上边缘，在鼻根处呈螺旋状扩大。这是大，矮胖和强烈的浮雕，由两对平行的切口标记；鼻孔穿孔了。圆而突出的眼睛中间有一个圆形的孔。小嘴也是镂空的，呈拱形，露出两排倒角的牙齿。在强烈的结垢下，可以感觉到胡须，胡须落在嘴的两侧。在面具的轮廓上有无数的小孔，因此可以将其缝在皮革支架上。', '', '', '', 'https://www.maotorino.it/media/opere/Cb62.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4772', '国立东方艺术博物馆（都灵）', '高颈整流罩花瓶', '', '', '这个花瓶的造型和装饰都是典型的最古老的马家窑类型的兵马俑，其特点是面团的高质量，容器的棱角轮廓和丰富的水平条纹绘画。从外缘开始，长脖子轻轻弯曲，并与花瓶的肩部无缝连接。身体继续向外强烈扩张，达到最大直径后，突然扭转了这一趋势。主要装饰带位于花瓶边缘上方。它由三个等距图案组成，由节省下来的椭圆形组成，由弧形曲线横向延伸：在每个椭圆形的内部都涂有黑色和白色，通过将两个弯曲的三角形连接到顶点而获得的斜“蝴蝶”图案。', '', '', '', 'https://www.maotorino.it/media/opere/Cn10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4773', '国立东方艺术博物馆（都灵）', '“真木寿”，人类面部坟墓的保护生物', '', '', '小雕像是空心的，描绘了一个人脸的真目寿，蹲在后腿上。这种生物有张开的肉质嘴唇，宽阔的鼻子，间隔突出的眼睛，皱皱的眉毛，大耳朵向外突出。浓密的胡须和长长的胡须被涂成黑色。头部中央立着一个向前弯曲的刮刀喇叭。在背部-在几乎看不见的尾巴上方-有一个带有三个圆形生长物的背脊。肩膀上有两个弯曲的扁平的钝尖翅膀。前肢呈肘骨骨刺，四条腿均呈蹄形。肚子是中空的。小雕像最初的复杂色素沉着无法立即识别。', '', '', '', 'https://www.maotorino.it/media/opere/Cc97.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4774', '国立东方艺术博物馆（都灵）', '带鸟头天线的匕首', '', '', '匕首是平的，无论是刀柄还是装饰元素，两面都非常相似。刀片，又长又窄，双线，它的中心有一个强烈的凸起的肋骨，一直延伸到手柄和手柄之间的连接处。穿孔的刀柄是由两只鸟的脖子和头部形成的，这两只鸟在这条中央肋骨前相对。这显然是两只勐禽，有着大而圆的眼睛，钩状的喙折叠成一个孔眼。动物的脖子被分成两个独立的元素，其中最外面的元素被装饰成像绳子一样扭曲的条纹。在扁平的手柄上刻有三只浅浮雕的鹿，非常程式化。手柄的末端部分重新提出了装饰手柄的相同的穿孔鸟类，唯一的变化是喙可以直接接触。在内蒙古毛青沟遗址及周边地区发现了多把带有鸟状触角的匕首。', '', '', '', 'https://www.maotorino.it/media/opere/Cb53.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4775', '国立东方艺术博物馆（都灵）', '带有蒜头边缘的扁壶“扁壶”', '', '', '装谷物酒的容器，颈部短，身体宽，扁平，呈椭圆形，脚高呈长方形。在这款白胡的经典造型中，加入了一个带有六边形装饰的颈部（也称为“蒜头”）。就在一条很短的直边下面。颈部的内径是恒定的，因此通过加厚瓶壁来获得装饰。这个技术细节是3世纪青铜器的典型特征，而后来的裂片将变成空心的。另一方面，直边反而是汉初作品的特点。腹部的椭圆形在两侧由两个同心槽和一个突出的中央部分突出。脚是中空的，边缘凹陷，底部在外面的中心有一个环形钩子。', '', '', '', 'https://www.maotorino.it/media/opere/Cb12.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4776', '国立东方艺术博物馆（都灵）', '密封的骏马', '', '', '由于后腿弯曲，左前伸展和左抬起，这匹马呈现出突出的活力。头部向左转，并向强大的颈部强烈折叠。张开嘴，扩张的青蛙和在皮肤下形成的枪口肌肉很好地帮助唤起动物的头皮和神经亚硝化反应。鬃毛梳理在右侧，锁扣成形。额头上的两簇更长，紧贴耳朵，而尾巴则简单地打结。弓形是由一个向前延伸到肩膀的厚厚的椭圆形臀部组成的。在瓜尔德拉帕上有一个鞍座，上面覆盖着一块绑着的布，在大腿上有很多褶皱。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_87.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4777', '国立东方艺术博物馆（都灵）', '带装饰圆锥形手柄的匕首', '', '', '宽斜刃匕首，中央部分平坦，侧面形状和三角形尖端。刀片连接到一个更宽更厚的小刀柄上，梯形轮廓：两侧的电缆装饰由两个并排的方形螺旋组成。椭圆形截面的空心手柄，向下逐渐变细，装饰有雕刻，代表两侧并列的两排希腊字母。手柄的末端是一个扁平的旋钮，也是椭圆形的，通过一个用于通过悬挂带的矩形孔横向穿过。手柄上还有其他非常小的孔，这一定是出于技术原因。刀片的形状，斜角和三角形尖端在小范围内让人联想到公元前六至五世纪的中国剑。虽然这把匕首不是严格意义上的“中国”，而是来自北郊地区，它似乎来自那些剑。', '', '', '', 'https://www.maotorino.it/media/opere/Cb54.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4778', '国立东方艺术博物馆（都灵）', '坐着的年轻女子', '', '', '这位女士坐在沙漏形的凳子上，上面和下面装饰着菱形雕刻背景上的大圆花浮雕；在座位的底部增加了四朵含苞待放的花。这位女士胖乎乎的脸和大大的发型与她瘦小的身体和小手形成了鲜明的对比。右手握着一根花枝或一朵“长生不老的蘑菇”（灵芝）。女人的脸被涂成冷色，雕像的其余部分被涂成绿色，奶油色和琥珀色的混合色。头发中间有分叉，在头部两侧聚集成两个又大又高的发髻。玻璃的绿色弄脏了臀部和颈部的左侧。', '', '', '', 'https://www.maotorino.it/media/opere/Cc13.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4779', '国立东方艺术博物馆（都灵）', '男性裸体雕像', '', '', '这种类型的人体模型的可拆卸木臂固定在与肩膀高度相同的孔中。他们后来穿着布衣，并可能配备了武器和木制盾牌。小雕像描绘了一个赤身裸体的人，姿势僵硬，解剖部位刚刚提到，头部特征很好。这张脸有年轻人的特征，长发梳着中间的分叉，把它们收回来，放在头上的一个小包里。身体苗条，骨骼和肌肉仅建议：胸部和臀部略显突出，脚趾被雕刻，生殖器的浮雕图案。肉色的凹槽几乎完全覆盖了小雕像，但其他颜料没有保存下来。一个多世纪以来，这些人体模型被广泛用于皇家陵墓和汉族贵族的装饰，它们大多是在首都本身制造的。', '', '', '', 'https://www.maotorino.it/media/opere/Cc57a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4780', '国立东方艺术博物馆（都灵）', '坟墓的守护神', '', '', '根据一些学者的说法，这个具有恶魔特征的人物描绘了一位驱魔人，他将恶魔和瘟疫从坟墓中驱逐出去。而另一些人则认为他是负责保护死者和他的坟墓的神（真木神或真木勇）。雕像是站立的，粗壮的腿分开，与细长的身体和小胳膊不成比例。他的手臂弯曲，双手在胸前紧握着一条蛇：蛇，在想象中的汉族，他们是食尸鬼，必须被赶出坟墓。雕像的头部构成了整体中最令人惊讶的部分，头部两侧有两个大猪耳朵，皱眉上方有三个圆形的角，眉毛下方有轻微的凸起，凸起更明显，大鼻子，张开的嘴上长着两颗锋利的尖牙，最后垂下的舌头在胸前形成一条长长的扁带。', '', '', '', 'https://www.maotorino.it/media/opere/Cc64.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4781', '国立东方艺术博物馆（都灵）', '“YUE”衍生的半月形斧头', '', '', '刀片有一条宽的半圆形螺纹，两端尖尖，用弯曲的曲线连接到一个方形的鞋跟上，该鞋跟又在一个长的矩形的鞋柄上闭合。刀片中央有一个大圆孔，与螺纹同心，在其附近，物体的均匀厚度在两侧增加：因此，孔的模制边缘具有凹槽c圆形，可能是最初插入装饰品的地方。在脚后跟的角落，向刀片的底部，打开了两个矩形孔，用于将斧头固定在手柄上：这也必须至少部分覆盖后面的刀柄。这把斧头的造型沿袭了传统的越式商刀，很可能是一种礼仪武器。', '', '', '', 'https://www.maotorino.it/media/opere/Cb35.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4782', '国立东方艺术博物馆（都灵）', '猪圈模型', '', '', '带有倾斜屋顶的圆形猪圈，上面有一个灯泡。表面有规律的穿孔让人联想到一层由树枝束固定在一起的稻草覆盖：这些由六根破布肋骨表示。巨大的，每个都在中心和末端部分凸起，好像暗示着两个前缀。屋顶从陈列柜焊接到下面的圆柱墙，其中切割了方形入口和十四个又高又窄的矩形开口。三个柔软的水平螺纹完成了围栏的装饰：一个在开口对齐的上方，一个在下方，第三个朝向模型的底部。里面放着一头猪，它的背部有切口条纹，正对着入口。除底座外，玻璃覆盖了整个内部和外部。', '', '', '', 'https://www.maotorino.it/media/opere/Cc79.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4783', '国立东方艺术博物馆（都灵）', '铠甲勇士', '', '', '战士被描绘成站立的静态姿势，大脚并拢，小臂紧握：右手举在胸骨下，左侧靠近相应的臀部。右手有一个通孔，最初可能在该孔中插入了武器。巨大的圆头突出了整体的不成比例感，顶部是带有长矩形耳罩的圆顶头盔。然后，头上戴着一顶风帽型的帽子（“风帽”），它沿着肩膀向下分成三个圆形的襟翼。面部的细节是模糊的-浓密的眉毛在弯曲的额头下皱起，大眼睛和明显的眼睑，迷彩的鼻子，小嘴巴-和绘画：注意浓密的黑胡子，眉毛和相同颜色的瞳孔，红色的嘴巴。', '', '', '', 'https://www.maotorino.it/media/opere/Cc93.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4784', '国立东方艺术博物馆（都灵）', '跪着的裸男', '', '', '小雕像描绘了一个裸体男人跪在弓箭手的位置，具有刚才提到的解剖部分和具有良好特征的头部。圆脸有年轻人的五官，长发梳着中分，把它们收回来，放在头上的一个小包里。身体苗条，骨骼和肌肉仅建议：胸部和臀部略显突出，脚趾被雕刻，沉默的生殖器。保持左腿呈直角抬起，脚踩在地上，右手放在膝盖上，然后向后躺在脚背上。与这些角色中的大多数角色相反，他们的身体均匀地被包裹着，我们的标本在原本应该被衣服覆盖的地方出现了其他颜色的斑点。也许这些是用与原始颜料不同的颜料修复的区域。', '', '', '', 'https://www.maotorino.it/media/opere/cc60.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4785', '国立东方艺术博物馆（都灵）', '锅铲形状的物体', '', '', '由长管状手柄组成的物体，该手柄的一侧连接到薄刀片，另一侧连接到配置的旋钮。刀片呈长方形，尖端呈圆形，可能被认为是一种刮刀。苹果由两个扁平的蛇头组成，每侧一个，每个都只有圆形的眼睛浮雕和钩状的角：侧面视图，两个头似乎合为一体，嘴巴张开，这个开口形成了一种钳子。手柄的装饰，略带浮雕的人字形，它唤起了爬行动物的身体，爬行动物的身体逐渐变薄，变成了刀片上部的中央肋骨。在与手柄连接的地方，它的侧面有两个朝向尖端方向的钩子；最后，在手柄的一侧稍高一点的地方有一个小吊环。可能有一根绳子被塞进了环里，并在手柄和手柄之间的凹槽上缠绕了几次。', '', '', '', 'https://www.maotorino.it/media/opere/Cb72.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4786', '国立东方艺术博物馆（都灵）', '到处都是猴子的碗', '', '', '碗没有脚，底部平坦，没有釉面，外面有三个同心切口。碗的中央固定着一个大的圆形空心底座上的卵形罐，顶部是一只蹲在其边缘的猴子，他向前推进，好像在瞄准它一样。动物的断尾和右臂从肩膀向下滑动是以前或现在的熔炉缺陷。玻璃化。倾向于黄色的绿色陈列柜明亮且有裂缝，覆盖了整个物体，并不规则地停在底座上方。', '', '', '', 'https://www.maotorino.it/media/opere/Cv12.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4787', '国立东方艺术博物馆（都灵）', '瓶盖（？', '', '', '它是一个沉重的中空物体，封闭在扁平的圆形底座上。它的顶部有一个尖尖的球根状，在顶部展开：在这里，在一边，面团的突起是人脸轮廓特征的背景。眼睛和嘴巴呈镂空，有细长的狭缝，小鼻子呈浮雕图案，在脸上可以看到细小的笔触痕迹。这个物体的功能仍然不确定。由于它的直径对应于罐子开口的平均直径，因此推测它可能是一个盖子。', '', '', '', 'https://www.maotorino.it/media/opere/Cn28.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4788', '国立东方艺术博物馆（都灵）', '吉亚拉·特里波德', '', '', '罐子有一个球形的主体，并以三只脚的形式放置在集市的爪子上。它的脖子很短，嘴巴向外。装饰由模制和应用的花卉纪念章组成。以绿色为主的陈列柜在花瓶底部以不同的方式停下来，部分覆盖了它的脚。这一时期应用于三彩陶器上的装饰品的灵感来源是银器的浮雕图案，受萨珊王朝和粟特金属艺术的影响，以及应用于中亚生产的陶瓷上的图案，总是受到金属原型的启发。', '', '', '', 'https://www.maotorino.it/media/opere/Cv10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4789', '国立东方艺术博物馆（都灵）', '斧头-匕首刀片“ GE ”', '', '', '这里展示的斧头-匕首刀片对应于最古老和最稀有的ge类型之一（斧头-匕首或戟），其特点是形状对称，并且没有孔可以将其固定在手柄上。狭窄的刀片，不是完全笔直的，中间稍厚，原本应该在两侧磨尖；尖端是钝的。在底部，它连接到一个从两端突出的细袖子上，然后继续形成一个几乎矩形的柄。正是这种双肩使木柄能够垂直于刀片固定。茎的末端部分，在不知不觉中向底部延伸，它装饰有非常简化的动物形态面具（道铁）。：这是一个降低的矩形扇区，在该扇区中，面具是通过很少的对称浮雕特征获得的。在茎的另一侧，设计是相同的。', '', '', '', 'https://www.maotorino.it/media/opere/Cb38.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4790', '国立东方艺术博物馆（都灵）', '“HU”花瓶，一个袖口和高领', '', '', '花瓶有一个高的圆柱形颈部，边缘强烈向外，嫁接在圆顶主体上，圆顶主体与凸底形成对比，底部狭窄。一个手柄将颈部连接到水壶的肩部，另一个钩状手柄朝下，它位于腹部和容器底部的交线下的截然相反的位置。图案装饰几乎覆盖了作品的整个表面，并以水平方向排列。下摆的内部和颈部的上部也被涂成黑色：第一个带有节省下来的锯齿形饰带，第二个带有交叉拱门的双花彩。脖子外面（分开的耳朵）它被均匀地涂上了赭红色；这种颜色在整个表面上占主导地位，几乎取代了饼干的颜色作为容器的背景。', '', '', '', 'https://www.maotorino.it/media/opere/Cn07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4791', '国立东方艺术博物馆（都灵）', 'BRICCO TRIPODE “ GUI ”与GRONDAIA的BECCCIO', '', '', '此桂具有大汶口晚期和龙山文化早期的典型特征。三个乳房形状的脚被应用于宽阔而扁平的身体：两个在前面，第三个更大，在后面。一条绳索浮雕线绕着肩膀下方的容器旋转，并在后面与手柄的下端连接。这个gui的独特元素，脖子比锅的其余部分高，并在前脚上方向前移动。它是管状的，向两部分的边缘延伸：在它的后面压扁成一个大的圆形嘴唇，前面继续有一个长而窄的尖嘴，朝上。一个凸起的圆盘作为装饰元素被应用在容器的肩部，与喷嘴成轴线。', '', '', '', 'https://www.maotorino.it/media/opere/Cn24.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4792', '国立东方艺术博物馆（都灵）', '凤头水罐', '', '', '水罐由梨形的身体组成，从一个高颈到一个凤头，喙上有一颗珍珠，从一个扁平的、喇叭形的高脚上，从一个连接鸟的头部和容器的肩部的拱形环上。壶口由一个低的椭圆形开口组成，在鸟的顶部上方张开。腹部的两半用模具装饰，主要人物被花卉元素包围在一个双叶纪念章中：从一个出发，在一朵大牡丹花的上方，一位疾驰的骑士用弓向后拉；另一边是一只张开翅膀和尾巴的凤凰，它的左腿站在一朵程式化的花上。这个水罐的设计灵感来自粟特的银器，在8世纪非常流行。', '', '', '', 'https://www.maotorino.it/media/opere/Cv08.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4793', '国立东方艺术博物馆（都灵）', '带有程式化生物装饰的两环罐子', '', '', '球状的奥拉，向上展开，向狭窄的基部逐渐变细，颈部从下摆向外张开。两个小孔眼对称地应用于容器最大直径以下。下摆的内部装饰有凹痕，黑色条纹和双“花彩”圆弧。相反，颈部有不规则的黑色人字形斑点。主饰带由两个大的网状圆圈组成，对称于耳孔上方的容器上部，与两个大的几何图案交替出现。这些奇特的图案中的每一个都是由某种垂直的树干组成的，其中之字形带对称地分开，其顶点和末端有时带有四个条纹。', '', '', '', 'https://www.maotorino.it/media/opere/Cn04.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4794', '国立东方艺术博物馆（都灵）', '三个安塞的小花瓶', '', '', '罐子是深玻璃杯的开口形状，带有大的喇叭形颈部和下部的凸起-可能与容器的两个组成部分之间的连接相对应。底部用刮刀修剪，这一操作使底座呈方形，并带有倒角。内表面和外表面上的规则平行条纹表明至少在完成物体时使用了车床。在薄边缘下方，以相等的距离施加三个连接到下部突起的薄带状环。', '', '', '', 'https://www.maotorino.it/media/opere/Cn33.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4795', '国立东方艺术博物馆（都灵）', '阿弥陀佛阿弥陀佛', '', '', '气势磅礴的佛陀坐在禅宗中，双手摆成禅宗的神秘形状（两个拇指连在一起，食指伸直，其他手指向上折叠）。在强大的身体上，有一个巨大的方头，浓密的卷发也覆盖着浓密的乌什尼沙。鼻子和嘴巴画得很好，眼睛在冥想中半睁着。阿弥陀佛崇拜以净土宗的教义在中国和日本获得了极大的普及。', '', '', '', 'https://www.maotorino.it/media/opere/Cs13.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4796', '国立东方艺术博物馆（都灵）', '“真木寿”，有蛇的坟墓保护生物', '', '', '这只小真木手爬在椭圆形的蹄子上，向左扭动，右腿抬起。它具有不对称和不规则的形状，似乎是通过组装从不同土壤或假土中获得的零件而制成的。该生物的鼻子收缩成鬼脸，犬齿可见。它的前额有两个长角，尖端朝前；在后面，鬃毛像披针形的膜一样僵硬地升起。在肩膀上，从左侧伸出一种翅膀，从另一侧伸出一个长而尖的突起，朝上，它恢复了尾巴的形状-反过来又笔直并与身体分离-并赋予了身材苗条的外观。三条可见的腿以三个钩状的爪子结束。右前威胁地站起来，对着一条缠在左爪上的蛇，向下躺着，而爬行动物则向真木寿的头部伸出。', '', '', '', 'https://www.maotorino.it/media/opere/Cc09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4797', '国立东方艺术博物馆（都灵）', '折叠式面罩', '', '', '这个中空的动物形态面具在大概是葬礼的背景下起到了支撑的作用。面具有突出的枪口特征，使用的元素，例如在额头上横向分开的大分叉角：它们非常弯曲和尖锐，上面有一个短的分支。怪物有突出的大眼睛，浓密的向下倾斜的眉毛，猪耳朵，宽而扁平的鼻子，巨大的拱形开口，可见方齿。圆口的顶部有一个尖尖的一端，也许是第三个角，下巴上有三个类似的尖端。对称的角和中央尖端被涂成相同的黑色。面具的异位功能是由所描绘的生物的悲伤和善良之间的表达来调解的。', '', '', '', 'https://www.maotorino.it/media/opere/Cc40.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4798', '国立东方艺术博物馆（都灵）', '双容器', '', '', '相当不对称的双容器，无需特别注意即可制成。两个球形的罐子，直口开口的形状，通过一个短的空心管状接头连接到腹部的中间，在边缘有一个弯曲的环。手柄由两条条纹纵向雕刻而成；一条有缺口的粘土条，涂在容器的边缘下面，它是唯一突出的装饰元素。花瓶的外表面有垂直的绳纹，底部是平的。一种是底部深深地印着编织的柳条，腹部下部被烟灰熏黑；在另一个，肚子上的绳纹几乎被抹去，表面粗糙。', '', '', '', 'https://www.maotorino.it/media/opere/Cn16.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4799', '国立东方艺术博物馆（都灵）', '维塔卡穆德拉的阿西索菩萨', '', '', '这座雕像可能与另一座雕像（Cs/7）形成一对，仅在手臂的镜面位置上有所不同。菩萨盘腿坐着，双手摆成“争论的姿势”（vitarkamudra）。座位由两个并列的莲花花冠组成，一个向上作为雕塑的支撑，另一个向下作为真正的基础。花只雕刻了外部花瓣，花瓣呈椭圆形，中央凸起。', '', '', '', 'https://www.maotorino.it/media/opere/Cs08.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4800', '国立东方艺术博物馆（都灵）', '用于提供谷物的“GUI”花瓶', '', '', '安阳（公元前1300-1046年）的青铜礼器，用来盛放经典形式的熟谷物祭品。，有喇叭形的脖子和外翻的下摆，向下展开的腹部和略微喇叭形的圈足。两个半环形的环在顶部配置有一只短角的年轻鹿的头。每个环的底部都有一个长方形的盘子，上面装饰着一条腿和爪子（鸟的爪子？），上面有轻微的浮雕。容器的颈部和底部各有一条装饰带，其特征是四个动物形态的面具（道铁）。它们刚刚从雷文（“闪电图案”）的底部分离出来，并在面具的侧面放置了垂直蜗壳。', '', '', '', 'https://www.maotorino.it/media/opere/Cb04.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4801', '国立东方艺术博物馆（都灵）', '“Fanghu”高脚四边花瓶', '', '', '这种方胡的光滑光滑的身体——“方胡”，用来装谷物酒和谷物本身-它是从中空的脚上伸出来的。动环手柄由应用的动物形态面具（pushou）保持。颈部在其最高和喇叭形部分呈凸起状。花瓶配有截头金字塔形状的联锁盖。在四个斜面上的每一个都是固定的，在中心和垂直方向，一个穿孔的层流圈，隐约让人联想到一只鸟：环上方的突起看起来像山脊，而喙则指向盖子的中心。在公元前3世纪末和2世纪上半叶的墓葬中发现了许多类似的方胡。', '', '', '', 'https://www.maotorino.it/media/opere/Cb16.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4802', '国立东方艺术博物馆（都灵）', '用于提供半球形腹部肉类的“丁氏”三脚架花瓶', '', '', '自公元前8世纪以来，带盖三脚架经历了巨大的发展。，也许是由于仪式的演变和肉类供应性质的变化。封闭后，容器必须呈球形。身体是大腹便便的，开槽的下摆使盖子的接合非常明显。两个大的垂直开口连接到容器的肩部，以使其能够关闭。脚很大，向外向底部呈喇叭形。它们每个的上半部分都由一个有脊的动物形头组成，也许是一条龙，其余的脚从龙的下颚流出。丁身上的装饰仅限于一系列绝对不对称的蜗壳几何图案。', '', '', '', 'https://www.maotorino.it/media/opere/Cb06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4803', '国立东方艺术博物馆（都灵）', '带脚和宽边的杯子', '', '', '这款抛光玻璃的边缘只有1毫米的薄壁。深杯有宽的盘状盘口，底部略凹，与支架上方的中槽相对应。杯子是钟形的，直径向凹槽方向增加。在下面，空的支撑物向脚倾斜，脚是弯曲的，底部垂直加厚。杯口、杯脚光滑；颈部下方的主体在车床上装饰有非常精细的平行雕刻线条。装饰完成，在凹槽下方，由花瓣和菱形的小穿孔图案组成：这是一个由三个长方形孔隔开的组组成的装饰带。收敛的ghi，其下部略呈菱形。', '', '', '', 'https://www.maotorino.it/media/opere/Cn27.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4804', '国立东方艺术博物馆（都灵）', '带有雕刻装饰的蓝旗亚“毛”尖', '', '', '矛头（毛）典型的战国时期四川制造，具有管状嫁接和柳叶形叶片。短柄呈截锥形，截面为圆形，边缘笔直，内部延伸至矛尖。手柄的上部装有两个截然相反的波纹钩。手柄的边缘饰有雕刻的方形螺旋饰带。在上面，在手柄的两侧，出现了相同的符号组合：在底部，手臂以直角折叠，手伸开，拇指触摸e的茎。心形元素；顶部是一只细长而弯曲的老虎，纵向排列在手柄上，尾巴朝向手和“心脏”，枪口朝相反的方向，下巴张开。在手臂和心形元素上画了一些弧线，而老虎的披风的特点是一排同心的正方形。', '', '', '', 'https://www.maotorino.it/media/opere/Cb46.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4805', '国立东方艺术博物馆（都灵）', '短剑', '', '', '长而窄的剑，带有三角形的刀片，中间有轻微的肋骨，没有刀柄。它有一个中空的喇叭形手柄，通过一个干净的台阶连接到刀片上，末端是一个非常凸起的圆顶手柄。装饰以非常轻的浮雕制成，它影响叶片的上部直到手柄，并占据与中央肋骨对称的长三角形空间。它是根据鱼骨图案排列的抽象饰带，其中断线交替出现，链状的蜗壳和杏仁图案。手柄的装饰不那么复杂：四个由紧密缠绕的螺旋组成的装饰带，由一排排的点构成。', '', '', '', 'https://www.maotorino.it/media/opere/Cb68.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4806', '国立东方艺术博物馆（都灵）', '弯曲的“GE”斧头-匕首刀片', '', '', '这种ge的尖刃又长又窄，相当厚，形状弯曲。它具有轻微的中间肋骨和双线，在底部以两个小的弯曲的侧面突起结束。在这个肩膀之外，武器延伸成一个向下弯曲的方柄，中间有一个圆形通孔。柄的外缘是倒角的，特别是底部，在两侧之间几乎呈现出柔软的连接弯曲。在曲线之外是一个缺口和一个尖端，沿着茎的下部轮廓勾勒出一个台阶。沿着边缘深挖的凹槽突出了这个特殊的轮廓。在其中一个表面上，紧挨着固定孔，有一个小的融合缺陷。', '', '', '', 'https://www.maotorino.it/media/opere/Cb40.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4807', '国立东方艺术博物馆（都灵）', '两个环和球状身体的罐子。', '', '', '球状的奥拉，向上展开，向狭窄的基部逐渐变细，从下摆向外有一个短的颈部。两个孔眼几乎对称地应用于容器最大直径以下。下摆的内部装饰有凹痕，红色条纹和双“花彩”圆弧。主要装饰带的顶部由颈部下方的黑色带界定，底部由耳垂高度的黑色水平线界定。，下面有一个重复的拱门理论。装饰品的主要图案由容器顶部等距的四个红色圆圈组成，完全由黑色筛网填充。两个同心圆和两个与空白交替的黑色圆弧将这些图案中的每一个都展开，直到对称地连接在一起。你和那些相邻的人。', '', '', '', 'https://www.maotorino.it/media/opere/Cn03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4808', '国立东方艺术博物馆（都灵）', '有翼狮子形状的烛台', '', '', '烛台看起来像一只长着翅膀的狮子，蹲在构成物体脚的四条短腿上。身体呈圆柱形，中心狭窄，两端呈圆形。狮子的头巨大地立在一边，而蜡烛支架由动物背部的一个小圆锥形开口组成。细节以浮雕方式呈现，密集雕刻的平行线突出了身体的某些部位。以有翼的狮子为主题，这是一种以大尺寸或小尺寸的陪葬品制成的异形人物，从公元2世纪开始，它就非常受欢迎。C。像这样的烛台在吴国和西晋的墓葬中很常见，但它们似乎在4世纪过时了。', '', '', '', 'https://www.maotorino.it/media/opere/Cv76.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4809', '国立东方艺术博物馆（都灵）', '埃尔莫', '', '', '这款头盔重1.8公斤，呈细长的半球形：前后呈半圆形镂空，它的边缘是圆的和凸起的。顶部是一个半圆形的环；在两侧，靠近边缘，有一个直径约1厘米的圆形孔。在其中通过皮革校正以将其固定在头部。在中国北方发现了许多与所讨论的作品几乎相同的青铜头盔，特别是在北京、内蒙古和辽宁省附近。', '', '', '', 'https://www.maotorino.it/media/opere/Cb59.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4810', '国立东方艺术博物馆（都灵）', '两个安福拉和扁平的球形腹部', '', '', '这个花瓶的墙壁的光泽度和灰黑色的斑驳颜色与制作过程中的打磨和熏制过程有关。离子。它有一个椭圆形的身体，连接到由降低的中间带穿过的直圆形脚：底座是凹形的，并带有方形陶工标记，难以辨认，由两个双垂直条纹构成。花瓶的颈部与底座的宽度大致相同，但高度是底座的两倍：柔软地连接到肩部，它由三个形状的带突出显示。下摆，直向内，向外折叠成一个厚的盘状唇瓣。在与颈部的连接处，它有一个雕刻的细螺纹。茧状的身体由三个平行等距的线束垂直穿过。在两侧，它们不受颈部和花瓶底部的阻碍，它们以同心圆完全围绕腹部。', '', '', '', 'https://www.maotorino.it/media/opere/Cv59.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4811', '国立东方艺术博物馆（都灵）', '带平行六面体末端的斜角刀', '', '', '这把刀有一个细长的刻面手柄，截面呈圆形，略微弯曲。在宽而斜的刀片的背面继续有轻微的弯曲，仍然很锋利：切口向上弯曲到尖端，它位于刀背的末端，而后跟形成一个直角，使刀片与刀柄重新连接。这以一种平行六面体形状的旋钮结束，其长边平行于叶片。它被打孔，以便在五个面中的每一个面上形成一个同心的四边形-第六个是连接到手柄的四边形。在陕西的许多西周遗址中都发现了一些类似类型的刀。与我们的标本相比，主要区别在于手柄头部的细节较少。', '', '', '', 'https://www.maotorino.it/media/opere/Cb49.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4812', '国立东方艺术博物馆（都灵）', '“真木寿”，坟墓的保护生物，在高处', '', '', '贞木寿以僵硬的正面姿势蹲在一个宽阔的不规则的四边形底座上：形状像一块岩石，它是整个小雕像的三分之一高，并被涂成黑色。这种生物有一张肉乎乎的、严重变形的人脸：圆圆的、凸出的眼睛，宽而扁平的鼻子，他的嘴发出咕噜声。在头部的顶端有一个非常高的黑色波浪形圆锥形喇叭。耳朵也不成比例地大：它们呈弓形上升，并向角呈漏斗状。雕刻和涂成黑色的条纹装饰也是从肩膀正面升起的张开的披针形翅膀的特征。角，耳朵和翅膀为生物本身创造了一种“王冠”。', '', '', '', 'https://www.maotorino.it/media/opere/Cc11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4813', '国立东方艺术博物馆（都灵）', '男性裸体雕像', '', '', '这种类型的人体模型的可拆卸木臂固定在与肩膀高度相同的孔中。他们后来穿着布衣，并可能配备了武器和木制盾牌。小雕像描绘了一个赤身裸体的人，姿势僵硬，解剖部位刚刚提到，头部特征很好。这张脸有年轻人的特征，长发向后盘起，固定在脑后的一个小发髻上；头顶上的第二个发髻似乎是由中央丝带固定在一起的。身体苗条，骨骼和肌肉仅建议：胸部和臀部略显突出，脚趾被雕刻，生殖器的浮雕图案。肉色的外套几乎完全覆盖了小雕像。可以注意到头发上的黑色色素痕迹和脸上的其他黑斑，背部，胸部和腿部。', '', '', '', 'https://www.maotorino.it/media/opere/Cc57b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4814', '国立东方艺术博物馆（都灵）', '舞者的形象', '', '', '这位年轻的女士是由五名选手组成的小组中唯一的舞者，她被描绘成站在那里，一边跳着舞步，一边用长袍的长袖在空中画出手臂的缓慢手势。他的身体向右倾斜，膝盖弯曲，右腿似乎即将抬起或放下。这位年轻女子有一张精致的椭圆形脸，脸颊上涂着粉红色的美丽，嘴巴上涂着红色。额头上用红色绘制了间隔蜗壳的花卉图案（花点）。头发是黑色的，中间有分叉，梳成双髻（双髻）。这位舞者穿着一件红色的紧身紧身胸衣，领口很深，腰部有一条高而窄的浅色腰带和一条宽大的两层裙子。扇形鞋头朝上的大鞋袜。', '', '', '', 'https://www.maotorino.it/media/opere/Cc18a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4815', '国立东方艺术博物馆（都灵）', '带有蒜头边缘的瓶子', '', '', '斗-瓶，通常是青铜的，脖子长，肚子大，用来装谷物酒-放在高高的圆锥形的脚上。它在脖子的顶部有一个六边形的浮雕（也称为“蒜头”），就在短的直边下方。通过增加瓶壁厚度和保持内径不变来获得浮雕。在湖北秦墓和湖北、河南的汉初墓中经常发现这种瓶子。在我们的标本中，小直边的存在似乎是汉族早期的特征。', '', '', '', 'https://www.maotorino.it/media/opere/Cb13.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4816', '国立东方艺术博物馆（都灵）', '带有缰绳和丝带图案的马', '', '', '这种类型的马由可移动的骑手骑乘，部分由未保存的易腐物品组成。这匹马的鬃毛很短，枪口很长，是典型的汉马。嘴巴半张，青蛙被切开，大圆眼睛突出。外面完全覆盖着米色的外套，上面冷铺着柔软的颜料。一种橙色，一种棕色，一种黑色和一种白色，可以松散地画出覆层的饰面和色带。这个标本的标志是脖子和胸部的飘带，白色点缀的带子，通常是装饰的朴素。鞍座被一个图案所取代，该图案让人联想到环形折叠的织物带，系好背带，向后靠在臀部。', '', '', '', 'https://www.maotorino.it/media/opere/cc53.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4817', '国立东方艺术博物馆（都灵）', '带着面具的骆驼', '', '', '空心骆驼雕像（Battrian），造型简单，直立在一个薄的矩形底座上。它昂首挺胸，小耳朵，圆圆的眼睛，大鼻孔，嘴巴紧闭；在头部的顶部有一个小突起。头部和颈部的皮毛没有任何特征，尾巴也是臀部上简单的扁平浮雕。货物横向放置在动物的低驼峰上，由两个大袋子组成，上面覆盖着可怕的面具。每个面具都像一个神奇动物的头一样浮雕：一个巨大的被压扁的三叶形鼻子，凹陷的眼睛和突出的瞳孔，尖尖的耳朵和两个扭曲的角。嘴巴和下巴被真正的袋子所取代。', '', '', '', 'https://www.maotorino.it/media/opere/Cc94.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4818', '国立东方艺术博物馆（都灵）', '铠甲勇士', '', '', '穿着盔甲的空心战士，双腿并拢站立。他的体重稍微向左移动，并保持双臂向前抬起，就像拿着丢失的武器一样：左臂比左臂更高，更张开，而后者则处于将长枪放在地上的经典位置。巨大的面部特征，大而突出的眼睛，宽阔的嘴巴和迷彩的鼻子，指的是非中国人的身体类型。他所穿的盔甲从头到脚都是沉重而完整的，由不同大小的板子通过螺柱和搭扣固定在一起。头盔是一种紧贴耳朵的头盔，带有纽扣顶部和铆接的后鳍。小雕像上到处都可以看到冷颜料的残留物；头盔上可以看到镀金的残留物。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_92.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4819', '国立东方艺术博物馆（都灵）', '带有三个装饰寄存器的球形罐子', '', '', '这个卵形的罐子，上半部分非常圆，逐渐缩小到底部略凹，没有脚。两个垂直的，截然相反的环，它们将肩膀连接到腹部的上部：它们的上部呈角状面具形状，它们由垂直条纹穿过，并在下部附件周围有一个凸起的环。在上关节上方应用了双“S”形螺旋。罐子的上部由三条凸起线水平穿过。乐队划定了三个装饰寄存器：在上面的两个中，刻有强烈风格化和装饰的鸟类的连续混合线条图案。你点点滴滴。', '', '', '', 'https://www.maotorino.it/media/opere/Cv68.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4820', '国立东方艺术博物馆（都灵）', '带有镶嵌装饰的“鼎”三脚架花瓶', '', '', '这种用于烹饪和祭祀肉类的祭祀容器具有秦汉时期的典型形状：co压扁的球状rpo，底部呈圆形，腹部横肋，蹄足，两个向外略微弯曲的垂直矩形手柄，在盖子上，三个半圆形手柄被一个圆形按钮覆盖。它的大部分表面都有精致的金银锦缎装饰，上面有风格化的图案和山，云，花鸟。最后，脚与容器的大连接呈现出螺旋图案中间的三角形理论。这种容器起源于陕西，在公元前3世纪末和2世纪大规模传播。，从甘肃向西北，到粤桂向东南。相反，黄金和白银的装饰要罕见得多。', '', '', '', 'https://www.maotorino.it/media/opere/cb15.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4821', '国立东方艺术博物馆（都灵）', '香水燃烧器', '', '', '该物体由一个大的浅板组成，在它的中心矗立着一个复合香炉，上面有动物形状的圆形图案：龟形的底座，像鸟一样的茎，大杯和可拆卸的龙盖。乌龟用尖尖的鼻子抬起头。胸甲板是用雕刻的线条制成的。这只鸟用三指的腿蹲在甲壳上，将长长的脖子和三根羽毛的高尾巴向上抬起，直到碰到为止。它的喙上有一个球体，上面放着香水杯。这个开口很大，然后稍微向上关闭。盖子紧贴在杯子凸起的边缘上。上面呈现出一条有翼的龙的细长身体的形状，它的四肢缠绕在一起，形成一个穿孔的圆锥，其顶部是圆形的。公元前1世纪，带有这种支架的香炉和灯在中国北方相当常见。', '', '', '', 'https://www.maotorino.it/media/opere/Cb29.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4822', '国立东方艺术博物馆（都灵）', '披针叶背景上的佛像和两个菩萨', '', '', '雕塑描绘了一尊高浮雕的佛陀，两侧是两个菩萨，背景是披针形的叶片。融合通常不准确，并且图形是粗略的。佛陀僵硬地站着，右手在abhayamudra，左手在varadamudra，穿着厚重的长袍，头上戴着笨重的乌什尼莎（rouji）。围绕佛头的光环以简单的线条为特征，勾勒出一朵六瓣花的轮廓。佛陀赤脚将大脚放在位于实际底座上方的半球形基座上。在此基础上的顶部，有两个侧枝，两个菩萨站在上面，双手合十，尖尖的光环和与大叶子相连的肩膀之一。', '', '', '', 'https://www.maotorino.it/media/opere/Cs04.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4823', '国立东方艺术博物馆（都灵）', '带装饰带的“HU”花瓶', '', '', '这个带有扩张肩膀的球形胡向脚的下方闭合，略微凹入，进一步向底部收缩。弯曲的颈部向开口方向延伸，呈双重形状，下摆略微向内收。花瓶大多是光滑的，转弯的线条清晰可见。通过模具获得的单个浮雕带，它围绕着腹部的上部，并以突出的鱼片为界：描绘了在岩石景观中奔跑的野生动物。我知道和两个对称的普寿动物形态面具。动物包括老虎、熊、鸟类、鹿和野兔。花瓶完全被绿色，深色但明亮的玻璃覆盖。', '', '', '', 'https://www.maotorino.it/media/opere/Cv65.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4824', '国立东方艺术博物馆（都灵）', '带包的宫廷女人', '', '', '身材苗条的宫女或宫女的站立雕像，右手放在肚子上，左手折叠在胸前，支撑着一个罐子：从前臂向左侧倒一个mor。Bida Sacca。她的发型很高，发髻在头上形成两个大翅膀（双扇髻）。她穿着一件窄袖长裙，深领口，高腰，臀部系着一条长缎带，前面有一个大结。腰带被涂成橙色，而黑色的垂直线突出了裙子的平行褶皱。在束腰外衣上，他穿着带有红色残留物的半袖紧身胸衣（banbi）。在发型上也可以看到冷的黑色颜料的痕迹。', '', '', '', 'https://www.maotorino.it/media/opere/Cc83.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4825', '国立东方艺术博物馆（都灵）', 'Nimbato菩萨，站在莲花上', '', '', '菩萨被描绘成一个优雅的三曲姿势（tribhanga），重量转移到左腿上。右臂向外折叠，手掌向上张开（也许是为了支撑一颗珍珠）。左侧也是分开的，但沿着侧面下降以适应身体的曲线。手拿着一个烧瓶。飘动的衣服有助于赋予小雕像活力，它们的两端朝外，并产生镂空的效果。花费自己的身体。菩萨的胸部被一条大项链围绕着，而他圆圆的肚子则被塑造成印度回忆的轻微肿胀。赤脚搁在一个小的半球形底座的平面上，上面有轻微浮雕的莲花花瓣。菩萨的头上戴着一顶戴在耳朵上的沉重的王冠和一个高高的发髻，它被后面的大光环所震撼。', '', '', '', 'https://www.maotorino.it/media/opere/Cs01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4826', '国立东方艺术博物馆（都灵）', '双环直颈罐子', '', '', '这个花瓶看起来像一个扁平的球形锅，顶部非常膨胀，向狭窄的底部逐渐变细，有一个高的圆锥形颈部。在容器的最大直径以下对称地施加两个孔眼。相应地，在下摆下方的脖子上，有两个小鳍，其功能被忽略了。半山陶典型的装饰带的主要图案是所谓的“烧瓶”。它由四个等距重复的图案组成，用一条连续的红线绘制，几乎在顶部相互连接。这些图案的形状让人联想到一个没有脚的球形花瓶的轮廓，它的边缘非常向外，内部以黑色对角线密集排列。黑色的画也是分隔“烧瓶”的空间，对称的杏仁形储蓄和底部的三角形。', '', '', '', 'https://www.maotorino.it/media/opere/Cn02.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4827', '国立东方艺术博物馆（都灵）', '带有动物形态装饰的球形罐子', '', '', '罐子有一个略呈扁平的球形主体，低颈有强烈的外倾边缘，底部略凹，没有脚。外壁和内壁的极端不规则表示加工类型：在后者上仍然可以看到花瓶的指纹。我把它完全塑造成了圆形。这个花瓶的独创性在于它的肩部有规律地排列着动物形态的装饰：八种动物——鸟类和蜥蜴。交替-浮雕建模并垂直应用。容器的整个表面，包括应用的小雕像，随后被印上了一个精细的方形图案，类似于一块布。', '', '', '', 'https://www.maotorino.it/media/opere/Cv52.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4828', '国立东方艺术博物馆（都灵）', '一对带有圆形尖端和野猪形末端的刀', '', '', '刀的两面几乎相同，刀刃是弯曲的，作为手柄的延伸，也是弯曲的。薄刀片长而窄，螺纹在凸侧，尖端呈圆形。柄盘纵向装饰有四个平行的细绳图案：两个沿轮廓排列，而两个中央部分被一条薄薄的凸起的肋骨隔开。手柄的末端是从侧面看到的一头站立的野猪的扁平雕像，头部朝向刀的凹面。动物的皮毛由密集的平行，示意性和精细的特征表示。两腿之间的空间有一个不规则形状的通孔，可以将刀挂在支架或鞋带上。这种类型的刀很可能有其他战争目的。', '', '', '', 'https://www.maotorino.it/media/opere/Cb48a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4829', '国立东方艺术博物馆（都灵）', '两个环的罐子和大的展开的脖子', '', '', '锅的轮廓略有棱角，形状有点扁平，向上膨胀，向狭窄的底部逐渐变细。它有一个大而喇叭形的脖子，下摆向外。花瓶的多孔和不透明的表面使装饰在某些地方难以辨认：底部由覆盖所有东西的红色凹槽组成。容器的上半部分，包括颈部和下摆的内部。腹部的主要装饰带被刻在两个黑色带中。在它们的内部，有一条由厚实的水平菱形组成的饰带，这些菱形为顶点连接在一起：每个菱形都是四边形的，而这样得到的小菱形则涂上了格子图案。它关闭了装饰，在腹部连接线的底部，有一条黑色的带子，有时会在完整的“花彩”拱门中变厚。', '', '', '', 'https://www.maotorino.it/media/opere/Cn37.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4830', '国立东方艺术博物馆（都灵）', '两个直颈的小花瓶', '', '', '这个容器做工粗糙。它是一个向底部收缩的大肚罐，并配有一个宽的圆锥形颈部。下摆的内部显示一条红色的带子和一个涂成黑色的同心双拱门图案（“花彩”）。在外面，在边缘下方有一排水平的三角形。向下的颈部是经典的半山凹痕，然后在与肩膀的交界处有一条红带。在容器的主体上，装饰被分成垂直的正方形，在花瓶表面的中心和耳孔下方用红色条纹划定，周围是黑色的锯齿状条纹。这样形成的四个正方形是用格子装饰的。在底部，朝向容器的底部，装饰由一条黑色带子和简单的重复拱门水平封闭，“花彩”。', '', '', '', 'https://www.maotorino.it/media/opere/Cn30.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4831', '国立东方艺术博物馆（都灵）', '宽肩储物罐', '', '', '这个罐子的表面不规则，有深棕色的面团，几乎是紫色的，在某些地方变黑：它是在圆形工具的帮助下手工制作的，在内壁和外壁的某些地方可以看到其痕迹。它的形状又高又瘦：肩膀上又宽又大，它逐渐缩小到一个长的圆锥形腹部的狭窄底部。颈部短而略呈喇叭形，下摆向外。花瓶的表面完全覆盖着一层非常薄的印花装饰，似乎想要再现一种精细的植物纤维编织。这样的。它由顶点由对角线连接的正方形组成，其布局仍然突出。装修漏洞很多。', '', '', '', 'https://www.maotorino.it/media/opere/Cv54.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4832', '国立东方艺术博物馆（都灵）', '一对动物形态面具形状的安塞', '', '', '在八角形的半环形环的上半部分，嫁接了一个带有突出下巴的宽口面罩，圆眼和复合角。表面以风格化的方式建模和雕刻，由肺泡，蜗壳，平坦的表面和浮雕的小环丰富。两条蛇的头出现在角的底部，它们的身体在它们下面螺旋扭曲。在手柄的右侧钻了两个孔，一端一个，用于通过销钉将其固定在花瓶主体上。两个环所属的容器的一些碎片与熔核的残骸一起保存了下来。模具由三个主要部分组成：一个用于掩模，两个对称，对于手柄-纵向接头在背面清晰可见。', '', '', '', 'https://www.maotorino.it/media/opere/Cb23b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4833', '国立东方艺术博物馆（都灵）', '配置的鼓架', '', '', '鼓架由十个实木节组成，成对对称：两个卧猫形支架；四条细长的涉水腿；鸟的两个身体和两个长脖子。腿被插入猫科动物背部的特殊外壳中，一个接一个地放置，并插入鸟类的身体中。这些也是向后转的，尾巴通过榫眼连接，并用榫头支撑动物的长脖子。琴身和琴颈构成了鼓架的框架：乐器通过三个环悬挂，这些环用绳子绑在琴脊和琴尖上。鸟类尾巴的交界处。表面上仍然可以找到的油漆痕迹表明，最初鼓架完全用不透明的油漆装饰。', '', '', '', 'https://www.maotorino.it/media/opere/Cl07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4834', '国立东方艺术博物馆（都灵）', '阿拉巴尔达护套', '', '', '长的鞘具管状的杏仁状的节段嫁接。物体的柱状形状，不考虑丰富它的众多突起，从开口到底部是直的，在主轴两侧的两个松果体突起中突然变宽。整个物体装饰有由调制浮雕平面组成的抽象图案，它们使表面恢复活力，并在前后或多或少地延伸成尖锐的附属物。几个平行的凸起带装饰在移植物附近的物体上，然后向上弯曲，形成最后一个扁平的前突起。在这个突起上，有一只奇怪的蜷缩着的杂交动物的腿，这是整个鞘中唯一的象征性元素，描绘在两边的马背上。头部轮廓突出，是一个圆形的元素：它具有兔子耳朵的啮齿动物的特征，但它也有很长的角和扭曲的角，在背后弯曲成弧形。', '', '', '', 'https://www.maotorino.it/media/opere/Cb73.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4835', '国立东方艺术博物馆（都灵）', '躺着的猪雕像', '', '', '小动物雕像在7世纪和8世纪初的墓葬中非常常见。这两只小猪，里面是空心的，长的鼻子向下倾斜，脖子上有直的鬃毛，耳朵很大。除了后者，全身都涂成了黑色。', '', '', '', 'https://www.maotorino.it/media/opere/Cc36b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4836', '国立东方艺术博物馆（都灵）', '大开口球形罐子', '', '', '这个球形容器的形状和装饰旨在模仿柳条或竹子编织的篮子。大开口的边缘是凹陷的，并略微突出，肩膀急剧膨胀，腹部逐渐向平坦的底部收缩，大约与罐子的开口一样宽。墙壁向底部变厚，向上向边缘变薄。印有浮雕图案的装饰几乎覆盖了整个外表面：它再现了八个条纹的对角线编织。重叠的Getali。面团均匀地覆盖着半透明的釉料，它也在内部铸造，在那里形成了浅棕色和橄榄绿色的斑块。', '', '', '', 'https://www.maotorino.it/media/opere/Cv56.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4837', '国立东方艺术博物馆（都灵）', '带有拟人头部的两环花瓶', '', '', '扁平底的球形小花瓶，高颈和直边，两个截然相反的宽带状环，将边缘连接到容器的肩部。烹饪前，在粘土上刻有一条线，突出了脖子和肩膀之间的接缝。容器的外表面被涂黑，完全覆盖着垂直和倾斜的绳纹。浮雕装饰仅限于一条沿边缘带有垂直缺口的粘土条，以及一个应用于u中心的拟人化头部。在两个面中，颈部与花瓶的主体相连。', '', '', '', 'https://www.maotorino.it/media/opere/Cn08.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4838', '国立东方艺术博物馆（都灵）', '“Fanghu”高脚四边花瓶', '', '', '这个罐子用来装谷物酒和谷物本身（还有另一个类似的罐子，与之形成一对，Cb/17b）。它由轻而相当薄的青铜制成，脚是空心的：它与第二个不同之处在于盖子的位置。它具有经典的方胡形状，带有截锥形的盖子，平顶上有一个垂直的钩子。它涂有一层厚厚的颜料，直接涂在青铜上：红色，黑色，绿色，“汉蓝”（由铜和钡的双硅酸盐组成的颜色）与原始色调相比，绿色和黄色可能发生了变化。盖子上有一座程式化的山；颈部上部的下摆和脚部饰有云纹。颈部每侧由三个大三角形突出，尖端向下。壶腹上的四个面各有一个蜿蜒的图案：两侧有两条龙，分别被普寿的面具占据，与复杂的蜗壳设计交替出现。', '', '', '', 'https://www.maotorino.it/media/opere/Cb17a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4839', '国立东方艺术博物馆（都灵）', '帕德马萨纳的观音与阿尔塔提亚拉阿西索', '', '', '菩萨佩戴的高头饰中央的小阿弥陀佛形象，将其认定为观音（观世音）。它盘腿坐在一个模制的木制底座上，不是原创的。它握在左手的手掌中，折叠在腹部前面，一个小的杯状的杯子：右手举到胸前形成争论的手势（vitarkamudra）拇指和中指并拢。菩萨的方头上戴着一个用植物蜗壳装饰的穿孔头饰，它向下延伸到耳朵，并在背面刻上一条带子。在它的中心矗立着另一个带有三个球形突起的扁平装饰品，耳朵上方的两侧挂着一条长丝带，丝带的两端呈叶状，一直垂到头饰上。长发在头顶上梳成高高的发髻；在颈背上方高高地编织着，它们以两个非常长的锁继续，并对称地落在肩膀上。', '', '', '', 'https://www.maotorino.it/media/opere/Cs19.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4840', '国立东方艺术博物馆（都灵）', '琵琶演奏者的形象', '', '', '这位年轻的女演员，是一个由另外四名女演员和一名舞者组成的团体的一员，它被描绘成盘腿坐在一个薄而方的底座上，演奏一把扁平而薄的琵琶。左手拿着键盘，右手拿着长拨子，前臂靠在乐器上。琵琶在细节上得到了很好的描绘：带有三尖拨片的肘柄，浮雕桥，其他雕刻和涂成黑色的细节，例如细长的“ЭЭ”形状的共振孔，琴键和琴弦。', '', '', '', 'https://www.maotorino.it/media/opere/Cc18f.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4841', '国立东方艺术博物馆（都灵）', '马舍罗内', '', '', '这个中空的动物形态面具很可能在葬礼上起到了支撑的作用，面具的枪口特征突出，小而直的耳朵贴在头顶上。它有一双大大的方形眼睛，浓密的拱形眉毛，宽而扁平的鼻子，巨大的畸形嘴，可见上牙弓和下犬齿。怪物的圆形鼻子，从一个太阳穴到另一个太阳穴，由胡须的程式化卷曲构成。它完全被涂成对称的斑纹，涂在白色的凹槽上，带有柔和的灰色、米色和黑色线条。物体的反向性功能是由所描绘的生物的悲伤和善良之间的某种表达来调解的。', '', '', '', 'https://www.maotorino.it/media/opere/Cc39.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4842', '国立东方艺术博物馆（都灵）', '带脚的法诺内“GE”斧头匕首刀片', '', '', '这把匕首（ge）它是战国时期青铜武器生产的一个很好的例子——标准化但精确：短尖刀片三角形，宽扇形，鞋跟由凸起的肋骨突出，并向下延伸，长矩形柄。肋骨被卡在一根木杆中，木杆从一侧穿过另一侧。为了确保有效的固定，一些鞋带穿过沿鞋跟分布的三个长方形孔，以及位于鞋跟中心的长而窄的孔。杆子被插入两个通孔的横向脚踝卡在脚上。带有滴状部分的移植物在中间用凸起的带子装饰，在底部，除了孔之外，从一个更突出的不对称装饰：一个倾斜的带，从它突出，截然相反，两个弯曲而尖锐的元素，一个尖端向上，另一个向下。除了这种装饰外，移植物还逐渐闭合在六边形截面的附属物中。', '', '', '', 'https://www.maotorino.it/media/opere/Cb44b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4843', '国立东方艺术博物馆（都灵）', '双环高颈花瓶', '', '', '这个花瓶是新店陶瓷的特色。脱脂良好的面团含有云母和白色颗粒；凹槽隐藏了垂直的布印，几乎遍布容器的整个外表面。它有一个又高又宽的弯曲的脖子，下摆扩大，嫁接在一个稍微凸起的肩膀上，达到最大直径，它逐渐向容器的底部闭合。这是凹凸不平和不平衡的，这是一个典型的特征，再加上容器底部的烟灰痕迹，新店生产。涂成黑色的装饰不是很准确。', '', '', '', 'https://www.maotorino.it/media/opere/Cn20.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4844', '国立东方艺术博物馆（都灵）', '剑刃', '', '', '细长的柳叶状叶片，中间加厚，两面有斜线。刀片经过表面处理，使其在银色背景上具有黑色斑点外观。所使用的技术是用细的圆形截面冲头锤打青铜。由于这种权宜之计，金属的铜色在表面下重新出现，事先经过处理以获得黑色。除了这种抽象的装饰外，在阀杆附近还有一些在铸造过程中用电缆绘制的图纸。一张脸上有一只被老虎追赶的鹿。另一方面，在刀片的另一侧有一只右手，其手臂弯曲成直角。与手平行的是一个看起来像钥匙的物体（可能是武器）。还有一把带环的刀。在这两个图案的前面出现了一个字符。自然主义的图案和文字符号都属于巴蜀文化，目前还无法破译。', '', '', '', 'https://www.maotorino.it/media/opere/Cb58.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4845', '国立东方艺术博物馆（都灵）', '与动物一起骑马', '', '', '这个小雕像描绘了一个骑马的女人和一只动物，它的项圈蹲在马背上的枕头上。女人把躯干和头向左转，他的手臂弯曲到不同的高度，握紧拳头，就像握着缰绳和/或武器一样。他的脸是白色的，嘴巴是红色的，眼睛是黑色的，头发在耳朵上方扎成两个发髻。她在膝盖下穿着一件浅绿色的长上衣，领口为“V”形，袖口为红色镶边。他穿着灰色的裤子和黑色的鞋子。一种有斑点的皮毛，在马背上的后腿上的动物身上有痕迹，假设它是一只猫科动物，可能是猞猁，用于狩猎。', '', '', '', 'https://www.maotorino.it/media/opere/Cc19.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4846', '国立东方艺术博物馆（都灵）', '骆驼和骆驼司机', '', '', '这座雕像的特点是大骆驼之间的比例对比，用逼真的笔触描绘，还有骑在驼峰之间的又小又长的骆驼司机。骆驼似乎突然被骆驼司机拉紧了缰绳而卡住了。动物的皮毛上覆盖着琥珀色的玻璃。驼峰从躺在动物背上的椭圆形瓜尔德拉帕中出来：织物在琥珀色的背景上涂有绿色和奶油色的斑点。骆驼司机双臂交叉，双手握拳。它的头部没有釉面，最初涂有冷色。他戴着一顶尖尖的圆锥形高帽，正面有一个原本涂成棕色的浮雕拱门。身体特征，其中突出的仍然是红色的嘴，是外国人典型的漫画特征，就像尖尖的鼻子和圆圆的眼睛。从脖子往下，驼夫的衣服都是釉面的。', '', '', '', 'https://www.maotorino.it/media/opere/Cc35.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4847', '国立东方艺术博物馆（都灵）', '女性裸体雕像', '', '', '这种类型的人体模型的可拆卸木臂固定在与肩膀高度相同的孔中。他们后来穿着布衣，可能还配有木制配件。小雕像描绘了一个裸体女人，姿势僵硬，解剖部位刚刚提到，头部特征很好。这张脸有年轻女子的特征，头发向后梳，在肩膀上打了一个柔软的结。身体很瘦，只有骨骼和肌肉：乳房像胸部下方的小凸起，臀部略显突出，脚趾有切口，生殖器有裂缝。肉色的外套几乎完全覆盖了小雕像。黑色颜料是头发，眉毛和眼睛的特征，而嘴唇被涂成红色。', '', '', '', 'https://www.maotorino.it/media/opere/Cc59.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4848', '国立东方艺术博物馆（都灵）', '带有冠状鬃毛的马的葬礼雕像', '', '', '马的四条腿处于静止状态：与强大而笨重的身体相比，头，颈和腿是平坦的。四肢和头部分别融合，然后嫁接到躯干上。细长的头部经过精心加工，并以与各个元素的强调一致的方式进行风格化：张开的嘴显示牙齿，牙龈和舌头；甚至钳口也有一个拱形突起。短鬃毛沿着长颈的弯曲垂直上升，就像一个尖顶。头部造型的精细与马的整体造型形成鲜明对比，相当粗糙：腿细，蹄高，腹部有暗示性的身体，表面光滑而粗糙。雕像的总体印象是抽象的化合物。动物的尾巴不见了，被嫁接到臀部的底部，现在只剩下一个非常小的圆形突起。', '', '', '', 'https://www.maotorino.it/media/opere/Cb31.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4849', '国立东方艺术博物馆（都灵）', '长腿的马和骑手', '', '', '这匹马摆着姿势，腿和脖子都很高很细，鬃毛剪得很短，尾巴打结成卷发，Montonino枪口的轮廓。没有痕迹，只是在枪口和下巴下方有红色的痕迹。嘴巴、鼻孔和耳朵也是红色的。背面有一个橙色的椭圆形鞍座。骑士坐在上面，向前看。他的双臂折叠到肘部的高度，双手垂直打孔：也许在孔中通过了deper材料制成的缰绳。或丢失的武器。战士戴着一顶镶有红色边框的黑色鸭舌帽。面部特征和长袍的颜色严重褪色，只剩下黑色和红色点缀的白色，边缘为黑色和橙色，与鞍座的颜色相混淆。角色穿着短上衣，脖子和袖子上有厚厚的翻领，裤子和鞋子。', '', '', '', 'https://www.maotorino.it/media/opere/Cc51.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4850', '国立东方艺术博物馆（都灵）', '密封的骏马', '', '', '由于后腿弯曲，左前伸展，右前抬起，这匹马呈现出突出的活力。头部向左转，并向强大的颈部强烈折叠。张开嘴，扩张的青蛙和在皮肤下形成的枪口肌肉很好地帮助唤起动物的头皮和神经亚硝化反应。鬃毛在右侧是扁平的浮雕，鬃毛没有定型。额头上的两簇紧贴耳朵，而尾巴则更简单地打结。弓形是由一个向前延伸到肩膀的厚厚的椭圆形臀部组成的。在瓜尔德拉帕上有一个鞍座，上面覆盖着一块绑着的布，在大腿上方有很多褶皱。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_91.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4851', '国立东方艺术博物馆（都灵）', '戴着帽子和帽子的马和骑士', '', '', '这匹马摆着姿势，腿很短，胸部很圆，鬃毛很短，尾巴向下打结。斗篷是红棕色的，脖子上有一个大的红色斑点（左），床头板上涂有相同颜色的细纹。类似的线条，用白色绘制，让人联想到胸带、腹带和臀带。背面有一个鞍座，前面尖尖，后面有裂片。骑士坐在上面，向前看。他的两只手臂都折叠到肘部的高度，拳头的手水平打孔：可能是马特尔的缰绳穿过了孔。易腐的。战士戴着一顶棕色的军帽，配有腰带。在束腰外衣的上方，他穿着黑色的盔甲，上面有白色的金属板设计。', '', '', '', 'https://www.maotorino.it/media/opere/Cc50.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4852', '国立东方艺术博物馆（都灵）', '叉尾马的葬礼雕像', '', '', '马靠在四条腿上：它粗壮，头部较小，胸部突出，四肢形状良好。波浪状的尾巴，在末端分叉，垂直地嫁接在动物的臀部上。头部有尖尖的小耳朵，凸起的圆眼睛和张开的嘴巴。鬃毛在脖子上有波浪形的切口，耳朵之间有一个三角形的凸起。身体是中空的，腹部下方有一个长方形的洞。胸部、腹部和腿后有融合间隙。', '', '', '', 'https://www.maotorino.it/media/opere/Cb30.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4853', '国立东方艺术博物馆（都灵）', '彩绘青铜“方壶”四边花瓶', '', '', '这个花瓶（以及与之形成一对的另一个类似的花瓶，Cb/17a）是用轻而薄的青铜制成的。它与前者的区别在于盖子的位置，盖子的钩子垂直于花瓶两侧的两个动物形态的普寿面具。它具有经典的方胡形状（“方胡”，用于容纳谷物酒和谷物本身）带有截断的金字塔形盖子，平顶上有一个垂直的钩子。它涂有一层厚厚的颜料，直接涂在青铜上：红色，黑色，绿色，“汉蓝”和黄绿色可能与原始色调相反。盖子上有一座程式化的山；颈部上部的下摆和脚部饰有云纹。颈部每侧由三个大三角形突出，尖端向下。一个独特的蜿蜒图案占据了花瓶腹部的四个面：一只张开翅膀的大鸟和一条龙（？）在浦首占据的两侧有翼，与另外两条龙交替。', '', '', '', 'https://www.maotorino.it/media/opere/Cb17b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4854', '国立东方艺术博物馆（都灵）', '大椭圆形杯子', '', '', '形状罕见的杯子，恢复了“有翼杯子”（yushang）的形状，但手柄的形状有所不同，尺寸大于平均尺寸。此外，平底靠在四个两两面对的半圆形脚上。木芯相当厚，是横向切割的。与豫商一样，这些环从杯子边缘的高度开始，并以长边为中心。但是，它们没有典型的“机翼”形状：在一个长而窄的矩形区域的中心，相对于杯壁升高，一个厚实的梯形突出体伸出，顶部是主要的底座。杯子完全覆盖着黑漆，里面覆盖着一层朱砂红漆。物体的几何装饰至少与其形状一样独特，并且是由红色和黄色颜料交替进行的。', '', '', '', 'https://www.maotorino.it/media/opere/Cl4.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4855', '国立东方艺术博物馆（都灵）', '穿着花朵装饰束腰外衣的宫廷女士', '', '', '这位女士站着，身体微微向左不平衡，优雅的弓形。他的双手合十放在胸前，被宽大的长袖的窗帘遮住了。他身材丰满，特点是圆脸，下巴丰满。面部特征被准确地呈现出来。眉毛和发际线上的头发是用非常细的刷子一个接一个地画的。面部的一般表情是适度的紧缩，适合不再年轻的贵妇。重要的发型被称为“锤子”（chuiji）它由耳朵上方的两个平簇和前面的两部分组成。女人穿着一件长的圆领长袍，两侧敞开，覆盖着一条裙子（或裤子）并通过一条厚皮带将其固定在臀部。连衣裙的白布上装饰着间隔开的大花。', '', '', '', 'https://www.maotorino.it/media/opere/Cc17.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4856', '国立东方艺术博物馆（都灵）', '翼展形状的支架', '', '', '该支持包括一个大型的全方位博览会，在其背面有一个长的管状移植物，顶部有一个装饰性的项圈。这座雕像的灵感来自于一种蓬勃的活力，该生物蹲在强烈的扭动中，长长的脖子与支撑柱平行地拱起。它的头向右，右前腿抬起并爬行，左侧靠着；右后腿向前推，靠在岩石凸起上，相反，左侧向后踩在基座的后边界上。博览会的枪口具有介于老虎和龙之间的特征。特征是浓密的波浪形胡须，画有眉毛的大眼睛，头上有两对纵向的羊角面包。在生物右侧突出的台阶上方，有一只蟾蜍的小雕像，它拿着一个贝壳形状的杯子。利亚或某种聚宝盆。', '', '', '', 'https://www.maotorino.it/media/opere/Cc46.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4857', '国立东方艺术博物馆（都灵）', '骑马的女性角色', '', '', '骑马的苗条女性角色向后弯曲。她有一头浓密的头发，梳成一个柔软的发髻。她穿着一件长长的圆领束腰外衣，侧面开叉，腰部系着腰带。右臂沿臀部下垂，束腰外衣的长而窄的下垂袖遮住了手。相反，左臂折叠在腹部。那个女的好像穿了一双靴子。小马鞍靠在马背上的四边形凸起的鞍座上。这个，又小又矮，被描绘成正面静止的姿势。鬃毛的发型是典型的唐式发型：辫子和短扎的尾巴，朝上；鬃毛梳理在脖子的左侧，并在耳朵上方分成两簇。小雕像保留了原始色素沉着的痕迹。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_82.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4858', '国立东方艺术博物馆（都灵）', '披针叶背景上的佛教团体', '', '', '全金属雕塑，以披针形叶片为背景，描绘了三尊浮雕佛像，设置在四个支撑的底座上。两个主要的佛陀占据了较低的位置：以简单和示意性的方式描绘了禅定中的阿西西。在他们的身后，刻有身体的光环和光晕的轮廓。第三个更小的佛放在杏仁的顶部：它的特征更是示意图，身体呈水滴状，只有头顶后的光环。叶子的轮廓略有凸起，人物之间的所有背景都刻有精美的波浪形图案，根据这一时期常见的佛教肖像画，这些图案代表了还有火焰。根据5至6世纪广泛流传的肖像画，两个并排的佛陀代表释迦牟尼和帕布达拉特，现在和过去的佛陀。', '', '', '', 'https://www.maotorino.it/media/opere/Cs02.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4859', '国立东方艺术博物馆（都灵）', '站着的人', '', '', '该人物站立姿势僵硬，手臂垂直于身体，但以提供姿势稍微向前突出。手和头是可拆卸的，通过别针与身体相连。头部造型甜美，是一个留着细胡子的年轻人的头部，梳着中央的分叉和扁平的发髻。该男子在右侧穿着各种重叠和交叉的衣服，形成了一系列非常厚的“V”形领口。白色的外衣，长到脚，使雕像看起来苗条，并展开成花冠形成底座。它的腰部系着一条红色的腰带，它的长瓣落在前面，几乎到了脚，向底部呈刀片状延伸。', '', '', '', 'https://www.maotorino.it/media/opere/Cc67.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4860', '国立东方艺术博物馆（都灵）', '球状储存罐', '', '', '不均匀厚度的罐子，深灰色的面团净化不良。它呈球状，在肩部展开，向宽阔的扁平基部逐渐变细。在车床上完成的颈部非常低，并带有双下摆。颈部下方的第一条装饰带，只有几厘米宽，有平行的水平条纹。肩部的剩余表面和腹部的上部有方形装饰，就像一块又厚又粗糙的布的印记。相反，腹部的其余部分则印有约4厘米的菱形图案，由水平和垂直交替的条纹组成。这些印象是重叠的，因此装饰整体不是很规则。', '', '', '', 'https://www.maotorino.it/media/opere/Cv53.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4861', '国立东方艺术博物馆（都灵）', 'BRICCO用于加热洗澡水', '', '', '功能可疑的青铜礼器：根据目前的解释使用，用于制备以谷物为基础的酒精汤剂或加水稀释葡萄酒，它们在葬礼环境中与盛水的容器的结合似乎表明它们可以用来加热洗澡水。他的这种变体有一个高度扁平的卵形身体，短的喇叭形脖子和突出的下摆。它有一个圆形的盖子，上面的孔眼通过两个交叉的环固定在环的中心。手柄上装饰有蜗壳图案；爬行动物头部的两端与固定在花瓶肩部的环相连。在两个铰链之一的下方焊接了带有钩嘴的狮鹫形状的喷嘴。容器的盖子和脚上方的上半部分用颗粒状底部上刻有几何图案的精美装饰带装饰。或者。', '', '', '', 'https://www.maotorino.it/media/opere/Cb09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4862', '国立东方艺术博物馆（都灵）', '汽车轴箱', '', '', '这些中空的物体被用来锁住车轮的轮毂，并保护货车车轴的末端。矩形扳手，穿过底座上方的两个开口，它用于将表壳固定在轴上，并由两个插入钥匙横孔的销钉锁定。在我们的样本中，它仍然是可提取的，它有一个模制的羊头状末端，非常适合板条箱的形状外壳。我们的标本遵循经典的截锥形形状，底部有宽的圆形边缘，但是物体的主体是八角形的，只有在顶点变窄时才会变成圆形。银线和银箔的装饰由三个水平带组成，分别对应于底座和顶部（圆形）。表壳和钥匙插孔上方的中间部分（八角形）。', '', '', '', 'https://www.maotorino.it/media/opere/Cb28.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4863', '国立东方艺术博物馆（都灵）', '带有弯曲脚的灯座杯（？）', '', '', '杯子很浅，边缘宽而反，底部有轻微的凸起。脚从这里开始，非常喇叭形和中空，底部扩大且略呈圆形。杯子的内部和底部没有上釉，而杯子的外部陈列柜已经铺满了污渍，让它流出来。三种明显的颜色是琥珀色，乳白色和蓝色。杯子可能是用来装光源的，形状来自银色的原型。', '', '', '', 'https://www.maotorino.it/media/opere/Cv11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4864', '国立东方艺术博物馆（都灵）', '弓弩机构', '', '', '这件沉重的作品具有中国弓弩装置的经典形状，并揭示了与汉代武器相关的力学的准确性。该机构插入一个盒子中，包括三个通过两个横向穿过它的脚踝组装而成的部件。在两个脚踝方头之间的盒子侧面，刻有一块融合后的文字。它只能部分阅读，但第一个字符提供了一个日期：“永元五年”，对应于公元93年。盒子的顶部，在取景器的高度，还刻有三个字：“敦煌保护区”。公元前4世纪中国发明了弩，也就是比欧洲早了一千多年。它由横向安装在杆（绞线）上的弓组成，该机构插入杆本身的底部。', '', '', '', 'https://www.maotorino.it/media/opere/Cb71.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4865', '国立东方艺术博物馆（都灵）', '拍卖护套', '', '', '带有杏仁部分的护套可能覆盖了戟的顶部。顶部有一个由三只动物组成的装饰：在一个有角的杂交动物的头上栖息着一只鸟，它的头面向印度。埃特罗。在后面，紧贴着护套的边缘，一只侧面的老虎，头也朝后。动物身上刻有蜗形图案，银质镶嵌。管状嫁接的装饰，全银色，描绘了五个按照几何风格重叠的龙头（？）。在护套的下部有一个凸缘，这可能有助于杆的接合。另外两条轻微的雕刻线条将装饰在此法兰和护套顶部之间一分为二。', '', '', '', 'https://www.maotorino.it/media/opere/Cb20.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4866', '国立东方艺术博物馆（都灵）', '带圆顶脚的灯座（？）', '', '', '杯子是开放的，浅的，里面有轻微的凸起，有一个宽的平边。在杯子的外墙上贴了八个用模具装饰的植物纪念章：四个大的，椭圆形，顶部和底部有两个尖尖的末端，与四个较小的末端交替出现，中间点缀着，圆形的，朝向杯子底部的位置。釉面在脚的边缘上方停止；杯子的底部和内部没有上釉。主要颜色是杯和脚上的琥珀色，茎上的绿色。纪念章是乳白色的绿色色调，灵感来自中亚银器的悬臂装饰。杯子可能是用来装光源的。', '', '', '', 'https://www.maotorino.it/media/opere/Cv09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4867', '国立东方艺术博物馆（都灵）', '一对弯曲的咬边', '', '', '两根杆子垂直排列在马的下颌两侧，并通过鞋带固定在嘴的侧环上。在背面，中间有两个用于此目的的圆柱环。形状是弯曲的；一端变大并以直角弯曲，形成具有下垂舌头的动物形态生物的头部，侧视图。其他装饰元素，雕刻或浅浮雕，它们由杆上的抽象蝉纹和另一端的示意性动物头组成。这些标本显示了相同的形状和装饰-镜面-和相同的大小。然而，不同的铜绿表明它们可能属于不同的夫妇。', '', '', '', 'https://www.maotorino.it/media/opere/Cb61b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4868', '国立东方艺术博物馆（都灵）', '带有八个弓箭手的瞭望塔模型', '', '', '这座塔由三个可移动的中空楼层组成，从一个几乎是方形的被墙包围的庭院中升起。在第二层和第三层，在阳台的四个角落，放置了同样多的弩手雕像，有些是可拆卸的，有些则粘在建筑物上。他们以简单而简洁的方式造型，每个人都穿着一件长束腰外衣，头上戴着高高的新月形头饰。这座塔是为正面视图而设计的-少数装饰元素集中在这里-仅部分侧面。而小雕像，建筑物的正面和侧面都被一个不稳定的橱窗所覆盖，整个庭院的背面和内部都没有上釉，并显示出兵马俑的颜色。这些瞭望塔的模型在中原大地主的坟墓中相当常见，特别是在河南，在汉末。', '', '', '', 'https://www.maotorino.it/media/opere/Cc70.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4869', '国立东方艺术博物馆（都灵）', 'BRICCO用于加热洗澡水', '', '', '这种方胡的美丽经典造型——一种“方胡”，用于盛装谷物酒和谷物本身-装饰植物突出了这一点，它将平坦的表面与精细雕刻的寄存器交替使用，并带有轻微浮雕的蜗壳和网纹。在构图的中心，每个花瓶的表面都有一个动物形态的面具（普寿）。它的末端是一个孔眼，孔眼上悬挂着一个大的层流环，雕刻和穿孔，两侧有模糊的动物形态图案：两个四足动物从侧面看，截然相反，中间是一个三尖的形状，让人想起后来中国人描绘的蝙蝠。这些装饰戒指可能模仿了玉器，获得了吉祥的象征意义。方壶体表的装饰分为五个不同几何形状的方格。', '', '', '', 'https://www.maotorino.it/media/opere/Cb10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4870', '国立东方艺术博物馆（都灵）', '击鼓者的形象', '', '', '这位年轻的女演员，是一个由另外四名女演员和一名舞者组成的团体的一员，它被描绘成盘腿坐在一个薄的方形底座上，他把一个小鼓放在膝盖上，中间有两条带子。他用双手在两侧弹奏，手指舒展而优美。女孩的脸是圆的，脸颊上涂着粉红色的美丽，嘴巴上涂着红色，睫毛和眼睛都涂成黑色。额头上用红色绘制了一个现在难以辨认的花卉镶嵌图案（花典）。头发是黑色的，中间有分叉，梳成双髻（双髻）。这位女演员穿着一件低胸紧身胸衣，长袖，白色背心和一条宽大的垂裙，涂成橙色。一条棕色的长围巾，卡在左胸高度的腰带上，穿过肩膀，落在右侧。', '', '', '', 'https://www.maotorino.it/media/opere/Cc18d.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4871', '国立东方艺术博物馆（都灵）', '香水燃烧器', '', '', '该物体由两个不同的部分组成，一个香炉和一个盆，将它们放在一起，并使它们与覆盖它们的玻璃密不可分。平底的盆，搁在三只蹄脚上，呈喇叭形，很浅，壁很厚。上面放着香炉，也是短脚的三脚架。它呈球根状，向下扩张，向顶部缩小，顶部有一个小的喇叭形颈部。在一侧打开一个三角形的孔，用于插入香精。上半身，占三分之二，它被打孔以形成与锯齿状三角形交替的圆弧的重复设计。', '', '', '', 'https://www.maotorino.it/media/opere/Cv78.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4872', '国立东方艺术博物馆（都灵）', '带包的宫廷女人', '', '', '身材苗条的宫女或宫女的站立雕像，右手放在腹部，左手折叠在胸部：一个柔软的袋子从前臂落在左侧。她有一个高发髻的发型，在头的右侧（banfanji）形成一个宽阔的翅膀。她穿着一件窄袖长裙，深领口，高腰，臀部系着一条长缎带，前面有一个大结。在原本应该涂成红色的束腰外衣上，他穿着一件带有棕色残留物的半袖背心（banbi）。在发型（灰色）和面部特征的下划线上可以看到其他冷色素的痕迹。', '', '', '', 'https://www.maotorino.it/media/opere/Cc80.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4873', '国立东方艺术博物馆（都灵）', '“猫头鹰形状”的动物形态花瓶', '', '', '这个花瓶的主体有一个相对细长和较深的形状，略有腹部，末端有一个短的喇叭形颈部。从顶部边缘到底部，在容器周围以几乎规则的距离涂抹了五个粘土带。然后用手将它们压在表面上：仍然可以看到指纹的痕迹。该容器的特点是在边缘上垂直嫁接的粘土三角形：它覆盖了其周长的一半，形成一种高圆屋顶。这种类型可以连接到一小群被称为“猫头鹰形状”的花瓶，其中的孔与动物的眼睛相对应，头部的三角形凸起和身体的腹部花瓶。', '', '', '', 'https://www.maotorino.it/media/opere/Cn38.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4874', '国立东方艺术博物馆（都灵）', '带有动物形态面具的“YUE”型弯刀头', '', '', '这把岳斧头的方刃从铁丝的侧面展开，铁丝是圆的，边缘是钝的。主要装饰在刀刃的两侧，向斧柄的平行方向发展。这是两只勐禽面对面的抽象表示，侧面的位置，总体上给人一种动物形态面具的印象：凸起的瞳孔变成了道铁的眼睛。刀柄也是方形的，中间有一个圆形的孔。在这个洞之外的终端部分，仍然出现了两个雕刻的道铁面具，一个是斧头的正面，比刀片上的要简单易读得多。正如这种装饰的可怕方面所表明的那样，这些斧头被用来杀死动物以供祭祀。', '', '', '', 'https://www.maotorino.it/media/opere/Cb34.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4875', '国立东方艺术博物馆（都灵）', '罗汉·阿西索·因·达玛卡克拉穆德拉', '', '', '罗汉（罗汉）坐莲座（莲座），双手放在胸前，以“启动教义之轮”（dharmacakramudra）的手势。这个人物被一件沉重的修道院长袍所覆盖，这件长袍落在座位前的褶皱瀑布中。头部，在太阳穴和脸颊之间有明显的凹痕，它向前倾斜，几乎是仁慈地向下看：在它原来的位置上，雕像可能是悬在上面的。寺庙祭坛上的奉献者。', '', '', '', 'https://www.maotorino.it/media/opere/Cs05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4876', '国立东方艺术博物馆（都灵）', '男性裸体雕像', '', '', '这种类型的人体模型的可拆卸木臂固定在与肩膀高度相同的孔中。他们后来穿着布衣，并可能配备了武器和木制盾牌。小雕像描绘了一个赤身裸体的人，姿势僵硬，解剖部位刚刚提到，头部特征很好。面部具有成年男性的特征，颧骨高，下巴呈方形，长发向后梳，并在头顶上卷曲。身体苗条，骨骼和肌肉仅建议：胸部和臀部略显突出，脚趾被雕刻，生殖器的浮雕图案。肉色的凹槽几乎完全覆盖了小雕像，但其他颜料没有保存下来。', '', '', '', 'https://www.maotorino.it/media/opere/Cc57c.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4877', '国立东方艺术博物馆（都灵）', '两个安福拉和扁平的球形腹部', '', '', '这种双耳瓶是闽江上游文化中最常见、最成功的陶瓷形式。墙壁的光泽度和深灰色与打磨和熏制过程有关。它有一个球状的、扁平的腹部，在一个狭窄的底部变窄：这形成了一个扁平的环，围绕着一个凹入的中心部分，刻有不规则的条纹。在花瓶的上部，肩部、弯曲的颈部和喇叭形边缘的通道是流畅和谐的。然后，这个波浪形的导向器通过两个大的、拱形的、对称的环从边缘连接到腹部的最大直径，其轮廓向容器底部延伸。', '', '', '', 'https://www.maotorino.it/media/opere/Cv58.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4878', '国立东方艺术博物馆（都灵）', '两个环的罐子和小膨胀的脖子。', '', '', '这个花瓶，不规则的墙壁，具有典型的半山陶瓷的形状和装饰。它看起来像一个球状的锅。在容器的最大直径以下施加两个孔眼。主要装饰由四个等距重复的螺旋图案组成，并连接在一个装饰带中。这些蜗壳的中心位于花瓶的肩部上方：一个黑色的圆周，外圆是虚线；涂成红色的第二个圆周继续向下延伸，然后向上并顺时针连接到红色圆周。下一个。这些红色的主要路线，可能是先画的，它们被典型的半山凹痕所包围，并被宽阔的黑色条纹所强调，与装饰带的上边缘和下边缘交替相切。', '', '', '', 'https://www.maotorino.it/media/opere/Cn01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4879', '国立东方艺术博物馆（都灵）', '围着围巾坐着的女人', '', '', '年轻女子坐在沙漏形的凳子上，其装饰图案描绘了编织的柳条。这位女士胖乎乎的脸和宽大的发型与纤细娇小的身体形成了鲜明的对比，并用小手将长围巾抱在膝盖上。女人的脸是冷画的，白底粉颊，红嘴，睫毛，黑瞳孔。雕像的其余部分以奶油色、棕色、蓝色、绿色和赭黄色上釉。深蓝色釉面的头发，中间有条纹，在头部两侧聚集成两个大发髻。长袍由长袖紧身胸衣和短袖背心（banbi）组成。宽百褶裙围绕着伴娘紧绷的臀部和纤细的大腿造型。', '', '', '', 'https://www.maotorino.it/media/opere/Cc12.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4880', '国立东方艺术博物馆（都灵）', '佛塔形状的罐子', '', '', '该物体由三个可变的圆形部分组成，它们重叠在一起，模仿了佛塔的形状。支撑物位于一个厚实的直底座上，该底座在上部闭合：从这里开始，一个高的穿孔的开放式栏杆围绕着它。除了支持。在内部，支撑体像一个圆锥形的树干一样上升，上面有两层圆形的花瓣。平底罐子靠在壁上，靠在支架的边缘。它有一个向底部收缩的卵形主体，并由一个圆形屋顶覆盖。屋顶略呈圆形，装饰有一系列呈放射状排列的杆，模仿皇家屋顶的肋骨。盖子有一个中央柱子，周围有一个类似于宝塔支架的穿孔栏杆。在中央柱子上方嫁接了一个小的五肋树冠，与一个地球仪相连。', '', '', '', 'https://www.maotorino.it/media/opere/cv79.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4881', '国立东方艺术博物馆（都灵）', '军官（军人？）站在高处', '', '', '这位政要穿着一件长到脚的束腰外衣，膝盖以上穿着一件宽大的夹克，左襟翼在右侧交叉。它有巨大的翻领袖子，从夹克本身的下摆向下延伸。面部有隐约的西方特征，其特征是“野鸡头饰”（和关）。军事官员穿的。头部和颈部是冷漆：在略带粉红色且不稳定的凹槽上方可以看到黑色，红色和橙色颜料的痕迹。他的左眼上偶然发现了一块绿色的玻璃片，一块棕色的玻璃片落在了头饰的一部分上。身体的其余部分是赭色、绿色和奶油色的釉面。这座雕像应该是8世纪初在陕西或更有可能在河南制作的，其中三色上光技术（三彩）它被微调了。', '', '', '', 'https://www.maotorino.it/media/opere/Cc02a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4882', '国立东方艺术博物馆（都灵）', '带有拟人装饰的两环罐子', '', '', '这个形状不寻常的容器的轮廓非常不规则。它有一个高而喇叭形的脖子，边缘不规则，有一个宽的截锥形肩；在肩膀和腹部之间的角度下，花瓶的侧面贴有两个细长的手柄。棕黑相间的彩绘装饰是典型的张家嘴式。从花瓶的肩部到底部，两个面被垂直的带子分成两半。从这些带的最高部分开始，有两条对角线，条纹，它们在与环相对应的钩子上向上延伸，并与来自相反方向的钩子镜面连接。在容器肩膀上形成的每个河床中，一个站着的拟人化人物的形状被涂成黑色，双臂抬起，巨大的头部向前不平衡。', '', '', '', 'https://www.maotorino.it/media/opere/Cn13.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4883', '国立东方艺术博物馆（都灵）', '“珍木寿”，猫科动物坟墓的保护生物', '', '', '小雕像是空心的，描绘了一只狮子脸的真目寿，蹲在后腿上。宽而低的头有紧绷的下巴，扁平的鼻子，间隔开的圆眼睛，尖尖的小耳朵。这些继续向下，带有黑色条纹的拱门，代表该生物的鬃毛。前额中央有一个向后扭曲的角，最后卷曲。从颈部到尾部，几乎看不见，有一个背脊，有四个圆形的生长物。肩膀上有两个弯曲的扁平尖翅。前肢显示出尖尖的肘刺，四条腿都是蹄形的。肚子是中空的。小雕像最初的复杂色素沉着无法立即识别。', '', '', '', 'https://www.maotorino.it/media/opere/Cc96.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4884', '国立东方艺术博物馆（都灵）', '“真木寿”，保护坟墓的怪物', '', '', '雕塑由四个部分组成，通过榫眼和榫眼安装：一个巨大的方形底座，一根茎，示意性地描绘了真木寿的身体和头部，两个鹿角。前两部分是用一块木头雕刻而成的。整体涂有黑色墨水和与朱砂或其他颜料混合的漆（主要用于装饰元素）。真目寿的角在头部两侧插入两个孔。它们很光滑，尖端涂成红色，在分叉的底部装饰有类似于我们在雕塑底座上发现的混合图案。', '', '', '', 'https://www.maotorino.it/media/opere/Cl09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4885', '国立东方艺术博物馆（都灵）', '锋利的“张”斧头', '', '', '获得该物体的石板最大厚度为1厘米，具有典型的张状。长刀片呈梯形，在凹面较宽，在两侧都很锋利。在刀片的底部，物体在两侧扩大了几毫米。该突起在一侧光滑且钝，长3.4厘米，在另一侧有不规则的凹痕，长3.8厘米。然后物体再次收缩成一个矩形的柄，在中心打孔。这类物品，可能是为了仪式和象征用途，而不是为了切割和冒犯，在公元前三千年和二千年的遗址中发现了许多。C.在中国大部分领土上。', '', '', '', 'https://www.maotorino.it/media/opere/cp1.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4886', '国立东方艺术博物馆（都灵）', '铠甲勇士', '', '', '身穿盔甲的战士形象，双腿站在一个带有倒角的小四边形底座上。他的体重向左移动，双臂向前举起，就像拿着丢失的武器一样。巨大的面部特征，大而突出的眼睛，宽阔的嘴巴和迷彩的鼻子，指的是非中国人的身体类型。他所穿的盔甲从头到脚都是沉重而完整的，由不同大小的板子通过螺柱和搭扣固定在一起。头盔是一种紧贴耳朵的头盔，带有纽扣顶部和铆接的后鳍。在小雕像上到处都可以看到冷颜料的残留物，但最重要的是在正面：脸上和前臂上都是粉红色的，红色集中在手镯和腿上，黑色在其余的盔甲上有痕迹。头盔上可以看到镀金的残留物。', '', '', '', 'https://www.maotorino.it/media/opere/Cc_88.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4887', '国立东方艺术博物馆（都灵）', '带盖子的小罐子', '', '', '带有压扁的球形腹部的小罐子，底部凹形，放置在短脚上，平底略呈喇叭形。弯曲的脖子支撑着一个带有加厚嘴唇的杯状嘴巴。盖子和边缘一样宽，是凹形的，中央有一个尖头旋钮。带有裂纹的半透明玻璃在中空部分较厚的地方趋于绿色：这是一个特征，典型的七世纪。它还覆盖罐子的内部，并在外部以相当规则的方式停在脚上方约1厘米处。盖子和容器之间有一个颜色上的区别：第一个有一个倾向于灰色的陈列柜，第二种倾向于乳黄色，带有大量的杂质和深色斑点。', '', '', '', 'https://www.maotorino.it/media/opere/Cv05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4888', '国立东方艺术博物馆（都灵）', '带着面具的骆驼', '', '', '空心骆驼雕像（Battrian），造型简单，直立在一个薄的矩形底座上。它的头抬起，枪口朝上：小耳朵，圆圆的眼睛，大鼻孔，嘴巴紧闭。头部和颈部的皮毛没有任何特征，尾巴也是臀部上简单的扁平浮雕。相反，斗篷是以一种独特的方式绘制的：在赭色和白色的背景上，同心的棕色圆圈和拱门创造了几何图形。抽象的ri。货物横向放置在动物的低驼峰上，由两个大袋子组成，上面覆盖着可怕的面具。每个面具都像一个神奇动物的头一样浮雕：一个巨大的被压扁的三叶形鼻子，凹陷的眼睛和突出的瞳孔，尖尖的耳朵和两个扭曲的角。嘴巴和下巴被真正的袋子所取代。', '', '', '', 'https://www.maotorino.it/media/opere/Cc85.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4889', '国立东方艺术博物馆（都灵）', '站着的女人', '', '', '人物站立姿势僵硬，双臂垂直于身体。手伸到肚子前面，藏在袖子里，它们被一个几乎垂直的通孔穿过，该通孔最初插入了一个物体。可拆卸的头部通过别针连接到颈部。相当宽，造型甜美，脸是一个戴着红色环形耳环的年轻女子的脸，梳着中间的分叉和扁平的发髻。这位女士在右边穿着各种重叠和交叉的衣服，形成了一系列非常厚的“V”形领口。外袍长到脚，使雕像看起来苗条，并展开成花冠形成底座。它是红棕色的，底部是白色的，边缘是深棕色的。这种雕像在公元前2世纪的墓葬中很普遍。', '', '', '', 'https://www.maotorino.it/media/opere/cc69.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4890', '国立东方艺术博物馆（都灵）', '带两个龙形环的透明甲壳安福拉', '', '', '这个具有纪念意义的双耳瓶在底部有一个锥形的卵形主体，脚刚刚提到。脖子又高又细，中间有四个凸起的环，肩膀上的附件突出。开口呈杯状，唇厚。从这里到肩膀，两个对称的龙形手柄分开（也许是角猫）他们用前爪抓住杯子的边缘并咬住它。在双耳瓶的肩膀上，在每个环上都贴有动物形态的面具（pushou）。它似乎从正面描绘了同样咬着边缘的生物。中国双耳瓶的形状来自于从近东和中亚进口的罗马时代的银和玻璃制品。龙形环和杯形开口证明了该模型对中国口味的适应。', '', '', '', 'https://www.maotorino.it/media/opere/Cv03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4891', '国立东方艺术博物馆（都灵）', '马背上的一群音乐家', '', '', '在7世纪和8世纪之间，重要的葬礼队伍经常伴随着骑马的音乐家。纪念这一事件的小雕像与葬礼队伍中的其他“发光物体”一起展出（明奇）。在运送尸体的马车前面的大马车上。然后，它们通常被放置在侧壁龛中，沿着通往真正坟墓的道路挖掘。女子吹奏琵琶的两种形式：琵琶和阮。男子使用的乐器有：双簧管、通侯琴、钹和腰鼓.', '', '', '', 'https://www.maotorino.it/media/opere/Cc20.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4892', '国立东方艺术博物馆（都灵）', '“真木寿”，坟墓的保护生物，在高处', '', '', '令人印象深刻的奇妙生物雕像，有猫科动物的鼻子，高角和翅膀，蹲在后腿上。它有一个巨大的方头，巨大的嘴巴张开，露出牙齿，方眼。在三叶形、宽而扁平的鼻子两侧，沿着上唇有两个副牙。下巴下方，一根长长的胡须凸出在胸前；在头部的两侧，枪口和颈部由一种鬃毛构成，鬃毛由两个由平行部分形成的刚性山脊形成。额头上嫁接了两个非常高的波浪角，让人联想到黑斑羚（非洲食草动物）的角。，在下半部分由一排角膜突起和一个短分支丰富。这个脚手架，本身就很重要，从贞木寿的颈背上升起的一个大的分支冠状图案使它变得更加重要，就像一个五角火焰（火炎）。', '', '', '', 'https://www.maotorino.it/media/opere/Cc43a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4893', '国立东方艺术博物馆（都灵）', '高脚中空玻璃', '(Cn/25)', '21，8 cm x 6，5 cm', '这个细长的抛光玻璃杯的壁很薄。深杯的边缘是反的，底部是凸起的。它嫁接在一个非常高的锥形管状茎上，底部有一个球根状的凸起，在底部闭合成一个细圆柱体。它的直径小于茎的上部，位于一个圆顶脚上。杯和脚是光滑的；阀杆在车床上用非常细的平行雕刻线条装饰。装饰是由圆形和花瓣形状的小穿孔图案完成的，与底部的凸起相对应，它的轮廓首先被雕刻出来。', '约公元前2550-2300年。', '', '黑陶', 'https://www.maotorino.it/media/opere/Cn25.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4894', '国立东方艺术博物馆（都灵）', '听证官员', '(Cc/89)', '61 cm x 17，5 cm x 15，4 cm', '这位政要被描绘成观众的姿势，僵硬地站着，双手交叉放在肚子上。他戴着官方的“山脊型”头饰（亮冠），并穿着经典的宫廷鞋，鞋尖抬高。他有一张轮廓分明的脸，直着鼻子，画着细节：眉毛和黑色的眼睛，严厉的目光，稀疏的胡须和胡须，嘴巴涂成红色。在圆领衬裙上方，他穿着一件长到地面的束腰外衣，右侧交叉成“V”形，袖子又长又宽。这件衣服的正面和背面由两块由半刚性材料制成的矩形保护板封闭：一种用于保险的背腹围裙。由两条皮带牵引，一条在胸部，另一条在腰部。正面绘有红色颜料花卉纪念章图案，背面绘有淡绿色残留物。长袍最初是红棕色的，脸和手是粉红色的，发型和鞋子是黑色的。', '七世纪下半叶。公元。', '', '透明陶土，釉料和颜料', 'https://www.maotorino.it/media/opere/Cc89.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4895', '国立东方艺术博物馆（都灵）', '莫洛索', '(Cc/49.D)', '71，6 cm x 77，5 cm x 35 cm', '强大的巨蜥站立时姿势僵硬，短尾巴在背部上方呈环状折叠。头部是可移动的，刚刚放下，它的鼻子被压扁了，大耳朵竖了起来。眼睛，鼻子和额头上的皱纹被渲染得具有一定的真实感；肛门也是模糊的。狗把涂成红色的饰物带到胸前：一种吊带，在脖子的底部有一个项圈，其他连接的带子穿过pe。并将身体绕在肩膀后面。所有这些都固定在动物臀部和颈部之间的一个凸起的钩子上，并在两侧水平延伸成两个短的圆形部分。坟墓的守护者-就像肉和骨头标本是主人领地的守护者一样-发现了兵马俑四川洞穴墓葬中的众多。', '公元二世纪下半叶。', '', '灰色陶土，红色颜料', 'https://www.maotorino.it/media/opere/Cc49.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4896', '国立东方艺术博物馆（都灵）', '左手持球的菩萨', '(Cs/20)', 'h. 21，4 cm', '菩萨坐在带有平台的四边形座位上，我们不知道这是否是他最初的支撑。脚是粗壮的，手又长又细。左腿折叠在身体旁边，脚掌放在座椅上，右腿垂直落下，脚放在平台上；左臂放松地放在相应的腿的大腿上，用手在手掌中支撑一个球体，从球体中流出一个扭曲的突起（燃烧的珍珠？）；另一只手举到胸前，形成争论的手势（vitarkamudra）。头部饰有圆形头饰，中央部分饰有精致的花卉装饰。面部线条细腻而规则，嘴巴周围环绕着卷曲的胡须和三个波浪状的三方花边。头发，胡须和手中的球体是黑色的，是雕像中唯一不是金色的部分。', '16至17世纪下半叶。', '', '镀金青铜', 'https://www.maotorino.it/media/opere/Cs20.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4897', '国立东方艺术博物馆（都灵）', '带脚和宽边的杯子', '(Cn/26)', '11，2 cm x 9，9 cm', '这款抛光玻璃的壁薄，边缘仅1毫米。深杯有宽的盘状盘口和平底，与支架上方的中槽相对应。杯身呈钟形，直径向凹槽方向增大。在下面，中空的支架向脚倾斜，脚是弯曲的，底部垂直加厚。杯口、杯脚光滑；颈部下方的主体在车床上装饰有非常精细的平行雕刻线条。在凹槽下方，装饰由圆形和花瓣形状的小穿孔图案完成。这是一个独特的装饰带，其中圆孔与三个长方形孔交替出现：两个对角线对齐，第三个向下倾斜，垂直于其他的，在右边。', '约。公元前2350-2100年', '', '黑陶', 'https://www.maotorino.it/media/opere/Cn26.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4898', '国立东方艺术博物馆（都灵）', '折叠式面罩', '(Cc/41)', '32，1 cm x 31，1 cm', '这个中空的动物形态面具在大概是葬礼的背景下起到了支撑的作用。面具有突出的枪口特征，使用的元素，例如在额头上横向分开的大分叉角：它们非常弯曲和尖锐，上面有一个短的分支。怪物有突出的大眼睛，浓密的向下倾斜的眉毛，猪耳朵，宽而扁平的鼻子，巨大的拱形开口，可见方齿。圆口的尖端在头部顶部，也许是第三个角，以及沿着下巴排列的七个类似的尖端，使其更加丰富。对称的角和下巴下方的尖端被涂成相同的黑色。面具的异位功能是由所描绘的生物的悲伤和善良之间的表达来调解的。', '公元7-10世纪（？', '', '棕色陶土，白色陶土和颜料', 'https://www.maotorino.it/media/opere/Cc41.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4899', '国立东方艺术博物馆（都灵）', '入侵盆地模型', '(Cc/72.D)', '', '圆形盆地，穿孔的墙壁上有三个矩形条纹，一个在另一个上方，模仿栅栏。垂直元素在外部刻有对角线相交的线条，形成菱形网格。水库宽而凹，底部平坦。中间贴着四个站立的鹅的圆形雕像；在更外的一条带上，有六只鹅和两条鱼，后者彼此截然相反。在倾斜的墙壁上，一边是三只蜗牛，另一边是两条鳗鱼之间的一只蜗牛的较平的图形：软体动物是仅代表外壳。整个模型，外部和内部，包括18个以相当机械的方式分布和间隔的小雕像，上面覆盖着厚厚的虹彩玻璃。', '公元1至2世纪。', '', '赤陶，深绿色釉面', 'https://www.maotorino.it/media/opere/Cc72.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4900', '国立东方艺术博物馆（都灵）', '带有盖子和彩绘装饰的“HU”花瓶', '(Cv/61.D)', 'h. 53，6 cm x 36，7 cm', '这个花瓶有一个非常膨胀的腹部，稍微压扁，放在一个弯曲的脚上，有一个凸起的支撑。高而宽的脖子从肩膀到下摆和谐地弯曲，在一个形状的开口中进一步变宽。形状和大小与脚的形状和大小相似。花瓶有两个装饰性的动物形态面具（普寿）对称地涂在肩膀下方，并涂上白色外套。胡的墙壁，一直到肚子的下部，用灰色背景上的白色和红色图案装饰成横条，有时富含紫色，周围环绕着白色的云朵图案，背景是明显更浅，更细微的灰色。棕色与面团的颜色相比。', '公元前2世纪。', '', '灰色陶土', 'https://www.maotorino.it/media/opere/Cv61.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4901', '国立东方艺术博物馆（都灵）', '不对称“ GE ”斧头-匕首刀片', '(Cb/39.D)', '8，2 cm x 22，6 cm', '这把匕首的三角形刀片，中间有轻微的肋骨（ge）它不是对称的，但具有弓形的上螺纹和直的下螺纹；尖端是钝的。在底部，它连接到从两端突出的细袖子上，然后继续形成一个几乎矩形的刀柄，与刀片的上侧对齐。在柄的中央有一个圆形通孔，用于固定木柄。茎的末端部分，向底部延伸一点，它装饰有一个以眼睛为中心的动物形态的半面具：宽而深的雕刻突出了绘画的特征，它由一条电缆线在三个侧面构成。刀刃的形状和刀柄的装饰在武器的另一面完全相同。', '商朝，公元前12-11世纪。', '', '红色，黄色和绿色铜绿', 'https://www.maotorino.it/media/opere/Cb39.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4902', '国立东方艺术博物馆（都灵）', '“真木寿”，动物枪口墓的保护生物', '(Cc/7)', 'h. 38，2 cm', '真目寿正面蹲着僵硬的姿势（此处为侧面画像）。它有一个动物的头，隐隐约约像猫一样，下巴张开，有高角和翅膀。口吻宽而扁平，巨大的莲座状鼻子，间隔突出的眼睛，小耳朵。嘴巴有下垂的舌头和突出的四个尖尖的犬齿。在口吻的两侧有一种黑色条纹的鬃毛。扭曲的角让人联想到黑斑羚（非洲羚羊）的角。从颈背到背部有一个五角形的背脊，而长而弯曲的尾巴则沿着背部上升。胸部有力，两侧有扁平的小翅膀。前肢显示出尖尖的肘刺，形状很好，有五指的腿，而后部则不是很发达，粗略地勾勒出来。挖出的腹部在强大的胸部下方留出了一个大开口的空间。主要颜色是白色，橙色，黑色和红色。', '公元7世纪下半叶（？', '', '红褐色面团陶土，白色釉料和颜料', 'https://www.maotorino.it/media/opere/Cc07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4903', '国立东方艺术博物馆（都灵）', '熊形灯', '(Cv/75.D)', 'h. 28，9 cm x 14，4 cm', '该灯的特点是配置了一个阀杆，该阀杆位于一个高的喇叭形底座上，并支撑着一个低的截锥形杯。所描绘的生物，具有拟人化的身体和动物的头部，双手放在膝盖上跪在圆形的蹄子上。巨大的头部当然让人联想到汉族肖像画中对熊的富有想象力的描绘，但它看起来更像老鼠。它有一个长而尖的鼻子，大嘴巴张开，舌头模糊，但没有牙齿或尖牙。相当厚的玻璃完全覆盖了灯。', '公元1至2世纪。', '', '绿色釉面的米黄色陶土', 'https://www.maotorino.it/media/opere/Cv75.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4904', '国立东方艺术博物馆（都灵）', '法诺内的“GE”斧头匕首刀片', '(Cb/43.D)', '11，2 cm x 19，1 cm', '这把匕首的形状是经典的格法农刀：短的三角形刀尖，宽的法农刀，鞋跟由凸起的肋骨突出，并向下延伸，长矩形鞋跟。武器的特点是主要的装饰，在刀刃和法诺内之间进行浮雕，它描绘了从侧面看到的动物的头部和颈部。头部的设计是用连续的笔触完成的：头骨和枪口的形状，下巴张开，它以突出猫科动物（也许是老虎）下巴的蜗壳结束。。在刀片的两侧，装饰非常相似，但不是很均匀。这种类型的斧头-匕首在公元前5世纪至4世纪的中国中部和南部非常普遍。，我们标本的形状在公元前4世纪的楚国肯定很普遍。，而描绘老虎的风格更让人联想到四川等周边地区的艺术，或与草原世界的接触。', '公元前4世纪。', '', '橄榄绿铜绿青铜', 'https://www.maotorino.it/media/opere/Cb43.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4905', '国立东方艺术博物馆（都灵）', '双盆灯', '(Cv/74.D)', 'h. 79 cm x 36 cm', '这种三层灯由两部分组成，分别对应于带有相关承载元件的下部和上部灯座，还有八个臂，也是可拆卸的，配有燃油杯。下部由一个大盆组成，放置在一个高的截锥形脚上，中间插入一个空心柱。在下骨盆的宽而平坦的边缘上，有四个等距的孔，其中插入了许多弯曲的臂：每个孔的末端都有在水平部分上涂上一层有波纹状波纹上骨盆的不同之处在于四个装有碗的臂，在这种情况下，碗的末端是有角的龙头。从第二个盆地的中心开始的柱子一直直走，向上伸展，形成一只鸟的身体。', '公元二世纪。', '', '绿色釉面的粉红色陶土', 'https://www.maotorino.it/media/opere/Cv74.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4906', '国立东方艺术博物馆（都灵）', '虎形手柄的“虎子”水罐', '(Cv/71.D)', '13，1 cm x 18，2 cm', '该标本的圆柱体，中间部分略窄，两端呈圆形，它靠在四只脚上，让人联想到程式化的腿。具有直开口的圆柱形颈部斜接在容器的两端之一上方，因此，它呈现出无头四足动物的整体形状。手柄是全方位配置的，看起来像一只老虎，它的爪子张开在水罐上方。有突出的眼睛和舌头的宽阔的头部，以及老虎的瘦小的身体，它们的造型很简单，并在尾部刻上了加厚的刻痕。', '公元3世纪中叶', '', '带绿色毯子的灰色瓷器', 'https://www.maotorino.it/media/opere/Cv71.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4907', '国立东方艺术博物馆（都灵）', '短展开的颈部和扁平底座的花瓶', '(Cn/22)', 'h. 12，3 cm x 17，2 cm', '这个花瓶的短而宽的脖子是圆锥形的，边缘的墙壁也很厚。身体呈球形，呈压扁的“鼓形”，底部扁平。装饰不是很好，护套几乎没有覆盖容器表面的绳纹。在边缘的内壁上有一条褪色的棕色油漆条纹，其中四对间隔开的黑色“V”形突出，并排或略微重叠。一种暗褐色也完全覆盖了颈部的外壁，用黑色的水平线和斜线在没有特别对称性的情况下进行边缘和交叉。花瓶的重要装饰在其上半身周围：伟大的希腊人的连续理论，涂有一条棕色带边，中间用黑线标记。', '约公元前1200-950年', '', '米色陶土，浅外套上有棕色和黑色装饰', 'https://www.maotorino.it/media/opere/Cn22.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4908', '国立东方艺术博物馆（都灵）', '带有丝带和骑士装饰的马', '', '24，7 cm x 37，2 cm', '这匹马保留了它可拆卸的骑手。这匹马的鬃毛很短，枪口很长，是典型的汉马。嘴巴半张，青蛙被切开，大圆眼睛突出。外部完全覆盖着白色涂料和黑色涂料，上面还冷涂了其他颜料。这个标本的标志是点缀的带子，最重要的是，全身长丝带的波纹。鞍座被一个让人联想到环形折叠织物带的图案所取代。骑手的身体是中空的，腿是平的，其形状适合动物的臀部。它制作简单，正面位置坚硬，有孔，可拆卸的手曾经插入过这些孔。大头，枕骨区域突出，它被一个带有下摆的军帽覆盖，可以让人瞥见带有中央装饰的发型。', '公元前一世纪初。', '', '灰色陶土，白色陶土和彩色颜料', 'https://www.maotorino.it/media/opere/Cc55.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4909', '国立东方艺术博物馆（都灵）', '带有边缘和青铜“Pushou”的漆花瓶', '(Cl/10.D)', 'h. 31，2 x 21，2 cm', '梨形花瓶在低的圆柱形的脚上，有一个大的腹部，逐渐缩小成一个长的喇叭形的脖子。下摆由光滑的深色青铜圆形垫圈加固。花瓶的凸盖与这个金属配件中突出的扁平联锁相吻合。构成盖子和容器的木头很轻；底部由一块明显的木板组成，相对于脚环略有凹陷。在花瓶的肩部，应用了两个对称相对的青铜动物形态面具（pushou）。花瓶的内部完全涂成黑色，同样的哑光制剂也覆盖了底部的外层。相反，物体的整个可见表面，包括盖子，都覆盖着一层厚厚的闪亮的棕色漆。装饰，既不影响盖子也不影响脚，位于两个水平带之间。', '公元前4至2世纪。', '', '漆木和青铜', 'https://www.maotorino.it/media/opere/Cl10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4910', '国立东方艺术博物馆（都灵）', '平底杯', '(Cn/35)', 'h. 7，7 cm x 20，4 cm', '在外面，黑色的密笔装饰仅限于沿边缘的四个花彩图案。主要装饰覆盖了容器的整个内表面，并在空与满之间进行了精心的绘画流动性游戏。一个大的彩绘圆圈，里面有四个带有完整中心圆圈的储蓄圆圈，它占据了杯子的底部。从这个核心，以一种曲折的旋转运动，三个臂向左开始，它们像绞线一样连接到许多椭圆形上，沿着边缘以规则的间隔放置，每个顶部都装饰有三个垂直条纹。由实线和空线交替组成的类似臂，沿边缘和对角线将椭圆彼此连接。由手臂定义的弯曲三角形空间是用纯色绘制的。', '约公元前3350-2650年。', '', '带有黑色装饰的米色兵马俑', 'https://www.maotorino.it/media/opere/Cn35.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4911', '国立东方艺术博物馆（都灵）', '带有彩绘装饰的茧形花瓶', '(Cv/60.D)', '45，1 cm x 51，2 cm', '花瓶有椭圆形和扁平的身体，弯曲的底座没有脚，短而直的脖子，下摆向外，嘴唇变厚。这件作品在烹饪前被均匀地涂上了灰棕色的底色——这是这种类型的平行条纹的特征。在容器的整个表面上仍然可以清楚地看到加工过程。然后在他的脸上画上了一头公牛，另一面画上了一个战士，由云朵的大蜗壳和其他装饰元素构成。该设计是用黄色和白色颜料制成的，甚至可能是粉红色的，并在稍后用黑色线条进行了详细说明。', '公元前一世纪初。', '', '灰色陶土', 'https://www.maotorino.it/media/opere/Cv60.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4912', '国立东方艺术博物馆（都灵）', '马背上的男性角色', '(Cc/81)', '31，5 cm x 29，3 cm', '骑在马上的魁梧的男性角色向后弯曲。他的服装的突出特点之一是顶部有穿孔的扁平头饰，一条长长的带子从那里下来，几乎到达背部的底部。另一个特点是沿着右臂垂下的非常长的袖子，而是包含在折叠在腹部的左臂中。这件束腰外衣有一个深方形领口，从上面的半袖背心（banbi）上取下来，前面有纽扣。他还穿着舒适的脚踝紧身裤，腿上有平行的雕刻褶皱。小马鞍靠在马背上的四边形凸起的鞍座上。这个又小又矮，虽然被描绘成一个僵硬的正面姿势，但它的腿暗示着轻微的运动。鬃毛的发型是典型的唐式发型：辫子和短扎的尾巴，朝上；鬃毛梳理在脖子的左侧，并在耳朵上方分成两簇。', '七世纪下半叶。公元。', '', '透明陶土，釉料和颜料', 'https://www.maotorino.it/media/opere/Cc_81.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4913', '国立东方艺术博物馆（都灵）', '有驼背的牛', '(Cc/37)', '17，3 cm x 25，5 cm', '牛，由于在马肩隆上有明显的驼背，发达的驼背和狭窄的后部，可以解释为斑马，虽然这种动物的角很小。牛的四条直腿静止不动。他的披风是白色的，有黑色的斑点。头上的饰面也用简单的黑色线条绘制而成。耳朵的内部，眼睛的上方，枪口的前部，轭和蹄子都涂成粉红色。从南北朝时期开始，牛在陪葬雕像和浅浮雕中经常以各种姿势被描绘出来（420-581天。C.）。在唐代，对送葬队伍（明起）的“发光物体”的逼真描绘确立了一定的自然主义品味。暗指日常生活。', '公元8世纪初（？', '', '黄面团陶土，白色釉料和颜料', 'https://www.maotorino.it/media/opere/Cc37.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4914', '国立东方艺术博物馆（都灵）', '双杯', '(Cn/15)', '9，9 cm x 35，8 cm', '两个没有脚的杯子，形状凸出，嘴唇张开，它们通过几厘米长的管状接头在腹部中间相互连接。这种接头通常是空心的，以允许碗中所含的液体从一个碗传递到另一个碗，在这种情况下，它被容器的壁封闭。两者的外部装饰都仅限于上半部分：下摆下方的红色条纹，黑色条纹和花彩蝴蝶结图案，将水平序列向下闭合。下摆是黑色的。内部完全粉刷过，尽管杯子与杯子的方式不同。新石器时代的双壶相当罕见。', '约公元前2200年。', '', '米黄色陶土，棕红色和黑色装饰', 'https://www.maotorino.it/media/opere/Cn15.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4915', '国立东方艺术博物馆（都灵）', '带有雕刻装饰的谷物酒“HU”花瓶', '(Cb/11.D)', 'h. 34，1 cm x 20，3 cm', '这种类型的花瓶，球根状的腹部向下变细，颈部略微张开，它们在楚国大量生产。它们的特点是一只相当宽的脚，并以带有精细雕刻细节的带状装饰而着称，在光滑条纹的中心被两条空心线隔开。这个标本保留了它的联锁盖，圆形，有三个层流和垂直穿孔的元素。两个可移动的环，也刻有混合图案，通过两个动物形态的面具（pushou）钩在花瓶的后面。从肩膀到脚有五条装饰带，由构成蜗壳的三角形组成，并由装饰有对角线排列的链状蜗壳的丝带包围。在顶部，在肩膀上方，一排与下顶点相切的三角形有助于使花瓶的颈部变细。尽管它很和谐，但容器有轻微的瑕疵，表明它是用于葬礼的。', '第四部分结束；公元前3世纪初', '', '铸模青铜', 'https://www.maotorino.it/media/opere/Cb11.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4916', '国立东方艺术博物馆（都灵）', '双手合十坐佛', '(Cs/9)', 'ca. 91 x 70 cm', '帕德玛萨那的佛像，双手放在胸前：右手的手指盖住左手折叠的手指，两个手指的食指在一起。to。人物的身体看起来很匀称，手脚都呈锥形。头部令人印象深刻，浓密的螺旋卷发无缝覆盖了乌什尼沙（cin.rouji）。前额中央的圆形空腔最初是用来容纳骨灰盒的宝石的。', '公元16世纪。', '', '木材和多色漆', 'https://www.maotorino.it/media/opere/Cs09.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4917', '国立东方艺术博物馆（都灵）', '带模型脸的两环罐子', '(Cn/39)', 'h. 37，9 cm x 32，6 cm', '这个花瓶的肩膀上有一个完整的拟人头像。它是一个球状的锅，向上扩张，向狭窄的底部逐渐变细，高颈弯曲，边缘向外。在肩膀上的黑色带子下面，在容器的一面中间，圆形菱形的粘土头被嫁接。扁平的脸颊被刺穿，面部的其他部分被雕刻并涂成黑色。衣服的背面是网状的，脖子上有人字形条纹。眼睛是雕刻的，周围是黑色的，下面有长长的垂直线条-也许是纹身，使脸部卷曲，看起来像眼泪。鼻子被发现并涂上油漆，鼻孔被刺穿；嘴巴也用黑色雕刻和标记。', '约公元前2300-2100年。', '', '米橙色陶土，黑色装饰', 'https://www.maotorino.it/media/opere/Cn39.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4918', '国立东方艺术博物馆（都灵）', '带研磨机和杵的围栏', '(Cc/71.D)', '9，7 cm x 22，6 cm x 25，2 cm', '矩形围栏由四堵墙界定，其中两堵墙在中间下降到一个巨大的拱形缝隙中，彼此相对。相对的墙壁完全被两个狭长的双层屋顶所覆盖，两端有凸起的茎和小球。几乎在围栏的中央应用了一个像磨石一样的模型元素，下齿板沿轮廓滚花，上齿板在中间凸起。相反，在工作区域的一个角落里，有一个木槌形状的杵，被封闭在两个半圆形的边缘之间。杵被用来清洗和粉碎谷物的颗粒，以获得粗面粉；把它们磨碎，这样就得到了面粉。这种模式证明了一种烹饪习惯，这种习惯在东汉的华北地区，特别是在城市中逐渐传播开来，这包括越来越频繁地准备以小麦粉为基础的食物（面食，馅三明治等）。', '公元1至2世纪。', '', '米色兵马俑，橄榄绿釉面', 'https://www.maotorino.it/media/opere/Cc71.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4919', '国立东方艺术博物馆（都灵）', '演员组', '(Cc/29a-d)', 'h. max. 25，1 cm', '演员们以各种模仿的姿态，穿着下到膝盖的长袍，将前襟翼折叠并插入低黑色腰带中。飘动的长袖遮住了手，这是表演服装的典型特征。在束腰外衣下面，他们穿着白色的管状裤子，它们与尖头和低胸的鞋子结合在一起，鞋面上装饰着小的雕刻部分。前景中的矮胖子以戏剧性的姿态张开双手，张开嘴宣告。其他三个演员似乎在有节奏地标记小矮人的表演。', '公元8世纪中叶。', '', '米黄色的陶土，带有灰色的外壳和彩色颜料，', 'https://www.maotorino.it/media/opere/Cc29b.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4920', '国立东方艺术博物馆（都灵）', '跪着的人', '(Cc/68.D)', '56，4 cm x 32，3 cm', '该人物跪着，双臂垂直于身体，但以提供姿势向前倾斜。手和头是可拆卸的，通过别针与身体相连。头，造型甜美，是一个浓眉大眼的年轻人的头，梳着中间的折痕和扁平的发髻。该男子在右侧穿着各种重叠和交叉的衣服，形成了一系列非常厚的“V”形领口。白色的外衣使人物看起来苗条，覆盖膝盖，并落在花冠上，形成雕像的底座。它的腰部系着一条红色的腰带，它的长边落在前面。', '公元前2世纪。', '', '灰色陶土，白色陶土和彩色颜料', 'https://www.maotorino.it/media/opere/Cc68.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4921', '国立东方艺术博物馆（都灵）', '方特', '(Cc/61.D)', '44，7 cm x 16，8 cm', '这名士兵是四人小组的一员，其中三人几乎可以肯定来自一个单一的葬礼背景。造型很简单，但冷色运用得很有品味，特别是画得很仔细。这个人被描绘成站着，双腿微微分开，左臂沿着身体伸展，右手抬起到肘部的高度：左手可能拿着一个垫子盾牌。易腐的伊利亚尔，右边是一把武器，也许是一把青铜长矛，类似于他仍然插在手孔里的那把。头在肩膀上微微凹陷。头后卷曲的头发被一顶帽子遮住，额头上有黑边和红边；这反过来又被覆盖了，在后半部分，是一顶长方形的棕色帽子，必须用领口固定。尽管它散发出一种天真的魅力，但它是一件品质卓越的作品。', '公元前2世纪。', '', '灰色陶土，带有白色外壳和彩色颜料', 'https://www.maotorino.it/media/opere/Cc61.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4922', '国立东方艺术博物馆（都灵）', '罗汉坐在阿尔达帕良卡', '(Cs/6)', 'ca. 111 cm x 69 cm', '罗汉（梵语中的罗汉）一词它根据佛教教义确定了完全实现解放目标的“圣人”。这个角色坐着，左腿伸开，右腿折叠在一个没有保存的座位上。左臂伸向相应的腿，手向内弯曲，手指向内，手掌向下。罗汉凝视着，嘴巴张开，在惊讶和愤怒之间表现出一种奇怪的表情。', '公元14世纪。', '', '木材和漆器，多色痕迹', 'https://www.maotorino.it/media/opere/Cs06.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4923', '国立东方艺术博物馆（都灵）', '饮料服务盒', '(Cl/5.D)', '17，4 cm x 49，6 cm', '盒子由一个长方形的盒子组成，盒子的短边有两个狮子的原型，和一个紧随其轮廓的厚实的盖子。这两个组件中的每一个都是由一块实木制成的。该物体完全被黑色漆（倾向于棕色）覆盖。，这也影响了容器的平底：然后将容器的内部和盖子均匀地涂上红色。外部装饰雕刻或涂成朱砂红，或两种技术的混合。相反，在盖子的末端，两个怪物头部的椭圆形红色眼睛脱颖而出，尖尖的耳朵压在四个侧面：切口是几何浮雕。描述猫科动物身体的装饰出现在4世纪中叶之前，并且是公元前3世纪楚漆器生产的典型代表。C。', '公元前4世纪下半叶。', '', '黑色和红色漆木', 'https://www.maotorino.it/media/opere/Cl05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4924', '国立东方艺术博物馆（都灵）', '披针叶背景上的佛像和两个菩萨', '(Cs/3.D)', '14，7 cm x 6，8 cm', '雕塑描绘了一尊高浮雕的佛陀，两侧是两尊菩萨，在背景中，披针形的叶片设置在四个支撑的底座上。融合通常不准确，尤其是图形非常粗略。佛陀僵硬地站着，右手在abhayamudra，左手在varadamudra，穿着厚重的长袍，头上戴着笨重的乌什尼莎（rouji）。在它的后面刻有尖尖的光环和身体明亮的光晕的轮廓。在叶子表面的其余部分上绘制了许多波浪线，这些波浪线以对称的方式向上辐射以表示火焰的舌头。佛陀赤脚站立在一个小的半球形底座上，两个侧枝从该底座上分开。在这些树枝的末端，有两个菩萨，比佛陀小得多，双手合十地站着。', '公元6世纪末。', '', '琥珀色铜绿', 'https://www.maotorino.it/media/opere/Cs03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4925', '国立东方艺术博物馆（都灵）', '带有怪诞人物的大花瓶', '(Cv/63.D)', 'h. 74 cm x 49，9 cm', '这个大肚子的罐子向平底收缩，平底相当窄，没有脚。容器的肩部和腹部之间的通道由一个轻微的边缘突出。脖子很短，下摆向外折叠。这件作品的独创性在于它的扁平联锁盖，上面是一个蓬松而怪诞的男人的空心身影。人物靠在盖子上，大屁股靠在盖子上，抬起他的短腿，把头向后弯曲成一个鬼脸。这名男子戴着一顶宽而深的帽子，帽子的尖端向四周凸起，中间部分呈圆形。', '公元二世纪。', '', '棕灰色陶土', 'https://www.maotorino.it/media/opere/cv63.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4926', '国立东方艺术博物馆（都灵）', '带有长嘴的谷物酒“Jue”花瓶', '(Cb/3.D)', '17，9 cm x 14，4 cm', '青铜礼器（爵），用于盛装和饮用，甚至加热谷物酒。爵的腹壁几乎是垂直的，再加上没有重要的浮雕装饰，它们使物体具有细长的外观。从边缘突出的柱子呈半球形截面，并以乳房形状的旋钮为主。花瓶的装饰以三条平行线为限，略有浮雕，它们围绕着腹部的上部：它们略呈拱形，因此占据了容器的上部轮廓。脚在末端部分又长又尖。从文体学的角度来看，这个爵被认为是商代晚期的产物。', '公元前12至11世纪。', '', '铸模青铜', 'https://www.maotorino.it/media/opere/Cb03.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4927', '国立东方艺术博物馆（都灵）', '龙头井模型', '(Cc/75.D)', '32，1 cm x 15，2 cm', '井由一个宽海岸的圆形截面护栏和一个由四坡屋顶覆盖的滑轮结构组成。。屋顶的瓷砖是有特色的，滑轮的外壳不是。高支撑框架由两根横梁支撑在井岸上，向一根短的水平横梁汇聚而成。它的两端向下延伸成龙头状，下颚有门牙，而不是最常见的尖牙。在岸边，在其中一根垂直梁旁边，并对应于一个小的造型缺陷，使用了一个没有手柄的双耳瓶。栏杆的轮廓略微弯曲，底部和顶部更宽：它可能是在车床上成型的，正如形状本身和平底可见的圆形线条所暗示的那样。这个物体被一个不断变化的橱窗不停地覆盖着。', '公元1至2世纪。', '', '米粉色陶土，黄绿色釉面。', 'https://www.maotorino.it/media/opere/Cc75.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4928', '国立东方艺术博物馆（都灵）', '带方形侧板的井模型', '(Cc/76.D)', '23，6 cm x 19 cm', '开口下方喉部狭窄的圆形井：其形状在所有方面都是带有细凹槽的圆柱形腹罐的形状。n高和低，宽而厚的平边；在底部可以看到车床的线条。在井口上方，有一个模仿四个交叉轴的方形河岸，支撑滑轮的报摊，上面有两个倾斜的屋顶。在两个倾斜的支撑梁之一旁边，在井的圆形边缘上安装了一个没有耳塞的双耳瓶。玻璃在壶形的井上呈虹彩绿色，顶部呈棕色（侧面、滑轮和双耳瓶）。', '公元1世纪（？', '', '红色兵马俑，绿色和琥珀色釉面', 'https://www.maotorino.it/media/opere/Cc76.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4929', '国立东方艺术博物馆（都灵）', '“ DING ”三脚架花瓶，用于提供带有圆柱形脚的肉类。', '(Cb/5.D)', 'h. 18，5 cm x 17，9 cm', '用于烹饪和祭祀肉类的仪式容器（通常是青铜的，但也用陶土制成）。身体呈深吻的开放形状，靠在三只脚上。三脚架具有朴素而简单的形状和装饰，反映了9世纪中叶的典型味道。身体向下展开，不是圆形的，而是倾向于三角形的，角与花瓶的脚相对应。它们几乎是圆柱形的，内部略微变平，并具有平坦的支撑。垂直的耳钉直接插在向外的边缘上，突出约一厘米。一条相当平坦的装饰带占据了下摆下的上半部分：六条程式化的d形龙的序列。躺着的“S”，每个都写在一个方框中，成对对称地面对。', '公元前9世纪左右', '', '铸模青铜', 'https://www.maotorino.it/media/opere/Cb05.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4930', '国立东方艺术博物馆（都灵）', '带有圆形三角形接头的斧头', '(Cb/66.D)', '11 cm x 12，4 cm', '三角形截面的嫁接，带有圆角和直边和不规则边的开口。因此，物体的主体在一侧完全平坦，在另一侧呈三角形。圆面逐渐向刀片倾斜，刀片与移植物无缝连接。移植物的上部平坦部分在融合过程中具有电缆痕迹的装饰，并且部分被删除。仍然可以从侧面看到马头，眼睛，耳朵和鬃毛清晰可见；再往下，画继续有两条平行线，似乎描绘了动物的左前爪。一旦组装好，该标本可能必须水平固定在手柄上，并使电线与地面平行：这是唯一的位置c他使雕刻的图案可见。', '公元前三至一世纪。', '', '铜绿-黑色和琥珀色-黄色', 'https://www.maotorino.it/media/opere/Cb66.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4931', '国立东方艺术博物馆（都灵）', '守护坟墓的独角兽动物', '(Cl/14.D)', '16，2 cm x 39，6 cm', '描绘独角兽动物的小雕像经常出现在汉墓中，以青铜、兵马俑或木材为模型。他们必须保卫死者和坟墓免受恶魔的攻击，并且总是被描绘在冲锋中。我们的标本具有马的特征，完全符合这一时期的典型趋势。身体和头部是由一块粗糙的木头制成的，上面提到的细节：除了动物的眼睛和鼻孔外，还突出了涂成红色的阴茎和睾丸。雕塑的多面表面涂有相当宽的黑色、白色和红色颜料的笔触和线条。角和尾巴是可拆卸的，是在头部和臀部的特殊凹槽中插入的薄木板。', '公元1至2世纪。', '', '涂漆木材', 'https://www.maotorino.it/media/opere/Cl14.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4932', '国立东方艺术博物馆（都灵）', '铠甲勇士', '(Cc/84)', 'h. 40，2 cm', '战士被描绘成站立的静态姿势，大脚并拢，小臂紧握：右手举在胸骨下，左侧靠近相应的臀部。右手有一个通孔，最初可能在该孔中插入了武器。巨大的圆头突出了整体的不成比例感，顶部是带有长矩形耳罩的圆顶头盔。然后，头上戴着一顶风帽型的帽子（“风帽”），它沿着肩膀向下分成三个圆形的襟翼。面部的细节是模糊的-浓密的眉毛在弯曲的额头下皱起，大眼睛和明显的眼睑，迷彩的鼻子，小嘴巴-和绘画：尤其要注意浓密的黑胡子。', '七世纪。公元。', '', '透明陶土，釉料和颜料', 'https://www.maotorino.it/media/opere/Cc84.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4933', '国立东方艺术博物馆（都灵）', '农民-方特', '(Cc/63.D)', '112，5 cm x 36 cm', '人站着的姿势，粗壮的腿稍微分开，小臂不与身体分离：左臂与手一起沿侧面伸展。它拿着一种没有手柄的桨状筛子；右手折叠在胸前，握住铲子长柄的末端，放在地上，靠近右腿。农兵塑像在汉朝晚期的随葬品中很常见，当富裕的四川地主变得更加独立于中央政府，并感到从严格的环境协议中解脱出来时。或葬礼。这些人物代表了死者庄园的农民，他们在必要时，他们还履行了武装民兵的职能。', '二世纪下半叶。公元。', '', '用彩色颜料绘制的红色陶土', 'https://www.maotorino.it/media/opere/Cc63.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4934', '国立东方艺术博物馆（都灵）', '直柄平柄匕首', '(Cb/52.D)', 'l. 22，3 cm', '这把匕首的披针形刀刃在中间较厚，向螺纹方向横向变薄。它横向连接到一个装饰有圆点的小长方形手柄上。相反，压碎的手柄带有浮雕装饰，模仿了五次交叉的绳索。在扁平的末端，手柄在用于悬挂匕首的中央通孔的侧面变宽。该终端元件的厚度与手柄和刀柄相同，略大于刀片的最大厚度。这件作品可以与位于北京和辽宁地区之间的一些遗址标本进行比较。', '公元前8至7世纪。', '', '棕绿色青铜带紫色斑点', 'https://www.maotorino.it/media/opere/Cb52.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4935', '国立东方艺术博物馆（都灵）', '配置椭圆形环的“Xiao”型刀', '(Cb/69.D)', 'l. 28，5 cm', '扁刀，很长很细，刀刃和刀柄是一体的。手柄的末端呈龙头状：一个弯曲的椭圆形钩子从生物的头部开始，以狭窄的蜗壳结束。在他张开的下巴之间。手柄有斜角，作为龙脖子的延续，然后继续直插叶片，长度约为其长度的两倍。刀的锋利的线在刀片的另一侧，从一个到另一个的通道由台阶突出。这种相对于手柄的加宽在刀片的整个长度上一直持续到尖端：在它附近，背面也在两个面上都是斜面的。', '公元前二至一世纪。', '', '琥珀色铜绿青铜', 'https://www.maotorino.it/media/opere/Cb69.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4936', '国立东方艺术博物馆（都灵）', '带有“TAOTIE”的“GE”斧头匕首刀片', '(Cb/42.D)', '8，4 cm x 25 cm', '均匀覆盖在这把匕首上的绿色铜绿，两面都是一样的，摸起来很香。窄三角形叶片相对于其纵轴不完全对称，它以非常明显的中间肋骨为标志。脚后跟的底部也有凸起，而这条细肋将它与后面的矩形茎分开，中间有一个圆形通孔。装饰占据了刀刃与脚跟相邻的部分，并以一个穿过中间肋骨的圆孔为中心：这是雕刻的面具，让人联想到商的道铁面具，眼睛凸起。这些都是方形的，瞳孔像安阳相的一些花瓶一样凹陷。两个小的矩形通孔，朝向鞋跟的角，它们完善了开口系统，从而可以操纵武器。', '公元前9至5世纪。', '', '水绿铜绿青铜', 'https://www.maotorino.it/media/opere/Cb42.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4937', '国立东方艺术博物馆（都灵）', '“真木寿”，人类面部坟墓的保护生物', '(Cc/10)', 'h. 47，2 cm', '真目寿是正面僵硬的姿势蹲着。它有一个大耳朵和高角的人头，侧鳍垂直嫁接在肩膀上。长方形的瘦脸有西方的特征，在粉红色的背景上以黑色和红色突出，眼睛充满了血液，长长的睫毛，浓密的皱眉，蕾丝，很长的胡子和鬓角。头的先端缩小成一个高的波状圆锥形的角；耳朵大，呈扇形，呈三棱形。头靠在不成比例的长脖子上。该生物的腹部平坦，前腿略微分开，并以牛蹄结束。与大多数体型较小的真目兽不同，这种标本也有蹄形的后腿。特别有趣的是画在树干和翅膀上的装饰，由浅绿色和绿松石色的带有红色边缘的浅叶蜗壳组成。', '公元8世纪初。', '', '米黄色陶土，浅色釉料和颜料', 'https://www.maotorino.it/media/opere/Cc10.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4938', '国立东方艺术博物馆（都灵）', '“博善路”香水燃烧器', '(Cv/77a.D)', '', '香炉的杯子，又深又大，插在一根弯曲的短茎上，茎靠在一个中空的圆顶脚上，它在凸起的底部变宽。脚的圆顶周围装饰着一条龙，在波浪中间有轻微的浮雕，由曲线暗示。高的锥形盖子围绕着杯子的凸起边缘：穿孔，并在支架上略有突出，它描绘了在圆山山峰的三个层次上移动的动物形态生物。每一层由四个山峰组成，每个山峰都有一个动物的形象，总共有十二个人物，两个两个地面对着。', '公元前1世纪-公元1世纪。', '', '深灰色陶土', 'https://www.maotorino.it/media/opere/Cv77a.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4939', '国立东方艺术博物馆（都灵）', '两瓣花瓶和流线型的肚子', '(Cn/32)', '13，3 cm x 13 cm', '这个罐子完全被一个透明的盖子覆盖着，几乎掩盖不了粗糙和不规则的表面，没有装饰。它有一个相当粗壮的形状。脖子很宽，相当高，弯曲，下摆扩大。嫁接在上面的高身体有一个狭窄的肩膀和一个略微凸起的形状。当它达到最大直径时，它会突然改变方向并在狭窄的底部闭合。这种方向的改变导致了容器的棱角分明的轮廓。两个低拱的带状环将边缘与花瓶的肩部截然相反地连接起来。', '约公元前2300-1700年。', '', '覆盖着米色外套的红色陶土', 'https://www.maotorino.it/media/opere/Cn32.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4940', '国立东方艺术博物馆（都灵）', 'VITARKAMUDRA INVERSA的ASSISO菩萨', '(Cs/7)', 'ca. 96 cm x 60 cm', '这座雕像可能与另一座雕像（Cs/8）形成一对，仅在手臂的镜面位置上有所不同。菩萨盘腿坐在双莲座上，双手摆出“辩手势”（vitarkamudra）。身材看起来很匀称，手很细，椭圆形的脸表现出坚定。头部被一个尖尖的高头饰包围，用植物树枝装饰。一条带有中央吊坠的沉重项链在裸露的胸部脱颖而出。中式僧袍披在两肩，垂在地上，软绵绵的。内袍由系着蝴蝶结的带子固定。', '公元16世纪。', '', '木材和漆器，多色痕迹', 'https://www.maotorino.it/media/opere/Cs07.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4941', '国立东方艺术博物馆（都灵）', '带有两个龙形环和“三彩”玻璃的安福拉', '(Cv/4)', 'h. 33，1 cm x 17，4 cm', '双耳瓶有一个球状的身体，上面提到的扁平足。脖子又高又细，中间有三个凹槽。下摆是厚唇的杯子形状。两个对称的龙形手柄从嘴向肩膀倾斜，咬住杯子的边缘。在动物的头部应用了长耳朵、卷角和披针形脊的细节。沿着手柄的附件往下走，在背面有三个凸起的按钮。在双耳瓶的肩部与腹部相连的地方，有三条略微雕刻的线条，另一条凹槽围绕着腹部中间的花瓶旋转。玻璃沿着墙壁流到腹部的中央。主要颜色是白色，绿色和琥珀色。', '7世纪末-公元8世纪初。', '', '三才釉白陶土', 'https://www.maotorino.it/media/opere/Cv04.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4942', '国立东方艺术博物馆（都灵）', '椭圆形杯子，带切成半圆的环', '(Cl/1.D)', '6，3 cm x 18，2 cm', '该容器具有典型的椭圆形碗的形状和尺寸，称为“有翼”（yushang）。，通常用于饮用酒精饮料，它们的名字来源于两个狭窄的水平手柄，它们对称地放置在与下摆相对应的长边上。它有薄的弯曲的木墙，向平坦的底部变厚。实木耳不像其他标本那样是圆形的，而是在中间有一个半圆形的凹槽。漆覆盖了物体的整个灵魂，黑色（深棕色）明显占主导地位。在外面，在环上，沿着下摆的内带。朱砂红漆为杯子内部的其余部分着色，并在黑色背景上用抽象设计装饰其手柄和边缘。在外面，我们发现了一个简单的稀疏标点符号。', '公元前3世纪', '', '涂有红色和黑色的漆木', 'https://www.maotorino.it/media/opere/Cl01.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4943', '国立东方艺术博物馆（都灵）', '带尖嘴的小三脚架“GUI”', '(Cn/23)', '17，7 cm x 13，4 cm', '这种精致的gui代表了大汶口最后阶段白陶中的一种罕见类型。三个乳房形状的脚被应用于宽阔而扁平的身体。肩部下方的细浮雕连接线是容器的唯一装饰元素。大的手柄环连接肩膀和后脚，与脖子位于同一轴线上。这是一个杯状的，有一个外缘和一个固定的前嘴：它被嫁接到容器的上部，相对于两个前脚稍微向前且居中。', '约公元前2900-2400年', '', '白陶', 'https://www.maotorino.it/media/opere/Cn23.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4944', '国立东方艺术博物馆（都灵）', '带腰带的宫廷女士', '(Cc/15)', 'h. 40，6 cm', '雕像描绘了一位站在左腿上的胖宫女，她的右腿向侧面伸展。他双手合十放在胸前，藏在宽大的袖子下面。脸是圆的，嘴巴涂成红色，眼睛涂成黑色；大发型，也是黑色的，它是duomaji类型的（“从马背上掉下来的发髻”，在8世纪中叶流行的故意弄乱的发型）。他穿着一件长到脚的束腰外衣，上面有很深的褶皱，两侧开放，并配有一条黑色腰带，可将其保持在臀部下方。长袍的肩部和袖子上涂有淡绿色，腹部以下为粉红色。从裙子里漏出来的鞋子很大，被涂成了红色。', '公元8世纪（？', '', '赤陶，白釉和颜料', 'https://www.maotorino.it/media/opere/cc15.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4945', '国立东方艺术博物馆（都灵）', '带“ V ”领口的宫女', '(Cc/16)', 'h. 36，7 cm', '身体沉重的小雕像，代表一个丰满但轮廓细长的女人，正面站立但不僵硬，稍微向左倾斜，双手合十放在胸前，被长袖遮住。他有一张椭圆形的脸，嘴巴涂成红色，眼睛在粉红色的背景上涂成黑色。椭圆形被高发髻所占据和突出，被涂成黑色，被称为高基。这位女士的身体被包裹在一件宽大的束腰外衣中，领口为“V”形，长到脚，并带有深深的雕刻线条。这件长袍的肩膀上涂着红色——也许是为了暗示披肩的存在——袖子下面是白色的。从裙子下面露出鞋尖，鞋尖有很高的浅裂装饰。', '公元8世纪下半叶（？', '', '橙色陶土，白色陶土和颜料', 'https://www.maotorino.it/media/opere/Cc16.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4946', '国立东方艺术博物馆（都灵）', '“HU”两环玻璃花瓶', '(Cv/66.D)', 'h. 44，8 cm，x 38，4 cm', '这个球形罐子形状的罐子向底部闭合，没有脚，支撑略微凹入；它有一个弯曲的高领，下摆是喇叭形的。在肩膀和上腹部之间有两个垂直的小耳朵，它们截然相反。两者都被一条条纹纵向分成两半，并在对角线上有密集的平行切口，它们汇聚在一起，仿佛唤起了一把编织的线：在底部，它们有一个凸起的扭曲环，并由两个小元素加冕。扭曲成公羊角的形状。花瓶的表面相当粗糙；简单的装饰元素只存在于上半部分。', '公元前1世纪；公元1世纪前25年。', '', '绿色釉面的灰色面团陶土', 'https://www.maotorino.it/media/opere/Cv66.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4947', '国立东方艺术博物馆（都灵）', '听证官员', '(Cc/90)', '60，5 cm x 16，6 cm x 14，3 cm', '雕像明显向右倾斜。这位政要被描绘成观众的姿势，僵硬地站着，双手交叉放在肚子上。他戴着官方的“山脊型”头饰（亮冠），并穿着经典的宫廷鞋，鞋尖抬高。他有一张轮廓分明的脸，直着鼻子，画着细节：眉毛和黑色的眼睛，严厉的目光，稀疏的胡须和胡须，嘴巴涂成红色。在一件长到地面的圆领衬裙上，她穿着一件半大腿的束腰外衣，在右边交叉成“ V ”形，袖子又长又宽。这件衣服的正面和背面由两块由半刚性材料制成的矩形保护板封闭：一种用于保险的背腹围裙。用皮带绑在胸前。', '七世纪下半叶。公元。', '', '透明陶土，釉料和颜料', 'https://www.maotorino.it/media/opere/Cc90.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4948', '国立东方艺术博物馆（都灵）', '剑和鞘', '(Cb/55.D)', 'l. 48，4 cm', '这把剑由一个圆锥形的手柄组成，里面是空心的，并由带有中央肋骨和锋利切口的大型双翼刀片制成。由于清洁和锐化，在其表面上可以看到许多条纹。剑柄几乎不存在：从剑刃到剑柄的通道只有一条细的浮雕线。手柄的旋钮非常喇叭形和扁平，呈圆形。在里面，您可以看到兵马俑的残留物。这把剑被插在一个由两根非常薄的木条组成的鞘中，用丝线把它紧紧地缠绕在一起。鞘上终于铺上了一层又一层深色的漆，使其表面光亮。这把剑的形状属于楚国两个经典模型中最古老的（6世纪末）。公元前。', '公元前6世纪末至4世纪。', '', '棕色铜绿青铜和金黄色刀片，漆木皮鞘', 'https://www.maotorino.it/media/opere/Cb55.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4949', '国立东方艺术博物馆（都灵）', '带有缎带和流苏装饰的马', '(Cc/52.D)', '25，9 cm x 32，4 cm', '这种类型的马由可移动的骑手骑乘，部分由未保存的易腐物品组成。我们的标本有长而公羊的鼻子，细长的脖子和短切的鬃毛，这是典型的汉马。嘴巴半张，青蛙被切开，大圆眼睛突出。外部完全覆盖着白色的外套，上面冷铺着柔软的颜料。这个标本的标志是浅裂的鞍座，长长的披针形流苏，脖子上飘动的丝带，以及-通常-白色背景上紫色和橙色的优雅组合。这些小雕像的年代很难确定，还考虑到在官方发掘过程中发现的类似作品很少，而这些作品一直是科学出版物的主题。', '公元前一世纪初。', '', '灰色陶土，白色陶土和彩色颜料', 'https://www.maotorino.it/media/opere/Cc52.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4950', '国立东方艺术博物馆（都灵）', '带穿孔底座的“ GANG ”殡仪馆', '(Cn/14)', '40，1 cm x 28 cm', '容器的类型是典型的河南中西部阎村葬瓮。在这种情况下，厚壁是倾斜和不规则的，下摆在内部略微折叠，并由涂成橙色的粘土棒加厚。紧随其后的是大约高的密集水平条纹。4厘米，底部被一条涂有油漆的带子封闭。在如此限定的容器的上部，以规则的间隔施加两个朝下的钩子和一个小的环形环。钩子可以固定盖子。容器的表面被划出了斜线。画有孤立的鱼的人物的装饰，鸟类和几何图案不是真实的，是最近才添加的。', '约公元前4000-3500年', '', '颜料米色陶土', 'https://www.maotorino.it/media/opere/Cn14.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4951', '国立东方艺术博物馆（都灵）', '大蒜头末端弯曲颈瓶', '(Cb/14.D)', 'h. 27，7 cm x 19 cm', '一种罕见的豆瓶，通常是青铜的，用来装谷物酒，脖子长，肚子大-脖子长而弯曲，末端是闭合的六瓣“大蒜”头。瓶子的开口位于颈部的最高点：它由一个短的凸起的边缘组成，最初装有盖子。这个想法被汉族陶艺家用来制作动物形的陶器。颈部的最后一部分，从开口到“蒜头”，是全金属的。沿着瓶身往下走，有一个凸缘，由三个水平的凸缘组成，凸缘位于瓶颈上。腹部呈球形，末端有一个高的喇叭形环状脚。豆底是平的。', '公元前三世纪末至公元前二世纪初。', '', '铸造青铜（在模具中？', 'https://www.maotorino.it/media/opere/Cb14.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4952', '国立东方艺术博物馆（都灵）', '双环高颈花瓶', '(Cn/21)', 'h. 16，1 cm x 15，3 cm', '这个花瓶是新店陶瓷的特色。脱脂良好的面团含有云母和白色颗粒；凹槽部分隐藏了织物的垂直痕迹，几乎遍布容器的整个外表面，但尤其在底部可见。容器有一个又高又宽的弯曲的颈部，边缘扩大，嫁接在一个稍微凸起的肩膀上，达到最大直径后，它逐渐向底部闭合。这是凹凸不平和不平衡的，这是一个典型的特征，再加上容器底部的烟灰痕迹，新店生产。涂成黑色的装饰不是很准确。', '约公元前1200-950年', '', '米色陶土，浅外套上有黑色装饰', 'https://www.maotorino.it/media/opere/Cn21.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4953', '国立东方艺术博物馆（都灵）', 'BACILE A DUE ANSE', '(Cn/17)', 'h. 13，8 cm x 25，3 cm', '精心制作的容器，由经过良好净化的面团制成：外部抛光，在马家窑的生产中，它的内部几乎总是很粗糙。它的形状像一个高盆，边缘宽而平。肩膀扩大到腹部的最大直径，然后向底部闭合。两个截然相反的环形环，有点粗糙，应用于腹部中部的斜角下方。这样的龙骨状在传统的马家窑杆菌中是相当罕见的。涂成黑色的装饰集中在容器的唇部和耳孔上方的外壁上。第一个装饰有同心和间隔的圆弧，它们之间的空间覆盖着交叉的格子线。同样的图案装饰了整个盆地的顶部。', '约公元前3350-2650年。', '', '涂成黑色的橙色陶土', 'https://www.maotorino.it/media/opere/Cn17.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4954', '国立东方艺术博物馆（都灵）', '带环形末端的弯刀', '(Cb/51.D)', 'l. 28，1 cm', '这把刀明显弯曲的形状是由刀柄和刀刃之间略微错位的接合处给出的。手柄具有向上锥形的梯形截面：最窄的一侧是与刀片线相对应的一侧，而与背部相对应的那个稍微宽一些。在手柄的末端插入一个与刀片平行的宽椭圆形环。空心手柄，其表面似乎覆盖着一层薄薄的金和一层银，它由三排又高又窄的穿孔三角形装饰。最长的两条边每排有三个三角形，最短的两条边只有一个。这种带孔刀柄和环的刀对我们来说是陌生的。金银制品的存在表明它不是公元前4世纪之前的作品。，因为正是在这个时代，这种类型的第一批作品出现在中国的青铜表面。', '公元前4至3世纪。', '', '镀金和镀银青铜，抛光刀片', 'https://www.maotorino.it/media/opere/Cb51.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4955', '国立东方艺术博物馆（都灵）', '马弓箭手', '(Cc/26)', '39，7 cm x 37，4 cm', '骑马的弓箭手雕像，线条优雅。这个人是用彩色颜料冷画的，其中棕色和粉红色在尖头上占主导地位，在他的长袍和背上的箭袋上。双手紧握，可能握着易腐材料制成的缰绳：或者，右手握着缰绳，左手握着缰绳，再往下，拱门。衣服的袖子向后拉伸，看起来像是被风吹走了。骑士的脸是白色的，嘴唇涂成了鲜艳的红色。这匹马有一个巨大的身体和一个精细的头部。在四足动物的泥塑雕像中几乎总是可以看到马的腹部是中空的。', '公元8世纪上半叶。', '', '清面团陶土、三彩釉料、釉料和颜料。', 'https://www.maotorino.it/media/opere/Cc26.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4956', '国立东方艺术博物馆（都灵）', '坐着的“罗汉”', '(Cc/42)', 'h. 23，8 cm', '这个和平的角色平静地坐在高脚椅上，左腿向胸部弯曲。左前臂放在膝盖上，右前臂放在相应的大腿上。双手放松：右手触摸僧侣长袍上浮雕的念珠。他的头完全剃光了，脸颊饱满，下巴的褶皱很明显，耳朵很大，耳垂很长。他在宽阔的眉弓下半闭着眼睛，小嘴露出微笑。罗汉在右边穿着一件又长又宽的交叉僧袍，有凸缘和宽大的布褶。左肩披着佛教僧袍（甲社）。面部的形状，尤其是鼻子的形状，是10世纪典型的描绘，它在较小程度上幸存下来，并在宋画中有一些变化。', '公元10世纪或更晚', '', '浅色面团陶土，白釉，颜料和烫金', 'https://www.maotorino.it/media/opere/Cc42.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4957', '国立东方艺术博物馆（都灵）', 'PUNTA DI LANCIA“MAO”', '(Cb/45.D)', '24，8 cm x 4 cm', '带有直边管状接头的菱形截面的矛头（毛）。在底部，刀片在其最宽的部分形成两个有角度的突起。金属的琥珀色突出了斜线和检测到的中心肋，它在物体其余部分的黑色铜绿上脱落。此外，刀片的中央部分，她接受了一种特殊的治疗，使她看起来有斑点：这些是银色背景上的小黑点。在管状移植物的一侧，位于中心，一个带有通孔的小三角形突起，可能是将尖端固定在杆上的功能原因。构思这件作品的工匠们所追求的琥珀色、黑色和斑驳的铜绿，似乎并没有因为时间而发生变化。', '公元前3世纪', '', '黑色和琥珀色铜绿', 'https://www.maotorino.it/media/opere/2096_Cb45.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4958', '国立东方艺术博物馆（都灵）', '花瓶或漆盒垫圈', '(Cb/24.D)', '11，3 cm', '这种环形垫圈，具有“U”形截面，它构成了漆木圆柱形容器的边缘：它仍然保留了内部的纤维残留物。因此，垫圈的功能还在于加固，而不仅仅是装饰由此获得的物体。装饰是用冷镶嵌银制成的，它用两种不同的几何设计覆盖了垫圈的整个上圆周和外圆周。在上面，卷曲的蜗壳从较厚的导轨中垂直伸出，这些导轨沿着带状疱疹的外部和内部边界交替出现。沿着外周，银色镶嵌物将空间划分为长方形的寄存器，在每个螺旋中插入两个相对的螺旋卷发。这种类型的装饰出现在公元前3世纪初，但在本世纪下半叶变得普遍。', '约公元前三世纪下半叶。', '', '琥珀色铜绿，银色镶嵌', 'https://www.maotorino.it/media/opere/2040_Cb24.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4959', '国立东方艺术博物馆（都灵）', '硬币轴支架', '(Cc/45.D)', '54，1 cm x 31，2 cm x 24，8', '这是一棵硬币树（摇钱树或竹竹）的支架。，是装饰多样性和执行自由的杰出典范。它具有椭圆形的钟形形状，带有两个水平的浅浮雕寄存器，它的顶部是一个圆形的图形，该图形支撑着插入树干的管状移植物。管子从一只有翅膀的山羊的臀部开始，被抱在一个角色的怀里，这个角色骑着它，头向左转。该角色可被识别为不朽人物，其特征是指动物世界的元素：大耳朵，短裙和短羽毛斗篷。有翼的山羊也是仙人的坐骑之一，这里描绘的是长胡子的动态姿势，高高的中央角和笔直的尾巴。', '公元三世纪初。', '', '带有红色颜料痕迹的赤陶', 'https://www.maotorino.it/media/opere/1413_Cc45.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4960', '国立东方艺术博物馆（都灵）', '三只脚上的小容器“脚豆”', '(Cb/19.D)', '27，1 cm x 37 cm', '小的青铜容器，用于加热液体或准备汤剂。这个饺子是由一个底部略凹，边缘非常喇叭形的杯子组成，其唯一的装饰由三条平行且紧密的线条组成，刻在腹部的中间。它位于三个长而细的脚上，略微向外弯曲，末端呈蹄形。每个蹄子仅位于尖端，并在内侧略微挖出。一个长的肘形手柄，连接到花瓶的主体和边缘，有两个不同的末端，它的另一端有一个圆形的龙头，并形成了锥形的脖子。所有这些特征使我们的饺子成为3世纪的典型标本。外部的黑色铜绿表明该容器确实长时间暴露在火焰中，以烹饪汤剂或其他东西。', '公元3世纪。', '', '青铜', 'https://www.maotorino.it/media/opere/1320_Cb19.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4961', '国立东方艺术博物馆（都灵）', '博维德', '(Cc/47.D)', '11 cm x 20，8 cm', '这只动物在大肚子的重量下显得弯曲，就像一头怀孕的母牛在努力站起来。前腿向前伸展，后腿弯曲在飞节上，头部和颈部用力向上推。长角和左侧的弓形尾巴也有助于运动的最终成功，其独特性最终在于身体从枪口尖端到后部形成的连续弧线。但是，从技术角度来看，这种可塑性并不对应，在准备粘土和绘制草图时要特别小心：模型看起来是多面的，没有完成，特别是在枪口上，除了左眼外，没有勾勒出身体特征。即使是不透明的橱窗，也是不规则地展开的。', '公元前1世纪-公元1世纪。', '', '赭绿色釉面红陶', 'https://www.maotorino.it/media/opere/1415_Cc47.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4962', '国立东方艺术博物馆（都灵）', '医学之佛', '(Cs/17)', 'h. 34，6 cm', '坐在帕德玛萨那，佛陀用左手的手掌，折叠在肚子前，拿着一个小药碗，而右手，她靠在腿上的varadamudra上，握住mirabolano的果实，保持食指和中指伸展。在裸露的胸部上，它带有Wan标志（梵文svastika），象征着吉祥。整个雕像似乎都是镀金的，尽管从微笑的表情来看，金色的痕迹在圆圆的脸上不那么明显。与相当精细的面部特征相比，它影响了耳垂的肉质。宽而低的前额上方排列着头发，按顺时针方向卷成螺旋状并压平：它们和谐地上升到ushnisha（rouji）。，这看起来像是头骨的简单延伸。一个光滑的半球形突起位于前额和头盖骨顶端之间。下面的木质底座是后加的。', '十六-十七世纪上半叶。', '', '镀金铜青铜', 'https://www.maotorino.it/media/opere/1796_Cs17.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4963', '国立东方艺术博物馆（都灵）', '琴手跪着', '(Cc/65.D)', '57，3 cm x 36，5 cm', '演奏者跪着，坐在脚跟上，双手放在水平放在膝盖上的乐器上。这是秦筝的地方变种，一个长的半空心圆柱体，带有两个前支架，用于将其正确放置在腿上。右手的拇指和食指合在一起，就像握着拨子或捏着一根绳子；相反，左手的拇指与其他手指保持一定距离，就好像要达成协议一样。这个男人面带微笑，很开心，他的头分别贴在大而不成比例的脖子上。他戴着一顶圆顶帽子，边缘坚硬且凸起，后部较高，并配有向上折叠的耳罩。外面的束腰外衣，在右边交叉，有宽大的蓬松的袖子。', '公元2世纪下半叶', '', '假面团灰色陶土', 'https://www.maotorino.it/media/opere/1421_Cc65.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4964', '国立东方艺术博物馆（都灵）', '带盖玻璃“DING”三脚架花瓶', '(Cv/70.D)', 'h. 20，1 cm x 20，2 cm', '许多汉族陶制礼器（鼎）模仿了最珍贵的青铜标本的形状。丁有一个扁平的球状身体，底部是圆形的，顶部有一个突出的水平肋骨。在此上方，有两个垂直的矩形环，略微向外弯曲。容器放置在三个喇叭形的动物脚上，垂直穿过印有轻微浮雕的拱形条纹。盖子是凸面的，完全用模具装饰在三个同心的寄存器上，上面有鱼，波浪形的设计，螺旋和螺旋让人联想到云和山，三角形加倍。', '公元1世纪前25年。', '', '绿色釉面的米红色陶土', 'https://www.maotorino.it/media/opere/1349_Cv70.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4965', '国立东方艺术博物馆（都灵）', '猫形装饰板', '(Cb/27.D)', '3，2 cm x 8 cm', '牌匾有轻微的浮雕，带有中空的内部和两个固定环，分别对应于动物的脖子和后腿的关节。这是一只猫科动物，侧面（右侧）描绘，腿弯曲，就像要跳起来一样。动物的尖尖的耳朵和嘴巴有一个很深的凹槽，眼睛有一个雕刻的圆圈，爪子有轻微的浮雕。长长的尾巴仍然与后腿上的融合毛刺相连。虽然刚刚提到了相貌，但猫科动物的力量是通过有力的轮廓和身体关节的造型来强调的。', '约。公元前5世纪。', '', '浅绿色铜绿镀银青铜', 'https://www.maotorino.it/media/opere/2042_Cb27.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4966', '国立东方艺术博物馆（都灵）', '带编织绳柄和护套的剑', '(Cb/56.D)', 'l. 55，8 cm', '这把剑由一个带有两个环形凸起的圆柱形手柄和一个带有中央肋骨和ta的长双翼刀片组成。非常锋利的。剑柄不厚，呈菱形截面，四边向剑刃略呈“V”形倾斜。手柄的旋钮呈圆形，外部呈喇叭形，内部呈凹形（漏斗形）。手柄仍然包裹着编织的丝绳，由几根线组成，略带弹性：最初的颜色是蓝色，由于氧化而变成棕色。剑被插在由两块雕刻的木板组成的剑鞘中。这些盘子被丝线粘合在一起，丝线把它们紧紧地包裹在一起，最后被一层又一层的深色漆覆盖着。在保存下来的地方，它的外观仍然非常明亮，并突出了丝线的密度。', '公元前4世纪。', '', '黑色铜绿青铜，木板护套，最初上漆', 'https://www.maotorino.it/media/opere/2109_Cb56.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4967', '国立东方艺术博物馆（都灵）', '长笛演奏者的形象', '(Cc/18e)', 'h. 19，9 cm', '这位年轻的女演员，是一个由另外四名女演员和一名舞者组成的团体的一员，它被描绘成盘腿坐在一个薄的方形底座上，用双手吹笛子。手指的形状和间距都很好，就像在移动一样。女孩的脸是圆的，脸颊上涂着粉红色的美丽，嘴巴上涂着红色，睫毛和眼睛都涂成黑色。额头上画着红色的花甸图案，代表一种“佛罗伦萨百合”。头发是黑色的，中间有分叉，梳成双髻（双髻）。这位女演员穿着低胸紧身胸衣，长袖，白色背心，宽裙和红色长围巾。', '七世纪下半叶，公元八世纪初。', '', '浅色面团和釉面陶土，颜料', 'https://www.maotorino.it/media/opere/1296_Cc18e.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4968', '国立东方艺术博物馆（都灵）', '有鬃毛和短鬃的马', '(Cc/56.D)', '110，7 cm x 38，6 cm x 101，5 cm', '这座大型雕像具有许多东汉末年马的典型特征。它以僵硬的姿势站在四条腿上，关节和结节都很突出，身体丰满，胸部相当细长。尾巴是直的，但不是直线的，末端打结成一个形状的球体，有两个截然相反的层流突起。头部被大力雕刻，嘴巴张开，嘴唇抬起以突出牙齿，舌头向左倾斜。汉式的鬃毛从尖尖的耳朵中间的一簇开始，其余的都被剪短了。马唯一的饰物是床头板，皮带由六个圆形凸纹螺柱连接，并带有难以辨认的图案。釉面完全覆盖了雕像，直到结的上方，就像划定了斗篷的斑纹一样。', '公元二世纪，公元三世纪初。', '', '赤陶，赭色釉面，带有深色内衬和绿色釉面修饰。', 'https://www.maotorino.it/media/opere/1426_Cc56.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4969', '国立东方艺术博物馆（都灵）', '带两个篮子的骆驼', '(Cc/31)', '24 cm x 24，1 cm', '这个优雅的骆驼雕像是由两个镜面模具拼接而成的。动物以静止的姿势放下爪子，抬起雕刻的小脑袋，眼睛活泼，耳朵小，前额上方的一簇毛发。长而结实的“新月形”脖子在喉咙上仍然有黑色颜料的痕迹。深挖的腹部，小而尖的驼峰，短而翘起的尾巴突出了身体后部的气势。总体而言，小雕像具有蜿蜒的轮廓。覆盖动物背部的布，带有用于通过驼峰的适当孔，仅通过在驼峰周围和沿两个装载篮的轮廓上刻有深刻线来建议。', '公元6世纪上半叶。', '', '灰色陶土，带有米色的凹槽和黑色颜料的痕迹', 'https://www.maotorino.it/media/opere/1279_Cc31.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4970', '国立东方艺术博物馆（都灵）', '方特', '(Cc/62.D)', '47，2 cm x 16，7 cm', '我们的士兵是五人小组的一员，几乎可以肯定来自一个葬礼背景。这个人被描绘成站着，双腿微微分开，左臂沿着身体伸展，右手抬起到肘部的高度：左手，穿孔，他可能拿着一个易腐材料的盾牌；右手，拇指与其他手指分开，拿着武器，也许是弓或矛。沿着束腰外衣的左边缘有一条白色的细线，在胸部形成一个蝴蝶结：一个细节，这个，与类似的标本相比，这使小雕像具有个性。这是通过步兵左肩上方和下方的胶带，理想情况下，它支撑着一个小的矩形箭袋，该箭袋被压在背部。像这样的步兵雕像是批量生产的，构成了军事编队，放置在坟墓附近的坟墓中。而且具有一定的重要性。', '公元前2世纪。', '', '灰色陶土和彩色颜料', 'https://www.maotorino.it/media/opere/1435_Cc62.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4971', '国立东方艺术博物馆（都灵）', '烤箱模型', '(Cc/78.D)', '14，5 cm x 13，1 cm x 20，7 cm', '烤箱的模型制作得很差，是空心的，没有底座。它的形状让人联想到船的半个倒置船体。相反，在半圆形截面的前部中央，几乎是方形的炉口打开。开口的侧面和上方刻有菱形网纹装饰的扇形。在顶部弯曲的平面上钻了两个对齐孔，在烹饪之前，应用了两个锅的模型。这种窑型在3世纪下半叶的下扬子地区的墓葬中非常常见。', '公元三世纪下半叶。', '', '浅灰色瓷器，配以橄榄绿毯子', 'https://www.maotorino.it/media/opere/2049_Cc78.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4972', '国立东方艺术博物馆（都灵）', '观音坐禅莲花', 'Cs/16', '59，7 cm x 31，8 cm x 33，2 cm', '菩萨在一个单独制作的高高的莲花宝座上沉思。双腿交叉在帕德玛萨那，双手合十，竖起大拇指互相抚摸。宽阔的圆形宝座由四排莲花花瓣组成，位于圆锥形底座上，并带有镂空装饰。面部集中的头部被沉重的头饰压得喘不过气来：一块对称折叠的大面纱，上面装饰着雕刻的花朵。中央位置的莲花宝座上装有阿弥陀佛的王冠。观音的脸（观世音）它是方形的，轮廓分明：宽阔的眉弓汇聚在直鼻上，大眼睛半睁，嘴巴匀称。脖子上有三个雕刻的褶皱，平坦的胸部装饰着一条精致的项链。雕塑上有金箔的痕迹（尤其在眼睑和手上可见）；脸上留下了黑漆的痕迹。', '明朝，15世纪末至16世纪上半叶', '', '铜质青铜，有黑漆和金箔的痕迹', 'https://www.maotorino.it/media/opere/Cs16.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4973', '国立东方艺术博物馆（都灵）', '坟墓之门', 'Cp/3a-3d.D', '154 cm x 165 cm ca.', '坟墓的入口由两个门框、一个门楣和两个门扇组成，正面装饰。表面在规则的颗粒状背景上有轻微的平坦和光滑的浮雕；一种橙红色的颜料，直接铺在石头上，均匀地覆盖了装饰元素。一些最小的细节被修饰成黑色，另一些则被雕刻。在各种板块上复制的饰带包括装饰有植物旋转的乐队，骑士人物，女性人物，动物形态的面具，展翅的鸟，龙和斗牛独角兽。', '东汉，公元二世纪初。', '', '灰色石头，部分涂成红色', 'https://www.maotorino.it/media/opere/Cp03c.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4974', '国立东方艺术博物馆（都灵）', '带有马头形末端的直刀', 'Cb/50.D', 'l. 27，9 cm', '这把刀的形状可以定义为“细高跟鞋”：长而窄的刀片位于手柄的延长线上，笔直且不厚。它必须覆盖皮革补丁，使手柄更舒适。末端饰有圆形马头，颈部比手柄厚。自然和简单是头部造型的特点，它具有正确的比例，并用逼真的细节装饰：浮雕鬃毛，舌头在张开的口腔内呈层状结构。草原动物艺术的特点，眼睛由一个圆形的洞组成，洞的边缘是一个圆圈，其中可能嵌有一块石头。这是一个形状罕见的标本，可能来自北京北部/东北部的辽宁或内蒙古。', '西周，公元前9至8世纪。', '', '深绿色铜绿', 'https://www.maotorino.it/media/opere/Cb50 ridotta ritaglio.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4975', '国立东方艺术博物馆（都灵）', '高脚楼上的文职人员', 'Cc/2b', '70，9 cm x 17，8 cm', '他站在高高的基座上。这位政要穿着一件长到脚的束腰外衣，它的尖端刚刚露出来，在一件较短的夹克下，左襟翼在右侧交叉。它有巨大的翻领袖子，从夹克本身的下摆向下延伸。面部具有东方特征，以文官佩戴的“梁冠”为特征。头部和颈部被冷漆：黑色颜料的痕迹，红色和橙色可以在略带粉红色且不稳定的驼峰上找到。身体的其余部分是赭色、绿色和奶油色的釉面。这座雕像应该是8世纪初在陕西或更有可能在河南制作的，其中三色上光技术（三彩）它被微调了。与CC/2a一起，他是经常出现在同一葬礼背景下的两名官员之一。', '唐朝，公元7世纪末至8世纪上半叶。', '', '陶土、粉白釉、三彩釉料和颜料痕迹。', 'https://www.maotorino.it/media/opere/Cc02bRIDOTTA_0.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4976', '国立东方艺术博物馆（都灵）', '德斯特里埃', 'Cc/34', '51，9 cm x 52，2 cm', '这匹马以一种相当逼真的方式塑造，以动态的姿势躺在一个薄的底座上，右前腿优雅地抬起。头部略微向左转，从而突出了整体的运动印象。动物的身体和四肢强壮，胸部有力，枪口的细节以精细有效的方式呈现，特别注意皮下肌肉。鬃毛在直耳前分成两簇，它在颈部左侧呈平行且整齐的长卷发排列。这匹马几乎全身都覆盖着白色的笼头，没有修饰。', '唐朝，公元7世纪下半叶。', '', '带有白色粉红色面团的陶土，带有白色涂层和颜料', 'https://www.maotorino.it/media/opere/Cc34 sx ridotta.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4977', '国立东方艺术博物馆（都灵）', '拟人颈罐', 'Cn/18', 'h 39，8 cm， d. 25，4 cm', 'Orcio有梨形的身体，有一个凸起的高脖子。涂成黑色的装饰，在非常光滑且相当均匀的赭红色背景上，只影响海湾上方的花瓶顶部。奇异的颈部，以截锥形为基础，在上部膨胀形成一种鳞茎，然后用窄而直的边缘和薄壁重新闭合。在凸起的边缘和顶部之间塑造了一个拟人化的头部。两个穿孔的鳍，就在边缘下方，代表耳朵。所描绘人物的狭缝眼睛略有浮雕；长而弯曲的鼻子标志着花瓶脖子上隆起的通道；嘴只是用粘土糊做成的。再往下走，几乎在球体的中心，两个小的侧面突起似乎在复制乳房。', '公元前2300-1950年', '', '红色赭色背景上带有黑色装饰的米色兵马俑', 'https://www.maotorino.it/media/opere/Cn18 ridotta.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4978', '国立东方艺术博物馆（都灵）', '灵魂之瓮（“HUNPING”）', 'Cv/13.D', 'h. 43，6 cm， d. 25，5 cm', '花瓶是在车床上加工的，然后用应用的元素和单独建模的圆形小雕像进行装饰。最后，所有东西都被一个相当均匀的陈列柜覆盖，该陈列柜在底部上方几厘米处停止。在肩膀上方，花瓶被一个反向项圈包围，该项圈支撑着一大群人物，包括四个佛陀和动物形象。第二层的全圆形装饰，在假盖的底部，它的特点是四个罐子，周围环绕着许多飞舞的小鸟。顶部的特点是建筑群的模型。这些象征性容器的复杂图像揭示了似乎渗透到巴斯地区葬礼仪式中的融合。我知道三世纪的扬子。佛教图像与传统的吉祥图像相关联，旨在保护死者的灵魂。', '西晋，公元3世纪末。', '', '带灰绿色毯子的瓷器粗陶器', 'https://www.maotorino.it/media/opere/Cv13 ridotta.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4979', '国立东方艺术博物馆（都灵）', '带有装饰带的谷物酒“Jue”花瓶', 'Cb/2.D', '15，7 cm x 13，2 cm', '青铜礼器（爵），用于盛装和饮用，甚至加热谷物酒。它有一个圆形底部的圆柱体，嫁接在三个向下发散的尖脚上。下摆向上明显变宽，一侧以长尖唇结束，另一侧以长而宽的倒角结束。上面的柱子有一个三角形的部分，而旋钮有一个漩涡装饰。装饰，相当粗糙的浮雕，由一个单一的中间带组成，其连续性仅被手柄的附件打破。在环的另一侧，装饰是由一个独特的陶铁面具变体组成的，其特征是两只眼睛位于其中轴的两侧，在其上方有两个扁平的杏仁形（另外两只眼睛？', '后商，公元前13-12世纪。', '', '三模铸造青铜', 'https://www.maotorino.it/media/opere/Cb02 ritaglio ridotto.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4980', '国立东方艺术博物馆（都灵）', '带有彩绘装饰的“胡”花瓶', 'Cv/62.D', 'h 41，2 cm， d. 31，2 cm', '花瓶的腹部是圆形的，扁平的，脚是中空的，呈喇叭形。高领逐渐向宽的外缘延伸。墙壁用白色、红色、黑色、淡绿色和“汉紫”（介于灰蓝色和紫色之间）颜料水平条装饰。在面团的深灰色底部。主装饰带的顶部由两个红色带之间的白色条纹界定，底部由两个红色带之间的备用条纹界定。红色乐队。彩绘装饰描绘了一幅狩猎的场景，一名骑士在疾驰中射出一支箭，一只小鹿转向他的追逐者。马和骑士在深色背景上以白色突出，而白色轮廓则画出了小鹿的黑色肿块。', '西汉，公元前二至一世纪。', '', '烧成后彩绘的灰色陶土', 'https://www.maotorino.it/media/opere/Cv62-1 RIDOTTA.jpg', '', '', '');
INSERT INTO museum (id, museum, name, inventory, size, details, era, technique, category, image_url, author, number_of_volume, engraved_or_handcopied) VALUES ('4981', '国立东方艺术博物馆（都灵）', '神话树', 'Cc/44.D', '68，3 cm x 15 cm', '该对象是放置在三脚架空心底座上的树干的程式化表示。九个可移动的短枝，水平插入树干上半部三层的孔中，每层三个分支。树干在末端部分变窄：在一个圆形的蹄子上方，描绘了一条下巴向上张开的龙的头部。树顶上站着一只凤头鸟，它的尾巴从龙的嘴里伸出来，爪子紧贴着它的鼻子。浮雕印刷的装饰元素集中在物体的底座和叶子上。在第一个中，我们在带有植物图案的风格化山区背景上发现了一只蝉和四个裸体男人。在两个较高的树枝上，到处都是一只鸟和一只蹲在叶子上的小猴子。', '西汉，公元前1世纪。', '', '红色兵马俑，带有白色凹槽和两个铅制陈列柜，模具装饰', 'https://www.maotorino.it/media/opere/Cc44RIDOTTA.jpg', '', '', '');
