DROP TABLE IF EXISTS `museum`;

CREATE TABLE museum 
(
    id	INT,
    museum	VARCHAR(512),
    name	VARCHAR(512),
    inventory	VARCHAR(512),
    size	VARCHAR(512),
    details	VARCHAR(1024),
    era	VARCHAR(512),
    technique	VARCHAR(512),
    category	VARCHAR(512),
    image_url	VARCHAR(512),
    author	VARCHAR(512),
    number_of_volume	VARCHAR(512),
    engraved_or_handcopied	VARCHAR(512)
);

